  `CREATE_TIME` varchar(100) DEFAULT NULL COMMENT '创建时间',
  `UPDATE_TIME` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '修改时间',
  `DRAFT_CONTENT` text COMMENT '帖子内容',
  `DRAFT_TITLE` varchar(200) DEFAULT '' COMMENT '帖子标题',
  `DRAFT_LOCATION` varchar(255) DEFAULT '' COMMENT '发帖地点',
  `OPEN_ID` varchar(255) NOT NULL DEFAULT '' COMMENT '创建人OpenId',
  PRIMARY KEY (`DRAFT_ID`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

