  `UPDATE_TIME` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '修改时间',
  `P_MASTERID` varchar(36) NOT NULL DEFAULT '' COMMENT '贴主OpenId,外键',
  `P_IFGOOD` tinyint(1) DEFAULT '0' COMMENT '是否为精华帖',
  `P_IFTOP` tinyint(1) DEFAULT '0' COMMENT '是否置顶',
  `P_READ_NUM` int(11) DEFAULT '0' COMMENT '浏览量',
  `P_CONTENT` text COMMENT '帖子内容',
  `P_LOCATION` varchar(255) DEFAULT NULL COMMENT '发帖地点',
  `P_IMAGE` varchar(500) DEFAULT NULL COMMENT '帖子配图',
  PRIMARY KEY (`P_ID`),
  KEY `P_MASTERID` (`P_MASTERID`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

