#
# Structure for table "kno_category"
#

DROP TABLE IF EXISTS `kno_category`;
CREATE TABLE `kno_category` (
  `CATEGORY_ID` int(50) NOT NULL AUTO_INCREMENT,
  `CATEGORY_NAME` varchar(64) NOT NULL COMMENT '类目名称',
  `CATEGORY_TYPE` int(11) NOT NULL COMMENT '类目编号',
  `CREATE_TIME` datetime NOT NULL COMMENT '创建时间',
  `UPDATE_TIME` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '修改时间',
  PRIMARY KEY (`CATEGORY_ID`)
) ENGINE=InnoDB AUTO_INCREMENT=7 DEFAULT CHARSET=utf8mb4 COMMENT='类目表';

#
# Data for table "kno_category"
#

INSERT INTO `kno_category` VALUES (1,'生活',1,'2021-11-20 10:39:43','2021-11-20 12:35:54'),(2,'心理',2,'2021-11-20 10:39:43','2021-12-02 17:09:12'),(3,'健康',3,'2021-11-20 10:39:43','2021-12-02 17:09:20'),(4,'科学',4,'2021-11-20 10:39:43','2021-12-06 01:48:13'),(5,'饮食',5,'2021-11-20 10:39:43','2021-12-06 01:46:58'),(6,'知识',6,'2021-11-20 10:39:43','2021-12-02 17:11:09');

#
# Structure for table "kno_info"
#

DROP TABLE IF EXISTS `kno_info`;
CREATE TABLE `kno_info` (
  `KNO_CARD_ID` int(50) NOT NULL AUTO_INCREMENT,
  `KNO_CARD_TITLE` varchar(200) CHARACTER SET utf8 NOT NULL DEFAULT '' COMMENT '知识标题',
  `CATEGORY_TYPE` int(11) NOT NULL COMMENT '类目编号',
  `KNO_CARD_ICON` varchar(512) CHARACTER SET utf8 DEFAULT NULL COMMENT '小图',
  `KNO_CARD_SUMMARY` text CHARACTER SET utf8 COMMENT '文章摘要',
  `KNO_CARD_CONTENT` longtext CHARACTER SET utf8 COMMENT '文章内容',
  `KNO_CARD_AUTHOR` varchar(50) CHARACTER SET utf8 DEFAULT NULL COMMENT '文章作者',
  `KNO_CARD_READ` int(50) NOT NULL COMMENT '阅读量',
  `KNO_CARD_LIKES` int(50) NOT NULL COMMENT '点赞数',
  `KNO_ISHOT` int(6) NOT NULL DEFAULT '0' COMMENT '1--热门，0--非热门',
  `KNO_CARD_RELEASE_DATE` date DEFAULT NULL COMMENT '发布日期',
  `CREATE_TIME` datetime NOT NULL COMMENT '创建时间',
  `UPDATE_TIME` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '修改时间',
  PRIMARY KEY (`KNO_CARD_ID`)
) ENGINE=InnoDB AUTO_INCREMENT=23 DEFAULT CHARSET=utf8mb4 COMMENT='知识卡详情表';

#
# Data for table "kno_info"
#

INSERT INTO `kno_info` VALUES (1,'如何从分手后的哀伤中走出来 | 精选问答',2,'https://jdxl-img.jdxlt.com/post/39530b0ff15f414ca5e5aeb0194ccc07.png','摘要','<p align=\"left\" line=\"yP87\" linespacing=\"115\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n曾经有人问：失恋后最难熬的是什么时候？<br>&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n最让我有共鸣的回答是：<span style=\"font-weight: bolder;\">\n晚上睡觉时和早晨起床时的那一瞬间。</span><br>&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n最难熬的可能并不是你和朋友一起喝酒，边哭边把自己灌醉，或是很长一段时间的情绪低落和不可抑制的想念。<br>&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n而恰恰是那一个个瞬间最难熬：<br>&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n早上习惯性拿起手机却发现没有早安信息的那一刻；<br>看到好玩的东西想发给对方，却想到我们再也不是那么亲密的时候；\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n喝醉是很难受，吐也很难受，最难受的却是吐到清醒、清醒的知道自己已经失去对方的那一瞬间。<br>&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n我们常常收到大家的消息，最常被问的就是：<span style=\"font-weight: bolder;\">\n我失恋了，不知道该怎样面对，我该怎么办……</span>\n</p>\n<p align=\"left\" line=\"yP87\" linespacing=\"115\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<p align=\"left\" line=\"6PWc\" linespacing=\"115\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n我们从【心理问答区】挑选了两个真实用户关于分手的提问，如果你现在正在经历一段感情的结束，想走出来却身陷在泥潭，来看看学员咨询师给出的建议吧~<br>&nbsp;\n</p>\n<p line=\"P1Zo\" linespacing=\"115\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; text-align: center;\">\n<img alt=\"\" src=\"https://jdxl-img.jdxlt.com/images/a70a11403d2144efa153df34fcdafc0a.png\" style=\"border-style: none; vertical-align: middle; max-width: 100%;\">\n</p>\n<p align=\"left\" line=\"deVR\" linespacing=\"115\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<br><br><span style=\"color: rgb(255, 255, 255);\">\n<span style=\"font-weight: bolder;\">\n<span style=\"background-color: rgb(26, 188, 156);\">\n@咨询师 -&nbsp;范晓丹：「放空自己，照顾好自己。」</span></span></span><br>&nbsp;\n</p>\n<p align=\"left\" line=\"PsNd\" linespacing=\"115\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n看到你的问题，我脑海中对你的第一印象是：这是个多么勇敢和有生命力的姑娘！因为你面对伤痛敢于坚持求助而不是放弃。<br>&nbsp;\n</p>\n<p align=\"left\" line=\"HSDZ\" linespacing=\"115\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n其实放弃是一件很简单的事：如果我学骑自行车总摔倒，那就不学了；如果我觉得考研好痛苦，那就去找工作好了……并不是说不会骑自行车和找工作不好，如果内心能自洽，那也极好。而我们不愿意轻易放弃一段刻骨铭心的感情，也是非常正常的反应。我能理解你的初衷，同时也感受到你的痛苦。<br>&nbsp;\n</p>\n<p align=\"left\" line=\"F6nd\" linespacing=\"115\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n那接下来就是如何做，你可能想继续忍受痛苦不放弃。对此我有两个建议：<br><br><br><span style=\"font-weight: bolder;\">\n第一个是这么做的前提是要给自己一段时间放空，先不去联系对方</span><br><br>在只有自己的空间和时间里，去想清楚在此之前你们的关系是一段好的健康的关系吗？如果你认为在这段关系是平等的、有边界的、互相来电且双方都有成长的，那接下来我们就可以跟对方有一两次正式的沟通，去明白问题出在哪里，能否继续。不能的话也可以做一个很好的道别，任何关系的道别都是很重要的。而如果你在这段时间猛然发觉以前的关系并不平等健康，那你的痛苦就是有价值的，它让你深刻地记住并可能在以后更容易识别避免不良关系。<br><br>&nbsp;\n</p>\n<p align=\"left\" line=\"zP7o\" linespacing=\"115\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<span style=\"font-weight: bolder;\">\n第二个建议是不管你是否放弃，一定要先照顾好自己</span><br><br>痛苦的强烈程度不是衡量爱情是否浓烈的标准，过度痛苦和沉浸其中反而可能加速关系的破裂。<span style=\"font-weight: bolder;\">\n在爱别人之前，你要先学会爱自己</span>。可以在控制不住情绪时记录自己的情绪日记，试着找去是否因为一些不合理信念所导致，练习换个角度想让自己能平和些。还有很多很实用的小方法，比如角色扮演一个理想中能照顾自己的人之类的。<br>&nbsp;\n</p>\n<p align=\"left\" line=\"beU9\" linespacing=\"115\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n希望这些能对你有帮助，如果你还是觉得一个人度过这个时期很艰难，也可以寻求咨询师的陪伴，更深入了解自己的情感模式和需求。<br>&nbsp;\n</p>\n<p align=\"left\" line=\"KpUS\" linespacing=\"115\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n最后，感谢你的勇敢来信。<br>&nbsp;\n</p>\n<p align=\"left\" line=\"bgS6\" linespacing=\"115\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<span style=\"font-weight: bolder;\">\n&nbsp;</span>\n</p>\n<p line=\"bgS6\" linespacing=\"115\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; text-align: center;\">\n<a href=\"https://www.jiandanxinli.com/experts/341113?utm_source=position&amp;utm_medium=app_questions&amp;utm_term=20200529\" style=\"text-decoration-line: none; color: rgb(57, 191, 183); -webkit-tap-highlight-color: transparent; background-color: transparent;\">\n<img alt=\"\" src=\"https://jdxl-img.jdxlt.com/images/0a70e5ad2e35430aadf17cd4e41d1a38.png\" style=\"border-style: none; vertical-align: middle; max-width: 100%;\">\n</a>\n</p>\n<p align=\"left\" line=\"bgS6\" linespacing=\"115\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<br><br><span style=\"font-weight: bolder;\">\n<span style=\"color: rgb(255, 255, 255);\">\n<span style=\"background-color: rgb(26, 188, 156);\">\n@咨询师 - 方琳：「理解自己，理解关系」</span></span></span><br>&nbsp;\n</p>\n<p align=\"left\" line=\"S6It\" linespacing=\"115\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n看到你的文字能感觉到一种情绪上的摆动和拉扯，对于他人的这种不一致的表现似乎也让你产生了很多的疑惑，疑惑对方的行为，疑惑自己的感受，也影响到了你日常的情绪状态和生活作息。我想你目前可能承受着非常多的情绪冲突，同时你目前正处在毕业的阶段，分手这个事件无疑叠加了你内心的压力。<br>&nbsp;\n</p>\n<p align=\"left\" line=\"Gk9b\" linespacing=\"115\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<span style=\"font-weight: bolder;\">\n分手也代表着一个关系的结束</span>，怎样面对分手后的关系也意味着我们是怎样去体验和他人的关系，以及我们是如何的面对我们生活中存在或者失去的关系。在生命的早期我们与主要养育者的互动，形成我们对于自我的觉知和对于关系的觉知。通过一段安全的满足的母婴依恋关系，我们可以以更积极的方式去应对这个世界的变化。<br>&nbsp;\n</p>\n<p align=\"left\" line=\"LuZr\" linespacing=\"115\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n研究表明，<span style=\"font-weight: bolder;\">\n儿时形成的依恋关系会伴随终身</span>，对于我们的行为处事、人际交往以及亲密关系产生至关重要的影响。成人的依恋模式有安全型，焦虑型，回避型，而不同的依恋模式在遇到同样的情境下会出现不同的反应。焦虑型和回避型的依恋模式都显示着内心的不安全感。\n</p>\n<p align=\"left\" line=\"pQCK\" linespacing=\"115\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<p align=\"left\" line=\"o6nb\" linespacing=\"115\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n我想，如何处理分手后的这段关系，我想可以试着从几个方面去尝试：<br>&nbsp;\n</p>\n<p align=\"left\" line=\"BZDw\" linespacing=\"115\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<span style=\"font-weight: bolder;\">\n1.了解自己的依恋关系类型</span>\n</p>\n<p align=\"left\" line=\"WoYW\" linespacing=\"115\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n我们只有先了解自己，才能更好的理解关系。当我们去尝试理解自己的成长经历和过程，理解了是什么影响了我们对他人的看法和体验，更好的理解关系中被唤起的情感体验，才能更多的理解关系中的相遇或是结束。<br>&nbsp;\n</p>\n<p align=\"left\" line=\"3KrT\" linespacing=\"115\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<span style=\"font-weight: bolder;\">\n2.了解他人的依恋关系类型</span>\n</p>\n<p align=\"left\" line=\"pHBq\" linespacing=\"115\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n我想只是理解关系中的一方也许是有些不全面的，试着站在他人的角度，以自己对对方的认识来理解TA背后的一些想法或是行为，也有助于我们看到他人的需求，知道有时候TA的行为也许并不是针对我们本身，而是受TA以往的经验影响。这也许可以一定程度上缓解我们的疑惑。<br>&nbsp;\n</p>\n<p align=\"left\" line=\"Y0Pr\" linespacing=\"115\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<span style=\"font-weight: bolder;\">\n3.尝试在理解之后的沟通</span>\n</p>\n<p align=\"left\" line=\"Wv8F\" linespacing=\"115\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n当我们看到和理解了自己的情绪，我们就能更好的接纳它们，当它们出现的时候，也不会再次增加我们的不安。当我们更坦然的面对自己，内心的力量就会油然而生，我们就可以表达自己，面对对方把自己的想法和感受表达出来，进行双方的沟通。<br>&nbsp;\n</p>\n<p align=\"left\" line=\"Ydbr\" linespacing=\"115\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<span style=\"font-weight: bolder;\">\n4．学习和掌握一些调节情绪的方法</span>\n</p>\n<p align=\"left\" line=\"WQl5\" linespacing=\"115\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n面对分手事件给我们带来各种问题也许需要的一些时间去面对，理解和消化，我们需要给自己一些时间，也需要掌握一些方法，比如在情绪强烈的时候做一些正念的觉察，身体的觉察，做一些深呼吸和肌肉放松的练习。同时我们也可以找到我们信任和安全的人际关系，有可以被倾听和理解的安全体验，可以帮助我们以一个更稳定和持续的的内在感去处理这段分手后的关系。<br>&nbsp;\n</p>\n<p align=\"left\" line=\"P1Zo\" linespacing=\"115\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<p line=\"P1Zo\" linespacing=\"115\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; text-align: center;\">\n<a href=\"https://www.jiandanxinli.com/experts/480554?utm_source=position&amp;utm_medium=app_questions&amp;utm_term=20200529\" style=\"text-decoration-line: none; color: rgb(57, 191, 183); -webkit-tap-highlight-color: transparent; background-color: transparent;\">\n<img alt=\"\" src=\"https://jdxl-img.jdxlt.com/images/5221d35ad57b4e5da7a33a2a80333b1c.png\" style=\"border-style: none; vertical-align: middle; max-width: 100%;\">\n</a>\n</p>\n<p align=\"left\" line=\"P1Zo\" linespacing=\"115\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<br>&nbsp;\n</p>\n<p align=\"left\" line=\"xA5Q\" linespacing=\"115\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<span style=\"color: rgb(255, 255, 255);\">\n<span style=\"font-weight: bolder;\">\n<span style=\"background-color: rgb(26, 188, 156);\">\n@咨询师 - 孙颖：「了解自己需要什么样的关系」</span></span></span><br>&nbsp;\n</p>\n<p align=\"left\" line=\"JfDd\" linespacing=\"115\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n首先关系对于我们都非常重要，恋人关系更是属于亲密关系中的一种了，如果我们面对的是关系的问题，可以从两个角度来思考和改善。\n</p>\n<p align=\"left\" line=\"mh8H\" linespacing=\"115\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<p align=\"left\" line=\"9yGh\" linespacing=\"115\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<span style=\"font-weight: bolder;\">\n一是关系带给我们的意义</span>\n</p>\n<p align=\"left\" line=\"QBil\" linespacing=\"115\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<span style=\"font-weight: bolder;\">\n二是关系中的界限</span>\n</p>\n<p align=\"left\" line=\"mIZa\" linespacing=\"115\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<p align=\"left\" line=\"eiUg\" linespacing=\"115\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n关系带给我们的意义究竟是什么？<span style=\"font-weight: bolder;\">\n你需要怎样的关系？</span>滋养的关系？独立但互相支持的关系？还是共生的关系？这种意义对我们每个人都不尽相同，应该会是一个光谱的形式，在这个光谱中，我们可以随着自己趋向于独立，关系的质量也会越来越高，远离有毒和共生的关系。<span style=\"font-weight: bolder;\">\n获得关系中的滋养。</span>\n</p>\n<p align=\"left\" line=\"zQww\" linespacing=\"115\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<p align=\"left\" line=\"iqem\" linespacing=\"115\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n在关系中我们彼此的界限是怎样的，通过你的描述，在关系结束之后，你们似乎还在用一种彼此都不确定的方式在继续联络，<span style=\"font-weight: bolder;\">\n可以感觉到在关系结束之前，在彼此之间，你需要什么，对方需要什么，你能提供什么，对方可以提供什么</span>，这样日常的相处中，也会经常在模糊地带拉扯。\n</p>\n<p align=\"left\" line=\"Zshj\" linespacing=\"115\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<p align=\"left\" line=\"F7we\" linespacing=\"115\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n在想清楚这两个方面之后，你可能慢慢获得了两个清晰：第一个是明白自己需要什么，第二个是明白自己需要怎么样的关系。<br><br><a href=\"https://www.jiandanxinli.com/experts/312678?utm_source=position&amp;utm_medium=app_questions&amp;utm_term=20200529\" style=\"text-decoration-line: none; color: rgb(57, 191, 183); -webkit-tap-highlight-color: transparent; background-color: transparent;\">\n<img alt=\"\" src=\"https://jdxl-img.jdxlt.com/images/54ed0eeb7dfd4fa9955c62148dfe720e.png\" style=\"border-style: none; vertical-align: middle; max-width: 100%;\">\n</a><br><br><br><img alt=\"\" src=\"https://jdxl-img.jdxlt.com/images/686060832a584b198cc2a177cca914b8.png\" style=\"border-style: none; vertical-align: middle; max-width: 100%;\">\n</p>\n<p align=\"left\" line=\"vp41\" linespacing=\"115\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<br><br><span style=\"color: rgb(255, 255, 255);\">\n<span style=\"font-weight: bolder;\">\n<span style=\"background-color: rgb(26, 188, 156);\">\n@咨询师 -&nbsp;韩艳：「接纳自己现在是这样一个“走不出来”的状态。」</span></span></span><br>&nbsp;\n</p>\n<p align=\"left\" line=\"EKan\" linespacing=\"115\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n看到你说“虽然知道我们不可能了，但我走不出来”，我在心里深深地对你点了点头，是啊，对于“疯狂地付出了所有，毫无保留的爱他”的你来说，真的是没办法轻易走出来的。或者反过来说，我们拼命的爱着一个人，然后对方一说分手我们就能欣然接受，这反而会让人觉得有点奇怪了。所以我们要为自己做的第一件事情，就是<span style=\"font-weight: bolder;\">\n接纳我们自己现在就是在这样一个“走不出来”的状态。</span><br>&nbsp;\n</p>\n<p align=\"left\" line=\"pC5m\" linespacing=\"115\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n然后，接下来，<span style=\"font-weight: bolder;\">\n我们完全可以允许自己呆在这样的状态里</span>，我们想哭就关起门来痛痛快快的大哭一场！想喊就找个没人的地方大声地喊出来！想打架就找个沙袋拼了命的盘它！想说就找个好闺蜜痛痛快快的说个够！<br>&nbsp;\n</p>\n<p align=\"left\" line=\"1pCb\" linespacing=\"115\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n可千万不要小瞧了这些操作哦，咱们不管是哭也好，喊也罢，其实都是让我们的情绪有个出口，能够流动起来，避免把自己憋出内伤。<br>&nbsp;\n</p>\n<p align=\"left\" line=\"vMx0\" linespacing=\"115\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n当然，<span style=\"font-weight: bolder;\">\n以上都只是过程不是目的，我们的目的是要从这段关系和这次经历中有所收获</span><span style=\"font-weight: bolder;\">\n。</span><br><br>在发泄过后，冷静下来，我们可以尝试着去思考一下：\n</p>\n<blockquote style=\"color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<ul><li align=\"left\" line=\"vMx0\" linespacing=\"115\">\n这段关系让自己放不下的究竟是什么？</li><li align=\"left\" line=\"vMx0\" linespacing=\"115\">\n是什么样的动力推动着我疯狂地付出所有、毫无保留的爱他？</li><li align=\"left\" line=\"vMx0\" linespacing=\"115\">\n在我们的关系和互动中，我自己有什么样的期待和需求得到了满足或者没有得到满足？</li><li align=\"left\" line=\"vMx0\" linespacing=\"115\">\n由此我产生了什么样的情绪和感受？</li><li align=\"left\" line=\"vMx0\" linespacing=\"115\">\n我的这些情绪和感受能够被对方理解和接纳吗？</li><li align=\"left\" line=\"vMx0\" linespacing=\"115\">\n我自己能够理解和接纳自己的这些情绪和感受吗？</li></ul></blockquote><p align=\"left\" line=\"vMx0\" linespacing=\"115\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<br>当我们能够幸运的透过痛苦的迷雾渐渐看清楚自己，当我们终于搞明白让我们痛苦的究竟是什么，我们也许会充满感激地发现，这段痛苦的关系其实是为了帮助我们成长，成为更好的自己。<br>&nbsp;\n</p>\n<p line=\"vMx0\" linespacing=\"115\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; text-align: center;\">\n<a href=\"https://www.jiandanxinli.com/experts/838031?utm_source=position&amp;utm_medium=app_questions&amp;utm_term=20200529\" style=\"text-decoration-line: none; color: rgb(57, 191, 183); -webkit-tap-highlight-color: transparent; background-color: transparent;\">\n<img alt=\"\" src=\"https://jdxl-img.jdxlt.com/images/f7c085bbfd934701aa4f5b793fdd3c1a.png\" style=\"border-style: none; vertical-align: middle; max-width: 100%;\">\n</a>\n</p>\n<p align=\"left\" line=\"vMx0\" linespacing=\"115\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<br>&nbsp;\n</p>\n<p align=\"left\" line=\"70m8\" linespacing=\"115\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<span style=\"color: rgb(255, 255, 255);\">\n<span style=\"font-weight: bolder;\">\n<span style=\"background-color: rgb(26, 188, 156);\">\n@咨询师 - 冷晓：「找到一个安全的环境，让自己去度过这段“丧失”的时间」</span></span></span><br>&nbsp;\n</p>\n<p align=\"left\" line=\"iL5z\" linespacing=\"115\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n5年的感情走到分手时，旁人如我也不由为之叹息，更何况在感情之中的你。我相信，在这段情感中的起转承合中，一定给到你们彼此许多美好与收获。所以，此刻“分手”的事实好像在意识层面上是接受的，“知道我们不可能了”，但内心的感受上，“我觉得他心里还有我”，“我走不出来”。<br>&nbsp;\n</p>\n<p align=\"left\" line=\"VOWv\" linespacing=\"115\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n虽然今天谈的主题是分手，或者说，“丧失”，但我不由得记起多年前电视剧中的一句台词，“山无棱，天地合，才敢与君绝”。“我想永远和你在一起”这几乎是每个走入亲密关系的人的想法。确实不如意的分开也会发生。这种“丧失”意味着什么呢？\n</p>\n<p align=\"left\" line=\"44Yp\" linespacing=\"115\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<p align=\"left\" line=\"cXCW\" linespacing=\"115\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<span style=\"font-weight: bolder;\">\n“丧失”会经历怎样的过程呢？</span><br>&nbsp;\n</p>\n<p align=\"left\" line=\"hnwx\" linespacing=\"115\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<span style=\"font-weight: bolder;\">\n1、震惊与否认</span>。“他很决绝”，“我觉得他心里还有我。”\n</p>\n<p align=\"left\" line=\"9BOD\" linespacing=\"115\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<span style=\"font-weight: bolder;\">\n2、愤怒</span>。“我疯狂地付出了我所有，毫无保留的爱他。”背后是不是在说，“你怎么可以这样对我？”当然这只是个假设。通常，我们愤怒的背后是强烈的挫折感，指向的对象是他人、社会或者是自己。<br>&nbsp;\n</p>\n<p align=\"left\" line=\"5pPS\" linespacing=\"115\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<span style=\"font-weight: bolder;\">\n3、讨价还价</span>。比如，“如果我当时怎么做，可能我们就不会分手”\n</p>\n<p align=\"left\" line=\"nJ0J\" linespacing=\"115\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<span style=\"font-weight: bolder;\">\n4、沮丧</span>。在确认无法改变之后的无助、无奈的状态。<br>&nbsp;\n</p>\n<p align=\"left\" line=\"nr21\" linespacing=\"115\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<span style=\"font-weight: bolder;\">\n5、接受</span>。从丧失中学习中，找到正面意义。<br>&nbsp;\n</p>\n<p align=\"left\" line=\"uHlW\" linespacing=\"115\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n这个过程顺序不是必然如上面所列，也未必是一次性的循环，有可能会在某些环节摇摆。知道这个过程的意义在于，眼下的痛苦是每个分手的人都会经历的，也是会过去的。\n</p>\n<p align=\"left\" line=\"vPn2\" linespacing=\"115\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<p align=\"left\" line=\"MBxZ\" linespacing=\"115\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<span style=\"font-weight: bolder;\">\n那么，我们可以为自己做些什么呢？</span><br>&nbsp;\n</p>\n<p align=\"left\" line=\"1Ufg\" linespacing=\"115\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<span style=\"font-weight: bolder;\">\n第一，给自己接受事实的时间</span>。在这个情形下愤怒、悲伤、无力都自然的状态，允许它们发生。<br>&nbsp;\n</p>\n<p align=\"left\" line=\"gQxY\" linespacing=\"115\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<span style=\"font-weight: bolder;\">\n第二，经验悲伤的痛苦</span>。这很重要，虽然悲伤很想让人跑回到前恋人的怀抱，或者，想直接关掉体验的阀门。悲伤的时刻，也是治愈时刻，可以一个人安静地度过，可以请你信赖的朋友陪你一下，或者请专业人士，如心理咨询师，陪伴你。<br>&nbsp;\n</p>\n<p align=\"left\" line=\"vjnk\" linespacing=\"115\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n为什么我们无法直接通过意识层面上接受跳跃过去呢？我们的大脑既有现代的部分（如，思维的前额叶）也有古老的部分（如，易恐惧的杏仁核），丧失所激发的分离体验和被抛弃的恐惧是极易触发我们的杏仁核，道理是前额叶懂的，而悲伤的情绪是前额叶深知不可能在一起的现实后又允许那种得不到的难过的情绪流淌，这样两个部分可以一并协助你走过这个过程。<br>&nbsp;\n</p>\n<p align=\"left\" line=\"zptx\" linespacing=\"115\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n一个足够安全的环境，会让二者一起协助你走过这个阶段。什么是足够安全的环境呢？我记得有个故事讲的是，一个小男孩看到一位老人在哭，他过去，坐下来，静静地陪在那里。回到现实，可能是你的家人、朋友、治疗师、一首歌、一部电影。<br>&nbsp;\n</p>\n<p align=\"left\" line=\"rFJn\" linespacing=\"115\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<span style=\"font-weight: bolder;\">\n第三，当上述哀悼充分发生了之后</span>，你会发现可以重新发现世界其他的美好，那时请恭喜你自己做到了！<br>&nbsp;\n</p>\n<p align=\"left\" line=\"tSDv\" linespacing=\"115\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n关于“分手”的讨论可以在此告一段落，但在伙伴的提问中，有个字眼让我一直在思考——“爱”。“爱”是动词还是名词呢？是静态的还是动态的呢？是付出还是获得呢？是爱TA如TA所是还是爱TA如我所愿呢？我也没有找到答案，愿一同在生活继续对这些问题探索与体会。<br>&nbsp;\n</p>\n<p line=\"tSDv\" linespacing=\"115\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; text-align: center;\">\n<a href=\"https://www.jiandanxinli.com/experts/211182?utm_source=position&amp;utm_medium=app_questions&amp;utm_term=20200529\" style=\"text-decoration-line: none; color: rgb(57, 191, 183); -webkit-tap-highlight-color: transparent; background-color: transparent; outline-width: 0px;\">\n<img alt=\"\" src=\"https://jdxl-img.jdxlt.com/images/eb8a1028733c4beda9b87237cb68e967.png\" style=\"border-style: none; vertical-align: middle; max-width: 100%;\">\n</a>\n</p>\n<div><br></div>','简单心理',87,28,1,'2021-01-04','2021-11-20 10:39:43','2021-12-08 23:41:14'),(2,'睡眠这些事，这篇文章全说透了\r\n抑郁症到底是什么病？帮你全面科学了解抑郁症！',4,'https://pic2.zhimg.com/v2-3b4c63de923961c8f4249fe581a7f3c4_1440w.jpg?source=172ae18b','摘要','<header class=\"Post-Header\" style=\"margin: 0px auto; width: 690px; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: medium;\">\n<h1 class=\"Post-Title\" style=\"margin: 24px 0px; font-style: inherit; font-variant: inherit; font-stretch: inherit; font-size: 24px; line-height: 1.22; font-family: inherit; word-wrap: break-word;\">\n抑郁症到底是什么病？帮你全面科学了解抑郁症！</h1><div class=\"Post-Author\" style=\"display: flex; -webkit-box-align: center; align-items: center;\">\n<div class=\"AuthorInfo\" itemprop=\"author\" itemscope=\"\" itemtype=\"http://schema.org/Person\" style=\"display: flex; -webkit-box-align: center; align-items: center; -webkit-box-pack: justify; justify-content: space-between; max-width: 654px; -webkit-box-flex: 1; flex: 1 1 0%; white-space: nowrap; overflow: hidden;\">\n<div class=\"AuthorInfo\" style=\"display: flex; -webkit-box-align: center; align-items: center; -webkit-box-pack: justify; justify-content: space-between; max-width: 654px; -webkit-box-flex: 1; flex: 1 1 0%; overflow: hidden;\">\n<span class=\"UserLink AuthorInfo-avatarWrapper\">\n<div class=\"Popover\" style=\"position: relative; display: inline-block;\">\n<div id=\"Popover5-toggle\" aria-haspopup=\"true\" aria-expanded=\"false\" aria-owns=\"Popover5-content\">\n<a target=\"_blank\" class=\"UserLink-link\" data-za-detail-view-element_name=\"User\" href=\"https://www.zhihu.com/people/en-en-yi-sheng\" style=\"text-decoration-line: none;\">\n<img class=\"Avatar Avatar--round AuthorInfo-avatar\" width=\"38\" height=\"38\" src=\"https://pic2.zhimg.com/v2-ce6049409a1227f76293a8a8798c1d12_xs.jpg?source=172ae18b\" srcset=\"https://pic2.zhimg.com/v2-ce6049409a1227f76293a8a8798c1d12_l.jpg?source=172ae18b 2x\" alt=\"恩恩心理医生\" style=\"background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; border-radius: 50%; vertical-align: top;\">\n</a></div></div></span><div class=\"AuthorInfo-content\" style=\"-webkit-box-flex: 1; flex: 1 1 0%; margin-left: 14px; overflow: hidden;\">\n<div class=\"AuthorInfo-head\" style=\"display: flex; -webkit-box-align: center; align-items: center; font-size: 15px; line-height: 1.1; flex-shrink: 0;\">\n<span class=\"UserLink AuthorInfo-name\" style=\"font-weight: 600; color: rgb(68, 68, 68);\">\n<div class=\"Popover\" style=\"position: relative; display: inline-block;\">\n<div id=\"Popover6-toggle\" aria-haspopup=\"true\" aria-expanded=\"false\" aria-owns=\"Popover6-content\">\n<a target=\"_blank\" class=\"UserLink-link\" data-za-detail-view-element_name=\"User\" href=\"https://www.zhihu.com/people/en-en-yi-sheng\" style=\"text-decoration-line: none;\">\n恩恩心理医生</a></div></div></span></div><div class=\"AuthorInfo-detail\" style=\"overflow: hidden; color: rgb(100, 100, 100);\">\n<div class=\"AuthorInfo-badge\" style=\"display: flex; -webkit-box-align: center; align-items: center; margin-top: 2px; font-size: 14px;\">\n<div class=\"ztext AuthorInfo-badgeText\" style=\"word-break: break-word; line-height: 1.6; overflow: hidden; text-overflow: ellipsis;\">\n专业的精神心理健康服务平台</div></div></div></div></div></div></div></header><div class=\"Post-RichTextContainer\" style=\"position: relative; overflow: visible; width: 690px; margin: 0px auto; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: medium;\">\n<div class=\"css-1yuhvjn\" style=\"margin-top: 16px;\">\n<div class=\"RichText ztext Post-RichText css-hnrfcf\" options=\"[object Object]\" style=\"word-break: break-word; line-height: 1.6;\">\n<p data-pid=\"B9LfG6VM\" style=\"margin-top: 0px; margin-bottom: 1.4em;\">\n抑郁症是严重的心理疾病，以持续情绪低落以及认知功能障碍为主要临床特征，包括焦躁情绪、快感消失、睡眠障碍、自我评价过低、负罪感和反复的自杀念头等表现。抑郁症严重困扰患者的生活和工作，给患者家庭及社会带来沉重的负担，据不完全统计，约15%的抑郁症患者死于自杀。那么，抑郁症到底是什么病？能彻底治好吗？下面，恩恩医生就给大家详细介绍一下。\n</p>\n<p data-pid=\"VFDZ3lTk\" style=\"margin-top: 1.4em; margin-bottom: 1.4em;\">\n一、抑郁症的发病原因\n</p>\n<p data-pid=\"s-TRdf9N\" style=\"margin-top: 1.4em; margin-bottom: 1.4em;\">\n抑郁症病因较为复杂，据推断主要与遗传因素、个性因素、躯体因素有关系。\n</p>\n<p data-pid=\"8O0kdiVt\" style=\"margin-top: 1.4em; margin-bottom: 1.4em;\">\nA、遗传因素\n</p>\n<p data-pid=\"7mCLh9E-\" style=\"margin-top: 1.4em; margin-bottom: 1.4em;\">\n据大量临床资料统计，抑郁症的发病与遗传有很大关系。患抑郁症的母亲所生的子女患抑郁症的可能性较大，这是因为，如果父母患有抑郁症，将会直接影响到孩子的情绪和个性发展。其中一级亲属患病的概率要远远的高于其他亲属，因此，抑郁症也有可能会是遗传所致。\n</p>\n<p data-pid=\"Gl-6pQwv\" style=\"margin-top: 1.4em; margin-bottom: 1.4em;\">\nB.人格因素\n</p>\n<p data-pid=\"lq0OwERM\" style=\"margin-top: 1.4em; margin-bottom: 1.4em;\">\n人格因素是诱发抑郁症的主要病因之一。在生活当中，每个人都有自己的个性和处事方式，但是有些人的性格就很容易导致心理疾病的发生。比如，有的人就是生性较为敏感、多疑、自卑、消极悲观、脾气急躁等性格特征，这样的性格特征一旦遇到压力过大或是人际关系处理不好等情况就可能会导致抑郁症的发作。\n</p>\n<p data-pid=\"KIG9UnE-\" style=\"margin-top: 1.4em; margin-bottom: 1.4em;\">\nC心理社会因素\n</p>\n<p data-pid=\"ifbC3yYh\" style=\"margin-top: 1.4em; margin-bottom: 1.4em;\">\n恩恩医生介绍，心理社会因素也是抑郁症发生的重要诱因。比如亲人死亡、家庭破裂、失恋、严重的躯体疾病、学习和工作中的困难和挫折等应激事件容易使人自尊心受到打击而诱发此病症。而且主观判断这些负性社会应激事件的危容程度越大，个体可利用的应对方式越缺乏，则抑郁情绪越严重。\n</p>\n<p data-pid=\"98jo8A_b\" style=\"margin-top: 1.4em; margin-bottom: 1.4em;\">\n二、抑郁症的症状表现\n</p>\n<p data-pid=\"q62shwBN\" style=\"margin-top: 1.4em; margin-bottom: 1.4em;\">\n抑郁症的症状主要有情绪症状、认知症状、动机症状、躯体症状、睡眠障碍等症状表现。\n</p>\n<p data-pid=\"mxtOnneZ\" style=\"margin-top: 1.4em; margin-bottom: 1.4em;\">\nA.情绪症状 主要是持续情绪低落、悲观绝望、对任何事都提不起兴趣，感觉生活没有任何意义。\n</p>\n<p data-pid=\"Ffs31G8w\" style=\"margin-top: 1.4em; margin-bottom: 1.4em;\">\nB.认知症状 主要是自我评价过低，感觉自己没有价值，一无是处，活着就是累赘。而且感觉对不起自己的朋友和家人，常常有强烈的自责感。\n</p>\n<p data-pid=\"TOf5wZay\" style=\"margin-top: 1.4em; margin-bottom: 1.4em;\">\nC.动机症状 主要是对任何事都提不起情趣，言语行动迟钝缓慢，严重时不吃东西、不说话也不动。\n</p>\n<p data-pid=\"r2Q1lCSy\" style=\"margin-top: 1.4em; margin-bottom: 1.4em;\">\nD.躯体症状 躯体症状是抑郁症隐藏很深的症状表现。懒于劳动、不爱工作，随着抑郁症状的发展，一切生物的、心理的快感都遗失殆尽。抑郁症病人的胃口常常不佳，食欲减退、口干、便秘，可伴体重减轻。即使是平时爱吃的人，美酒佳肴也勾不起他的食欲。\n</p>\n<p data-pid=\"8II85xWn\" style=\"margin-top: 1.4em; margin-bottom: 1.4em;\">\nE.睡眠障碍 睡眠不好使抑郁症的表现之一，抑郁症患者常伴有睡眠障碍，情绪低落，这时自杀的风险最大。\n</p>\n<p data-pid=\"I1rr2MZc\" style=\"margin-top: 1.4em; margin-bottom: 1.4em;\">\n三、抑郁症的治疗\n</p>\n<p data-pid=\"PG49c-9C\" style=\"margin-top: 1.4em; margin-bottom: 1.4em;\">\n抑郁症的治疗可根据程度不同，分类治疗，个性治疗。\n</p>\n<p data-pid=\"fA8BK89x\" style=\"margin-top: 1.4em; margin-bottom: 1.4em;\">\nA.轻度抑郁症\n</p>\n<p data-pid=\"PahYIVUt\" style=\"margin-top: 1.4em; margin-bottom: 1.4em;\">\n主要以心理治疗为主，也就是要做好心理疏导，通过专业心理医生倾听、理解、安慰、鼓励等方式，使患者产生安全感，帮助患者扩大生活圈子，避免抑郁程度进一步发展，逐步走出抑郁症困扰。\n</p>\n<p data-pid=\"HkdAZoj5\" style=\"margin-top: 1.4em; margin-bottom: 1.4em;\">\nB.中、重度抑郁症要坚持药物治加心理治疗\n</p>\n<p data-pid=\"JKzys3VX\" style=\"margin-top: 1.4em; margin-bottom: 1.4em;\">\n中、重度抑郁症患者要在医生指导下坚持服用抗抑郁类药品，根据疾病的时间、进展调整药品的剂量，一般情况下，患病的年限越长通常服药时间越久，还有对于一些肝肾功能不好的患者也可以采用中药方法治疗。另外，要积极配合心理医生做好心理疏导，逐步恢复健康的身心。最后，抑郁症也可以采用物理治疗，通过物理治疗方式刺激甲肾上腺素的分泌，增强细胞的活动兴奋性，缓解抑郁情绪。\n</p>\n<p data-pid=\"XjHEJ529\" style=\"margin-top: 1.4em; margin-bottom: 0px;\">\n如何治疗抑郁症？大家可以免费在各大应用市场下载恩恩医生app，平台汇聚了上千位专业心理卫生、精神健康医生，可免费为大家提供一对一心理咨询、疾病治疗等服务，并全程保证患者隐私，你可以不用去医院就能咨询治疗抑郁症，而且全是专业医生！\n</p>\n</div></div></div>','恩恩心理医生',999,28,0,'2017-04-23','2021-11-20 10:39:43','2021-12-09 11:12:31'),(3,'抑郁症带来的5大健康隐患，不可轻视！现在了解不算晚',4,'http://x0.ifengimg.com/res/2019/966A40C7886C156360D32B6DEF13EF08EF3002A8_size232_w537_h430.png','摘要','<div class=\"title\" style=\"text-align: center;\">\n<h2 style=\"margin: 20px 0px 10px; padding: 0px; font-size: 30px; line-height: 36px;\">\n抑郁症患者如何进行自我康复？这四招一定要学会</h2></div><div class=\"artOri\" style=\"padding: 10px 0px; font-size: 12px; color: rgb(153, 153, 153);\">\n<span class=\"share\" style=\"margin-left: 10px;\">\n<a title=\"分享到人民微博\" class=\"shareRMWB\" style=\"color: rgb(51, 51, 51); display: inline-block; width: 18px; height: 18px; vertical-align: text-top; background: url(&quot;../images/icon_arr_01.png&quot;) -50px -200px no-repeat; margin-left: 10px;\">\n</a><a title=\"分享到新浪微博\" class=\"shareSina\" style=\"color: rgb(51, 51, 51); display: inline-block; width: 19px; height: 17px; vertical-align: text-top; background: url(&quot;../images/icon_arr_01.png&quot;) -100px -200px no-repeat; margin-left: 10px;\">\n</a></span></div><div class=\"artDet\">\n<table width=\"450\" cellspacing=\"0\" cellpadding=\"3\" align=\"center\" style=\"width: 628px; font-family: Verdana, &quot;Microsoft YaHei&quot;, sans-serif; color: rgb(51, 51, 51);\">\n<tbody><tr style=\"padding: 0px;\">\n<td align=\"center\" style=\"padding: 0px;\">\n<img src=\"http://health.people.com.cn/NMediaFile/2018/0511/MAIN201805110815000275991596891.JPG\" alt=\"抑郁症患者如何进行自我康复？这四招一定要学会\" style=\"margin: 0px; padding: 0px; border: none; vertical-align: middle;\">\n</td></tr><tr style=\"padding: 0px;\">\n<td align=\"center\" style=\"padding: 0px;\">\n<font color=\"#000000\">\n</font></td></tr></tbody></table><p style=\"margin-top: 25px; margin-bottom: 25px; font-size: 18px; line-height: 32.4px; text-indent: 2em; color: rgb(51, 51, 51); font-family: Verdana, &quot;Microsoft YaHei&quot;, sans-serif;\">\n“心中的抑郁就像只黑狗，一有机会就咬住我不放。”丘吉尔如此形容抑郁症，这位闻名世界的政治家生前就曾饱受抑郁症的折磨。其实，抑郁症这条“黑狗”离我们并不遥远，随着一些关于抑郁症的新闻事件被报道，越来越多的人们逐渐正视抑郁症带来的问题。不过，大家也没有必要对抑郁症望而生畏，有些抑郁症是能够自我康复的。\n</p>\n<p style=\"margin-top: 25px; margin-bottom: 25px; font-size: 18px; line-height: 32.4px; text-indent: 2em; color: rgb(51, 51, 51); font-family: Verdana, &quot;Microsoft YaHei&quot;, sans-serif;\">\n抑郁症是一种复杂的心理-精神类疾病，可由多种原因引起，按照症状由轻到重，抑郁症可以划分为以下几种类型：\n</p>\n<p style=\"margin-top: 25px; margin-bottom: 25px; font-size: 18px; line-height: 32.4px; text-indent: 2em; color: rgb(51, 51, 51); font-family: Verdana, &quot;Microsoft YaHei&quot;, sans-serif;\">\n1、非忧郁型抑郁症：患者会表现出长期的、较为严重的心情不振作、食欲不佳、睡眠质量差、低活力、低欲望、自我评价降低等症状。\n</p>\n<p style=\"margin-top: 25px; margin-bottom: 25px; font-size: 18px; line-height: 32.4px; text-indent: 2em; color: rgb(51, 51, 51); font-family: Verdana, &quot;Microsoft YaHei&quot;, sans-serif;\">\n2、<span style=\"text-indent: 2em;\">\n忧郁型抑郁</span>症<span style=\"text-indent: 2em;\">\n：除</span>非忧郁型抑郁症的症状外，<span style=\"text-indent: 2em;\">\n患者会感觉全身或局部肌肉群强直或者出现痉挛、抽搐，还可能像感冒一样出现头痛、头晕、浑身乏力等症状。</span>\n</p>\n<p style=\"margin-top: 25px; margin-bottom: 25px; font-size: 18px; line-height: 32.4px; text-indent: 2em; color: rgb(51, 51, 51); font-family: Verdana, &quot;Microsoft YaHei&quot;, sans-serif;\">\n3、精神病型抑郁症：除忧郁型抑郁症的症状外，患者会出现某些精神病型症状，其中比较典型的是妄想、幻听、幻视等。\n</p>\n<p style=\"margin-top: 25px; margin-bottom: 25px; font-size: 18px; line-height: 32.4px; text-indent: 2em; color: rgb(51, 51, 51); font-family: Verdana, &quot;Microsoft YaHei&quot;, sans-serif;\">\n对于忧郁型抑郁症和精神病型抑郁症，患者一定要及时就医，并根据医嘱接受治疗。但对于非忧郁型抑郁症，患者是可以通过某些方法进行自我康复的，这里向大家介绍几种康复方法：\n</p>\n<p style=\"margin-top: 25px; margin-bottom: 25px; font-size: 18px; line-height: 32.4px; text-indent: 2em; color: rgb(51, 51, 51); font-family: Verdana, &quot;Microsoft YaHei&quot;, sans-serif;\">\n1、寻求情感支持。情感支持对抑郁症患者非常重要，研究发现，<span style=\"text-indent: 2em;\">\n几乎所有与抑郁症抗争过的患者，都会对那些曾经给予过自己情感支持的人念念不忘。如果在亲朋好友中找不到情感支持，不妨加入某些心理互助小组。</span>\n</p>\n<p style=\"margin-top: 25px; margin-bottom: 25px; font-size: 18px; line-height: 32.4px; text-indent: 2em; color: rgb(51, 51, 51); font-family: Verdana, &quot;Microsoft YaHei&quot;, sans-serif;\">\n<span style=\"text-indent: 2em;\">\n2、</span><span style=\"text-indent: 2em;\">\n进行心理咨询。专业的心理咨询师不仅拥有专业知识，而且充满关怀和同情，善于倾听，能够理解患者的语言和情绪，并可以同患者进行有效的沟通。</span>\n</p>\n<p style=\"margin-top: 25px; margin-bottom: 25px; font-size: 18px; line-height: 32.4px; text-indent: 2em; color: rgb(51, 51, 51); font-family: Verdana, &quot;Microsoft YaHei&quot;, sans-serif;\">\n3、在奉献中找到价值。抑郁症患者可以参加志愿活动，积极利用自己的力量服务他人，并从中找到生活的意义和价值，进而改善精神状态。\n</p>\n<p style=\"margin-top: 25px; margin-bottom: 25px; font-size: 18px; line-height: 32.4px; text-indent: 2em; color: rgb(51, 51, 51); font-family: Verdana, &quot;Microsoft YaHei&quot;, sans-serif;\">\n4、适当进行“爆发训练”。在身体能够承受的范围内进行爆发训练有利于提高大脑的内啡肽分泌水平，进而有利于改善情绪。患者可以每天散步30~45分钟，并在散步中做4~5组1分钟的快走或快跑。\n</p>\n<div><br></div></div>','医说健康',103,25,1,'2019-11-23','2021-11-20 10:39:43','2021-12-06 15:07:30'),(4,'抑郁症患者如何进行自我康复？这四招一定要学会',3,'https://gimg2.baidu.com/image_search/src=http%3A%2F%2F5b0988e595225.cdn.sohucs.com%2Fimages%2F20200425%2F3f841697b65c45ffbcf9b48c0103911f.jpeg&refer=http%3A%2F%2F5b0988e595225.cdn.sohucs.com&app=2002&size=f9999,10000&q=a80&n=0&g=0n&fmt=jpeg?sec=1641036904&t=b6ca4e97d25b2a8cdc32ad81329752be','摘要','<div class=\"title\" style=\"text-align: center;\">\n<h2 style=\"margin: 20px 0px 10px; padding: 0px; font-size: 30px; line-height: 36px;\">\n抑郁症患者如何进行自我康复？这四招一定要学会</h2></div><div class=\"artOri\" style=\"padding: 10px 0px; font-size: 12px; color: rgb(153, 153, 153);\">\n<span class=\"share\" style=\"margin-left: 10px;\">\n<a title=\"分享到人民微博\" class=\"shareRMWB\" style=\"color: rgb(51, 51, 51); display: inline-block; width: 18px; height: 18px; vertical-align: text-top; background: url(&quot;../images/icon_arr_01.png&quot;) -50px -200px no-repeat; margin-left: 10px;\">\n</a><a title=\"分享到新浪微博\" class=\"shareSina\" style=\"color: rgb(51, 51, 51); display: inline-block; width: 19px; height: 17px; vertical-align: text-top; background: url(&quot;../images/icon_arr_01.png&quot;) -100px -200px no-repeat; margin-left: 10px;\">\n</a></span></div><div class=\"artDet\">\n<table width=\"450\" cellspacing=\"0\" cellpadding=\"3\" align=\"center\" style=\"width: 628px; font-family: Verdana, &quot;Microsoft YaHei&quot;, sans-serif; color: rgb(51, 51, 51);\">\n<tbody><tr style=\"padding: 0px;\">\n<td align=\"center\" style=\"padding: 0px;\">\n<img src=\"http://health.people.com.cn/NMediaFile/2018/0511/MAIN201805110815000275991596891.JPG\" alt=\"抑郁症患者如何进行自我康复？这四招一定要学会\" style=\"margin: 0px; padding: 0px; border: none; vertical-align: middle;\">\n</td></tr><tr style=\"padding: 0px;\">\n<td align=\"center\" style=\"padding: 0px;\">\n<font color=\"#000000\">\n</font></td></tr></tbody></table><p style=\"margin-top: 25px; margin-bottom: 25px; font-size: 18px; line-height: 32.4px; text-indent: 2em; color: rgb(51, 51, 51); font-family: Verdana, &quot;Microsoft YaHei&quot;, sans-serif;\">\n“心中的抑郁就像只黑狗，一有机会就咬住我不放。”丘吉尔如此形容抑郁症，这位闻名世界的政治家生前就曾饱受抑郁症的折磨。其实，抑郁症这条“黑狗”离我们并不遥远，随着一些关于抑郁症的新闻事件被报道，越来越多的人们逐渐正视抑郁症带来的问题。不过，大家也没有必要对抑郁症望而生畏，有些抑郁症是能够自我康复的。\n</p>\n<p style=\"margin-top: 25px; margin-bottom: 25px; font-size: 18px; line-height: 32.4px; text-indent: 2em; color: rgb(51, 51, 51); font-family: Verdana, &quot;Microsoft YaHei&quot;, sans-serif;\">\n抑郁症是一种复杂的心理-精神类疾病，可由多种原因引起，按照症状由轻到重，抑郁症可以划分为以下几种类型：\n</p>\n<p style=\"margin-top: 25px; margin-bottom: 25px; font-size: 18px; line-height: 32.4px; text-indent: 2em; color: rgb(51, 51, 51); font-family: Verdana, &quot;Microsoft YaHei&quot;, sans-serif;\">\n1、非忧郁型抑郁症：患者会表现出长期的、较为严重的心情不振作、食欲不佳、睡眠质量差、低活力、低欲望、自我评价降低等症状。\n</p>\n<p style=\"margin-top: 25px; margin-bottom: 25px; font-size: 18px; line-height: 32.4px; text-indent: 2em; color: rgb(51, 51, 51); font-family: Verdana, &quot;Microsoft YaHei&quot;, sans-serif;\">\n2、<span style=\"text-indent: 2em;\">\n忧郁型抑郁</span>症<span style=\"text-indent: 2em;\">\n：除</span>非忧郁型抑郁症的症状外，<span style=\"text-indent: 2em;\">\n患者会感觉全身或局部肌肉群强直或者出现痉挛、抽搐，还可能像感冒一样出现头痛、头晕、浑身乏力等症状。</span>\n</p>\n<p style=\"margin-top: 25px; margin-bottom: 25px; font-size: 18px; line-height: 32.4px; text-indent: 2em; color: rgb(51, 51, 51); font-family: Verdana, &quot;Microsoft YaHei&quot;, sans-serif;\">\n3、精神病型抑郁症：除忧郁型抑郁症的症状外，患者会出现某些精神病型症状，其中比较典型的是妄想、幻听、幻视等。\n</p>\n<p style=\"margin-top: 25px; margin-bottom: 25px; font-size: 18px; line-height: 32.4px; text-indent: 2em; color: rgb(51, 51, 51); font-family: Verdana, &quot;Microsoft YaHei&quot;, sans-serif;\">\n对于忧郁型抑郁症和精神病型抑郁症，患者一定要及时就医，并根据医嘱接受治疗。但对于非忧郁型抑郁症，患者是可以通过某些方法进行自我康复的，这里向大家介绍几种康复方法：\n</p>\n<p style=\"margin-top: 25px; margin-bottom: 25px; font-size: 18px; line-height: 32.4px; text-indent: 2em; color: rgb(51, 51, 51); font-family: Verdana, &quot;Microsoft YaHei&quot;, sans-serif;\">\n1、寻求情感支持。情感支持对抑郁症患者非常重要，研究发现，<span style=\"text-indent: 2em;\">\n几乎所有与抑郁症抗争过的患者，都会对那些曾经给予过自己情感支持的人念念不忘。如果在亲朋好友中找不到情感支持，不妨加入某些心理互助小组。</span>\n</p>\n<p style=\"margin-top: 25px; margin-bottom: 25px; font-size: 18px; line-height: 32.4px; text-indent: 2em; color: rgb(51, 51, 51); font-family: Verdana, &quot;Microsoft YaHei&quot;, sans-serif;\">\n<span style=\"text-indent: 2em;\">\n2、</span><span style=\"text-indent: 2em;\">\n进行心理咨询。专业的心理咨询师不仅拥有专业知识，而且充满关怀和同情，善于倾听，能够理解患者的语言和情绪，并可以同患者进行有效的沟通。</span>\n</p>\n<p style=\"margin-top: 25px; margin-bottom: 25px; font-size: 18px; line-height: 32.4px; text-indent: 2em; color: rgb(51, 51, 51); font-family: Verdana, &quot;Microsoft YaHei&quot;, sans-serif;\">\n3、在奉献中找到价值。抑郁症患者可以参加志愿活动，积极利用自己的力量服务他人，并从中找到生活的意义和价值，进而改善精神状态。\n</p>\n<p style=\"margin-top: 25px; margin-bottom: 25px; font-size: 18px; line-height: 32.4px; text-indent: 2em; color: rgb(51, 51, 51); font-family: Verdana, &quot;Microsoft YaHei&quot;, sans-serif;\">\n4、适当进行“爆发训练”。在身体能够承受的范围内进行爆发训练有利于提高大脑的内啡肽分泌水平，进而有利于改善情绪。患者可以每天散步30~45分钟，并在散步中做4~5组1分钟的快走或快跑。\n</p>\n<div><br></div></div>','唐义诚',58,3,1,'2018-11-12','2021-11-20 10:39:43','2021-12-02 01:08:50'),(5,'得了抑郁症有哪些表现，这5个症状全占，也许比较严重了',2,'https://img1.baidu.com/it/u=4212850267,1189086050&fm=26&fmt=auto','摘要','<div class=\"XvFFhe0M00eU66dhHzH47\" style=\"background-color: rgb(248, 248, 249); border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(242, 242, 242); font-family: arial; font-size: 12px;\"><div class=\"_2j52cNz2l1nejzCrafdFDM\" style=\"padding-bottom: 24px; width: 900px; margin: 0px auto;\"><div class=\"_2akiyyCcJt3GTUofn31jvy \" style=\"font-size: 40px; line-height: 58px; padding-top: 41px; padding-bottom: 21px; text-align: justify; font-weight: 700; font-family: &quot;Microsoft Yahei&quot;, 微软雅黑, 宋体;\">得了抑郁症有哪些表现，这5个症状全占，也许比较严重了<div class=\"_2p8ac0wGmQwvO7JvBsm6Pn \" style=\"position: relative; display: inline-block; top: -6px; margin-left: 12px; width: 90px; height: 28px; padding-top: 6px; line-height: 1; box-sizing: border-box; padding-left: 24px; border-top-left-radius: 17.5px; border-top-right-radius: 17.5px; border-bottom-right-radius: 17.5px; border-bottom-left-radius: 17.5px; border: 1px solid rgba(0, 0, 0, 0.1); color: rgb(98, 102, 117); fill: rgb(98, 102, 117); font-size: 14px; font-weight: 400; -webkit-box-align: center; align-items: center; cursor: pointer;\">播报文章</div></div><div class=\"_7w5ONtCawo-feDOnBTED2\" style=\"margin-right: 250px; display: flex;\"><div class=\"XxtoRkG7yvq0XKZfamzhH\" style=\"position: relative; width: 47px; height: 47px; margin-right: 15px; border-top-left-radius: 50%; border-top-right-radius: 50%; border-bottom-right-radius: 50%; border-bottom-left-radius: 50%;\"><a href=\"https://author.baidu.com/home?from=bjh_article&amp;app_id=1550417612014784\" target=\"_blank\" style=\"text-decoration: none;\"><img src=\"https://user-center.cdn.bcebos.com/head/raw/uc.101.4d5b074e.96-yIzV3EYHPaqFbEkhPRA?x-bce-process=image/resize,m_lfit,w_200,h_200&amp;autime=44797\" style=\"border: 0px; width: 47px; height: 47px; border-top-left-radius: 50%; border-top-right-radius: 50%; border-bottom-right-radius: 50%; border-bottom-left-radius: 50%;\"></a><i class=\"_36dpbkcN-xauJbnOkODju\" style=\"position: absolute; bottom: 0px; right: 0px; width: 15px; heigh','快速问医生',32,12,0,'2019-10-17','2021-11-20 10:39:43','2021-12-09 11:18:35');
INSERT INTO `kno_info` VALUES (6,'如何摆脱情感勒索，重塑健康人际关系？',2,'https://jdxl-img.jdxlt.com/post/bdd76d14f2914b3c9d5e1a37472c725e.png','摘要','<section style=\"color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<section><section><section><p style=\"margin-top: 0px; margin-bottom: 0px;\">\n<em>在情感勒索的关系中，我们以（牺牲）自己的需要为代价，去关注别人的需要。通过对别人的让步，我们为自己制造了一个短暂的安全假象，使我们得以栖身其中聊以自慰。我们避免了冲突和对立，但同时我们也失去了一个建立健康关系的机会。</em>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; text-align: right;\">\n<em>——《情感勒索》作者苏珊·福沃德博士</em>\n</p>\n</section><section>&nbsp;</section></section></section></section><p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<img alt=\"\" src=\"https://jdxl-img.jdxlt.com/images/12123e6bc86347a9acecdea700b49264.jpg\" style=\"border-style: none; vertical-align: middle; max-width: 100%;\">\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; text-align: center;\">\n<span style=\"font-size: 12px;\">\n《波西米亚狂想曲》剧照</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<br>上周去看了最近热映的《波西米亚狂想曲》。男主弗雷迪和助理保罗在暴雨中的最后对峙，很是激动人心。<br><br>前情提要是弗雷迪的前女友前来看望他，告诉男主她怀孕了，并且诚恳地几乎带着乞求地劝他离开渣男保罗。\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n当弗雷迪终于幡然悔悟，命令保罗从他的生活中消失时，保罗威胁弗雷迪，说他手上掌握着弗雷迪很多不为人知的秘密：同性恋取向、私生活混乱、纸醉金迷……幸好我们的男主还是毅然决然地离开了保罗，当然之后他还是付出了很大的舆论代价。\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n保罗的威胁伎俩，正是典型的<span style=\"color: rgb(193, 88, 85);\">\n<span style=\"font-weight: bolder;\">\n情感勒索</span></span>。它明确地传递出一个信息：如果你不按照我的要求做，有你好看的。\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<br>&nbsp;\n</p>\n<section style=\"color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<section><section><section><section><section><section><p style=\"margin-top: 0px; margin-bottom: 0px;\">\n<span style=\"font-size: 20px;\">\n<span style=\"color: rgb(255, 255, 255);\">\n<span style=\"font-weight: bolder;\">\n<span style=\"background-color: rgb(0, 122, 170);\">\n01</span></span></span></span>\n</p>\n</section></section><section><p style=\"margin-top: 0px; margin-bottom: 0px;\">\n<span style=\"font-size: 20px;\">\n<span style=\"color: rgb(0, 122, 170);\">\n<span style=\"font-weight: bolder;\">\n什么是情感勒索？</span></span></span>\n</p>\n</section></section></section></section></section></section><p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n日常生活中，我们有时难以直接向别人提出要求，也会耍一些操纵的小把戏，比如说：“哎呀，要是有人把窗子打开就好了。”而不是说：“能请你把窗子打开吗？”这些小把戏只要没给双方带来不适，其实也无伤大雅。\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n而从日常的操纵转变为极其有害的行为，有一条清晰的界限。即：“<span style=\"font-weight: bolder;\">\n以我们的意愿、健康和快乐为代价，不断地利用操纵的手段来迫使我们妥协，满足他们的要求，操纵就变成了情感勒索。</span>”\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n因为情感勒索者深知我们有多珍视我们与他们之间的关系，了解我们的弱点，甚至熟谙我们心底的秘密。<br><br>一旦他们自己的需求未被满足，就会利用掌握的隐私来威胁我们，以让我们妥协和让步。<br><br>所以你会观察到，情感勒索有6大典型症状：<br>&nbsp;\n</p>\n<ul style=\"color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<li><p style=\"margin-top: 0px; margin-bottom: 0px;\">\n<span style=\"color: rgb(0, 122, 170);\">\n<span style=\"font-weight: bolder;\">\n要求</span></span>：勒索者根据自己的需求向勒索者提出要求。<br>&nbsp;\n</p>\n</li>\n    <li><p style=\"margin-top: 0px; margin-bottom: 0px;\">\n<span style=\"color: rgb(0, 122, 170);\">\n<span style=\"font-weight: bolder;\">\n抗拒</span></span>：被勒索者认为要求不合理，感觉不舒服和抗拒。<br>&nbsp;\n</p>\n</li>\n    <li><p style=\"margin-top: 0px; margin-bottom: 0px;\">\n<span style=\"color: rgb(0, 122, 170);\">\n<span style=\"font-weight: bolder;\">\n压力</span></span>：勒索者对被勒索者施加压力。让被勒索者感到恐惧、内疚或者认为按照勒索者说的做，是自己的义务。<br>&nbsp;\n</p>\n</li>\n    <li><p style=\"margin-top: 0px; margin-bottom: 0px;\">\n<span style=\"color: rgb(0, 122, 170);\">\n<span style=\"font-weight: bolder;\">\n威胁</span></span>：在遭到被勒索者拒绝后，勒索者威胁对方。如：分手。<br>&nbsp;\n</p>\n</li>\n    <li><p style=\"margin-top: 0px; margin-bottom: 0px;\">\n<span style=\"color: rgb(0, 122, 170);\">\n<span style=\"font-weight: bolder;\">\n屈服</span></span>：为了缓解自己内心的焦灼（可能是恐惧、内疚和义务感带来的压力），被勒索者压抑自己的需求，屈服于勒索者。<br>&nbsp;\n</p>\n</li>\n    <li><p style=\"margin-top: 0px; margin-bottom: 0px;\">\n<span style=\"color: rgb(0, 122, 170);\">\n<span style=\"font-weight: bolder;\">\n重复</span></span>：需求、施压、屈服的关系模式奠定下来，并一而再再而三地上演。<br>&nbsp;\n</p>\n</li></ul><p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n不过，很多现实生活中的情感勒索，要比保罗对待弗雷迪的伎俩来的更加隐晦，不易察觉。<br><br>这是因为“情感勒索者会释放出厚厚的迷雾（FOG），来掩盖他们的行为，因而几乎不可能看出他们是如何摆布我们的”。FOG就是情感勒索者的勒索工具。\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; text-align: center;\">\n<img alt=\"\" src=\"https://jdxl-img.jdxlt.com/images/9e09062fe55144cf9b8cceef8ecc18d3.jpg\" style=\"border-style: none; vertical-align: middle; max-width: 100%; width: 200px;\">\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<br>&nbsp;\n</p>\n<section style=\"color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<section><section><section><section><section><section><p style=\"margin-top: 0px; margin-bottom: 0px;\">\n<span style=\"font-size: 20px;\">\n<span style=\"color: rgb(255, 255, 255);\">\n<span style=\"font-weight: bolder;\">\n<span style=\"background-color: rgb(0, 122, 170);\">\n&nbsp;02&nbsp;</span></span></span></span>\n</p>\n</section></section><section><p style=\"margin-top: 0px; margin-bottom: 0px;\">\n<span style=\"font-size: 20px;\">\n<span style=\"color: rgb(0, 122, 170);\">\n<span style=\"font-weight: bolder;\">\n情感勒索者的工具：FOG</span></span></span>\n</p>\n</section></section></section></section></section></section><p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<span style=\"font-weight: bolder;\">\n<span style=\"color: rgb(193, 88, 85);\">\nFOG代表的是：恐惧（Fear）、责任（Obligation）、内疚（Guilt）</span></span>。<br><br>勒索者很擅长通过巧妙的方式唤起我们的这些内在感受，让我们焦虑难耐、压力山大，最终迫使我们屈服于他们的要求。\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<section style=\"color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<section><section><section><section><section>&nbsp;</section><section><p style=\"margin-top: 0px; margin-bottom: 0px; text-align: center;\">\n<span style=\"color: rgb(193, 88, 85);\">\n<span style=\"font-weight: bolder;\">\nFear:恐惧</span></span>\n</p>\n</section><section>&nbsp;</section></section></section></section></section></section><p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n你可以尝试问自己以下的问题：<br>&nbsp;\n</p>\n<ul style=\"color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<li><p style=\"margin-top: 0px; margin-bottom: 0px;\">\n我是不是害怕他们反对我？<br>&nbsp;\n</p>\n</li>\n    <li><p style=\"margin-top: 0px; margin-bottom: 0px;\">\n我是不是害怕他们生气？<br>&nbsp;\n</p>\n</li>\n    <li><p style=\"margin-top: 0px; margin-bottom: 0px;\">\n我是不是害怕他们不再喜欢我、爱我，甚至会离开我？<br>&nbsp;\n</p>\n</li></ul><p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n如果有肯定的答案，那么勒索者正是利用我们最深的恐惧作为威胁手段，让我们臣服于他。<br><br>其实这些恐惧早在婴儿期就存在了，<span style=\"font-weight: bolder;\">\n早期的无助感给婴儿带来被抛弃的恐惧</span>。<br><br>当成年人遇到情感勒索时，原初的婴儿式的恐惧就容易被唤醒，让我们在压力之下不得不屈服。这是我们童年恐惧的成人版在上演。\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; text-align: center;\">\n<img alt=\"\" src=\"https://jdxl-img.jdxlt.com/images/a71c7ebc52d141d68e8b36921afe938b.png\" style=\"border-style: none; vertical-align: middle; max-width: 100%; width: 449px;\">\n<br>&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n这类勒索者常以<span style=\"color: rgb(0, 122, 170);\">\n<em><span style=\"font-weight: bolder;\">\n惩罚者</span></em></span>或者<span style=\"color: rgb(0, 122, 170);\">\n<span style=\"font-weight: bolder;\">\n<em>自我惩罚者</em></span></span>的面孔示人。他们告知我们，如果他们的需求得不到满足，我们可能会要承担何种后果，或者他们就会对自己做出什么。<br><br>比如，“要是你想和我离婚，就再也别想看到孩子。”“你要是离开我，我就去死。”\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n有时，他们也会带上<span style=\"color: rgb(0, 122, 170);\">\n<em><span style=\"font-weight: bolder;\">\n诱惑者</span></em></span>的面具：“我可以给你帮助/金钱/事业/爱情……如果你按照我说的做……否则……”<br><br>诱惑者给予我们奖赏，但很明显<span style=\"font-weight: bolder;\">\n奖赏是有条件的</span>，我们必须对他们唯命是从，否则就别想得到奖赏。\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<section style=\"color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<section><section><section><section><section>&nbsp;</section><section><p style=\"margin-top: 0px; margin-bottom: 0px; text-align: center;\">\n<span style=\"color: rgb(193, 88, 85);\">\n<span style=\"font-weight: bolder;\">\nObligation：义务/责任</span></span>\n</p>\n</section><section>&nbsp;</section></section></section></section></section></section><p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n勒索者强调自己因为他人而放弃和牺牲了多少，还会利用社会传统、宗教信仰等的信条，强调别人应该感到对他们有亏欠。<br><br>这一招也常常被全身心扑在子女身上（而疏忽自我成长）的父母所利用，他们有意识或者无意识地向子女灌输：一个好孩子应该陪伴在家人身边；我为这个家做出了这么多牺牲，你应该服从我。\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<span style=\"font-weight: bolder;\">\n勒索者把他们对我们的要求，转换成了我们应尽的义务。</span><br>&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n这是一种乔装打扮的勒索，是一种强迫之下的责任感，等同于<span style=\"font-weight: bolder;\">\n道德绑架</span>。<br>&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; text-align: center;\">\n<img alt=\"\" src=\"https://jdxl-img.jdxlt.com/images/e05b3f3f1c264e1faa8edda8c0916dd9.gif\" style=\"border-style: none; vertical-align: middle; max-width: 100%;\">\n<br>&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n而那些被责任和义务操纵的人，苏珊形容他们就像是希腊神话中的阿特拉斯神，用自己的双肩扛起了整个天穹。<br><br>他们模糊了自己对他人所承担责任的边界，只记得要对他人尽心尽力，却忽略了自己，他们的内心OS常常是：<br>&nbsp;\n</p>\n<ul style=\"color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<li><p style=\"margin-top: 0px; margin-bottom: 0px;\">\n这是我欠他们的。<br>&nbsp;\n</p>\n</li>\n    <li><p style=\"margin-top: 0px; margin-bottom: 0px;\">\n他们为我做了那么多，我不能拒绝他们的请求。<br>&nbsp;\n</p>\n</li>\n    <li><p style=\"margin-top: 0px; margin-bottom: 0px;\">\n这是我的责任。\n</p>\n</li></ul><section style=\"color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<section><section><section><section><section><br>&nbsp;</section><section><p style=\"margin-top: 0px; margin-bottom: 0px; text-align: center;\">\n<span style=\"color: rgb(193, 88, 85);\">\n<span style=\"font-weight: bolder;\">\nGuilt：内疚</span></span>\n</p>\n</section><section>&nbsp;</section></section></section></section></section></section><p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n内疚，是对伤害、欺骗、虐待等行为的一种自然的恰当的心理反应。但过度的错误的内疚感，会让我们误读自己的行为。<br><br>有一个例子揭示了<span style=\"color: rgb(0, 122, 170);\">\n<span style=\"font-weight: bolder;\">\n“错误内疚心理”</span></span>的形成过程：<br>&nbsp;\n</p>\n<ul style=\"color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<li><p style=\"margin-top: 0px; margin-bottom: 0px;\">\n我打电话告诉妈妈晚上不能陪她一起吃饭了。<span style=\"font-weight: bolder;\">\n（我的行为）</span><br>&nbsp;\n</p>\n</li>\n    <li><p style=\"margin-top: 0px; margin-bottom: 0px;\">\n妈妈不高兴了。<span style=\"font-weight: bolder;\">\n（别人因为我的行为而感到难过）</span><br>&nbsp;\n</p>\n</li>\n    <li><p style=\"margin-top: 0px; margin-bottom: 0px;\">\n我应该为妈妈的不高兴负责。<span style=\"font-weight: bolder;\">\n（迷雾出现了：我为此负全责，不管和我的行为有没有关系）</span><br>&nbsp;\n</p>\n</li>\n    <li><p style=\"margin-top: 0px; margin-bottom: 0px;\">\n我感到内疚，因为我的行为让她感觉到了被忽视。<span style=\"font-weight: bolder;\">\n（迷雾出现了：我感到内疚）</span><br>&nbsp;\n</p>\n</li>\n    <li><p style=\"margin-top: 0px; margin-bottom: 0px;\">\n我推掉了所有其他安排，陪妈妈一起吃晚饭。<span style=\"font-weight: bolder;\">\n（我愿意做任何事情来补偿，以让我感觉到好受一些）</span><br>&nbsp;\n</p>\n</li></ul><p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n这个例子中的逻辑一推便倒：为什么仅仅因为一顿饭没有女儿陪伴吃，妈妈就会觉得自己被忽视而不高兴？这应该是妈妈自身未解决的关系议题，而非女儿应全权承担的责任。\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n但是勒索者释放出的FOG太厚重，常常让我们看不见这样的逻辑漏洞，尤其当勒索者是我们生命中的重要他人时，我们内心的OS自动切换为：<br>&nbsp;\n</p>\n<ul style=\"color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<li><p style=\"margin-top: 0px; margin-bottom: 0px;\">\n如果我不这么做，我会感到内疚。<br>&nbsp;\n</p>\n</li>\n    <li><p style=\"margin-top: 0px; margin-bottom: 0px;\">\n如果我不这么做，我会觉得自己很自私、没有爱心、贪婪、小气。<br>&nbsp;\n</p>\n</li>\n    <li><p style=\"margin-top: 0px; margin-bottom: 0px;\">\n如果我不这么做，我就不是一个好人。&nbsp;<br>&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; text-align: center;\">\n<img alt=\"\" src=\"https://jdxl-img.jdxlt.com/images/1eb403c3b3ea422d9049d5b1d4e0ae7f.jpg\" style=\"border-style: none; vertical-align: middle; max-width: 100%; width: 200px;\">\n<br>&nbsp;\n</p>\n</li></ul><p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n这类勒索者像<span style=\"color: rgb(0, 122, 170);\">\n<em><span style=\"font-weight: bolder;\">\n受害者</span></em></span>一样，常唤起我们的错误内疚心理。让我们觉得如果不按照他的要求做，他就会受到伤害，而这是我们的错。\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\nFOG，让我们在最熟悉的关系里，迷失了方向。<span style=\"font-weight: bolder;\">\n其实在我看来，与其说FOG是勒索者释放的迷雾，还不如说它是我们内心的阴霾。</span><br><br>是我们自己对被抛弃有深深的恐惧，认为自己有对别人负全责的义务，还有错误的内疚心理作祟，才让我们更容易成为勒索者的猎物。<br>&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; text-align: center;\">\n<img alt=\"\" src=\"https://jdxl-img.jdxlt.com/images/e05b3f3f1c264e1faa8edda8c0916dd9.gif\" style=\"border-style: none; vertical-align: middle; max-width: 100%;\">\n<br>&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n这可能有点扎心：明明我是被勒索的受害者，怎么我还成了问题的始作俑者？<br><br>因为情感勒索，从来不是一个人的交易，它是两个人的“<span style=\"font-weight: bolder;\">\n共谋</span>”。<br><br>共谋，并不是说情感勒索由被勒索者而起，而是被勒索者在某些方面允许了勒索的发生。<br><br>作者在书中就总结了一些<span style=\"font-weight: bolder;\">\n容易被勒索的人格特质</span>：<br>&nbsp;\n</p>\n<ul style=\"color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<li><p style=\"margin-top: 0px; margin-bottom: 0px;\">\n对认同的过分需求；<br>&nbsp;\n</p>\n</li>\n    <li><p style=\"margin-top: 0px; margin-bottom: 0px;\">\n对愤怒的强烈恐惧；<br>&nbsp;\n</p>\n</li>\n    <li><p style=\"margin-top: 0px; margin-bottom: 0px;\">\n为了获得平静的生活愿意付出任何代价（息事宁人）；<br>&nbsp;\n</p>\n</li>\n    <li><p style=\"margin-top: 0px; margin-bottom: 0px;\">\n倾向于对别人的生活负担起过多的责任；<br>&nbsp;\n</p>\n</li>\n    <li><p style=\"margin-top: 0px; margin-bottom: 0px;\">\n高度的自我怀疑：如果我们不相信自己，我们注定会赋予别人聪明和智慧。<br>&nbsp;\n</p>\n</li></ul><p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n这些特质中，无疑也可以看到FOG的影子<span style=\"font-weight: bolder;\">\n：对不被认同、愤怒的恐惧，模糊的责任义务界限，以及对自我判断的不确信造成的错误的内疚。</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; text-align: center;\">\n<img alt=\"\" src=\"https://jdxl-img.jdxlt.com/images/278a4b7dd1db4d898b58a5a2cecbfd22.png\" style=\"border-style: none; vertical-align: middle; max-width: 100%; width: 268px;\">\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; text-align: center;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n除了FOG这三个我们内心的“小辫子”以外，情感勒索者还常常用一些手段，让我们背负上沉重的包袱，臣服于他们。比如：<br>&nbsp;\n</p>\n<ul style=\"color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<li><p style=\"margin-top: 0px; margin-bottom: 0px;\">\n<span style=\"font-weight: bolder;\">\n角色塑造</span>：他们给我们贴上我们不愿接受的标签，贬低我们的品格、动机和自我价值，以此向我们施压。“你真是个自私鬼！”“你太让我失望了！”<br>&nbsp;\n</p>\n</li>\n    <li><p style=\"margin-top: 0px; margin-bottom: 0px;\">\n<span style=\"font-weight: bolder;\">\n归于病态</span>：勒索者告诉我们，我们之所以拒绝他们，是因为我们神经质、性格扭曲、丧失了理智。这会让我们对自己的记忆、判断、智力甚至品格产生怀疑，缺乏自信。<br>&nbsp;\n</p>\n</li>\n    <li><p style=\"margin-top: 0px; margin-bottom: 0px;\">\n<span style=\"font-weight: bolder;\">\n寻找同盟</span>：如果一个人的勒索无法奏效，勒索者就会把其他家庭成员、朋友、权威等拉来当后援。当被勒索者关心的人、尊重的人都站在勒索者的同盟阵线前时，被勒索者会感到非常无力。<br>&nbsp;\n</p>\n</li>\n    <li><p style=\"margin-top: 0px; margin-bottom: 0px;\">\n<span style=\"font-weight: bolder;\">\n反面对比</span>：“为什么你不能像……一样？”有时候勒索者只需要讲这样一句话，就能让我们感到自己的不足，让我们焦虑、内疚，以致于向勒索者屈服，以证明他们错怪了我们。<br>&nbsp;\n</p>\n</li></ul><p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<section style=\"color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<section><section><section><section><section><section><p style=\"margin-top: 0px; margin-bottom: 0px;\">\n<span style=\"color: rgb(255, 255, 255);\">\n<span style=\"font-size: 20px;\">\n<span style=\"font-weight: bolder;\">\n<span style=\"background-color: rgb(0, 122, 170);\">\n&nbsp;03&nbsp;</span></span></span></span>\n</p>\n</section></section><section><p style=\"margin-top: 0px; margin-bottom: 0px;\">\n<span style=\"color: rgb(0, 122, 170);\">\n<span style=\"font-size: 20px;\">\n<span style=\"font-weight: bolder;\">\n为什么会有情感勒索者的存在？</span></span></span>\n</p>\n</section></section></section></section></section></section><p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n作者坦言，其实“大部分的勒索者并不是恶魔，他们很少被内心的邪恶所驱使，相反他们是被心魔所驱使的”。<br><br>他们有的可能有过不幸的童年，遭遇过重大损失，失去了情感依赖的人，成年后，他们依然对挫折敏感，无法面对失去。\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n情感勒索者的内心充满了恐惧、焦虑和不安全感。<br><br>为了让自己不再有被拒绝、被忽视、被抛弃的感受，他们以勒索者的身份让自己处于看似强者、掌控者的地位。<br>&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; text-align: center;\">\n<img alt=\"\" src=\"https://jdxl-img.jdxlt.com/images/e05b3f3f1c264e1faa8edda8c0916dd9.gif\" style=\"border-style: none; vertical-align: middle; max-width: 100%;\">\n<br>&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n作者曾经说服她的一位来访者扮演咆哮式勒索者的角色，来访者表示当她咆哮的时候，一点儿也感觉不到有力量，反而是感觉到害怕和无助，就像是有人要拿走她最心爱的东西，她只能以咆哮和尖叫的方式以不让自己哭出来。\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n情感勒索，看似让勒索者处于了上风位置，却使得他们长久地堕入无明、混沌之中。更让被勒索者的自尊被损害，健康快乐被剥夺，还丧失了关系中的安全感和亲密感。<br><br>试想，当我们必须战战兢兢地和某人交往时，关系中还有什么真诚和亲密可言呢？\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; text-align: center;\">\n<img alt=\"\" src=\"https://jdxl-img.jdxlt.com/images/be4ebee068c04f06af616dc7c324a25c.jpg\" style=\"border-style: none; vertical-align: middle; max-width: 100%; width: 270px;\">\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<br>&nbsp;\n</p>\n<section style=\"color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<section><section><section><section><section><section><p style=\"margin-top: 0px; margin-bottom: 0px;\">\n<span style=\"color: rgb(255, 255, 255);\">\n<span style=\"font-size: 20px;\">\n<span style=\"font-weight: bolder;\">\n<span style=\"background-color: rgb(0, 122, 170);\">\n&nbsp;04&nbsp;</span></span></span></span>\n</p>\n</section></section><section><p style=\"margin-top: 0px; margin-bottom: 0px;\">\n<span style=\"color: rgb(0, 122, 170);\">\n<span style=\"font-size: 20px;\">\n<span style=\"font-weight: bolder;\">\n如何摆脱情感勒索？</span></span></span>\n</p>\n</section></section></section></section></section></section><p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n为了彻底改变情感勒索的不良关系，作者为我们提供了很多路径。在我看来，这其中最有效的一条路径便是：<span style=\"color: rgb(193, 88, 85);\">\n切断热键。</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n热键，就像是我们内心的软肋，与那些最能引起我们焦虑不安的感受联系紧密：恐惧、责任、内疚。<br><br>只要一按下这些热键，情感勒索的行为模式（要求、抗拒、压力、威胁、屈服、重复）就自动运行。<br><br>所以，切断热键，就有可能阻止情感勒索的模式一再上演。\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<section style=\"color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<section><section><section><section><section>&nbsp;</section><section><p style=\"margin-top: 0px; margin-bottom: 0px; text-align: center;\">\n<span style=\"color: rgb(193, 88, 85);\">\n<span style=\"font-weight: bolder;\">\n切断恐惧的热键</span></span>\n</p>\n</section><section>&nbsp;</section></section></section></section></section></section><p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n恐惧的反面是“<span style=\"color: rgb(193, 88, 85);\">\n<span style=\"font-weight: bolder;\">\n自由地想象和创造真正属于自己的生活</span></span>”。可以从以下几个方面来一一调整。\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<ul style=\"color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<li><span style=\"color: rgb(0, 122, 170);\">\n<span style=\"font-weight: bolder;\">\n应对反对</span></span>：梳理自己的价值观，明确自己喜欢的、看重的那些价值是什么。然后思考对方的价值观是什么，在两者间清楚地划出界限。</li></ul><span style=\"color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;</span><ul style=\"color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<li><span style=\"color: rgb(0, 122, 170);\">\n<span style=\"font-weight: bolder;\">\n应对愤怒</span></span>：选择一个平静的时刻向愤怒者摊牌，告诉他们你不喜欢他们咆哮发脾气，如果再吼叫，你就离开房间。或者在当下直接告诉对方，别叫了，平静下来再谈！</li></ul><span style=\"color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;</span><ul style=\"color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<li><span style=\"color: rgb(0, 122, 170);\">\n<span style=\"font-weight: bolder;\">\n应对改变</span></span>：改变可能是分手、离婚危机等等。告诉自己，“危机并不等于危险，只要鼓起勇气小心应对，危机也可以是个人成长和赢得更好生活的巨大机遇”。当你觉得一个人难以应对改变可能带来的危机时，可以寻求心理热线、心理咨询、支持性团体或社群的帮助。</li></ul><span style=\"color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;</span><ul style=\"color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<li><span style=\"color: rgb(0, 122, 170);\">\n<span style=\"font-weight: bolder;\">\n应对抛弃</span></span>：我们在爱的关系中感到被抛弃的恐惧，其实是我们童年恐惧的成人版。我们会觉得，要是被抛弃了，我们就活不下去了。要清楚地告诉自己：这只是我的幻象，不是事实！</li></ul><p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<section style=\"color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<section><section><section><section><section>&nbsp;</section><section><p style=\"margin-top: 0px; margin-bottom: 0px; text-align: center;\">\n<span style=\"color: rgb(193, 88, 85);\">\n<span style=\"font-weight: bolder;\">\n切断责任的热键</span></span>\n</p>\n</section><section>&nbsp;</section></section></section></section></section></section><p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n责任感是我们从父母、学校、宗教、社会文化中习得的。边界不清的责任感会让一个人疲惫不堪。<br><br>可以尝试把<span style=\"font-weight: bolder;\">\n<span style=\"color: rgb(193, 88, 85);\">\n别人对你的期望</span></span>一条一条写下来，比如：\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<em>即使让我放弃我自己喜欢梦想，我也要满足他们的期待。</em><br>&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<em>只要他们打电话给我提出要求，我就会立刻照做。</em><br>&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<em>……</em>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n写完之后，再以“<span style=\"color: rgb(193, 88, 85);\">\n<span style=\"font-weight: bolder;\">\n这有什么道理……</span></span>”开头，把这些句子重写一遍：\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<span style=\"font-weight: bolder;\">\n<em>这有什么道理</em></span><em>，即使让我放弃我自己喜欢梦想，我也要满足他们的期待？</em><br>&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<span style=\"font-weight: bolder;\">\n<em>这有什么道理</em></span><em>，只要他们打电话给我提出要求，我就会立刻照做？</em><br>&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<em>……</em>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n反复地练习，让被改写的、全新的信念体系植入进你的思想里。<br><br>顺便提一句，国内精神分析师张沛超老师在一篇分析《盗梦空间》的文章中，就曾提出，我们很多的信念都是在无意识中被植入的。<br><br>试想，如果意念能够被植入，那也可以被改写，以及，重新植入!\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<section style=\"color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<section><section><section><section><section>&nbsp;</section><section><p style=\"margin-top: 0px; margin-bottom: 0px; text-align: center;\">\n<span style=\"color: rgb(193, 88, 85);\">\n<span style=\"font-weight: bolder;\">\n切断内疚的热键</span></span>\n</p>\n</section><section>&nbsp;</section></section></section></section></section></section><p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n当你觉察到自己的内疚时，可以通过问自己以下问题，来分辨你的内疚是正常的还是错误的：\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<em>你做过的或想要做的是恶意的吗？</em><br>&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<em>你做过的或想要做的是残酷的吗？</em><br>&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<em>你做过的或想要做的具有虐待性吗？</em><br>&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<em>你做过的或想要做的涉及侮辱、贬低或者鄙视吗？</em><br>&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<em>你做过的或想要做的真的会损害别人的健康和快乐吗？</em><br>&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n当你的回答几乎都是否定的，而你还是感觉到矛盾和不安——也就是你的内疚与你的行为很不相称，那么你的<span style=\"font-weight: bolder;\">\n内疚很可能就是错误的或者被夸大的。</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<br>&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n以上，与你分享。<br><br><span style=\"color: rgb(193, 88, 85);\">\n<span style=\"font-weight: bolder;\">\n愿我们都能看清迷雾，拨开迷雾，重塑健康的人际关系</span></span>。\n</p>','简单心理',10,8,1,'2019-12-04','2021-11-20 10:39:43','2021-12-08 23:44:33'),(7,'抑郁症患者家属：抑郁症的人为什么无法控制自己的痛苦?',1,'https://s.cyol.com/shuzibao/cmsfile/paper/2021/1105/20211105060022796.jpg','摘要','<div class=\"XvFFhe0M00eU66dhHzH47\" style=\"background-color: rgb(248, 248, 249); border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(242, 242, 242); font-family: arial; font-size: 12px;\"><div class=\"_2j52cNz2l1nejzCrafdFDM\" style=\"padding-bottom: 24px; width: 900px; margin: 0px auto;\"><div class=\"_2akiyyCcJt3GTUofn31jvy \" style=\"font-size: 40px; line-height: 58px; padding-top: 41px; padding-bottom: 21px; text-align: justify; font-weight: 700; font-family: &quot;Microsoft Yahei&quot;, 微软雅黑, 宋体;\">抑郁症患者家属：抑郁症的人为什么无法控制自己的痛苦？<div class=\"_2p8ac0wGmQwvO7JvBsm6Pn \" style=\"position: relative; display: inline-block; top: -6px; margin-left: 12px; width: 90px; height: 28px; padding-top: 6px; line-height: 1; box-sizing: border-box; padding-left: 24px; border-top-left-radius: 17.5px; border-top-right-radius: 17.5px; border-bottom-right-radius: 17.5px; border-bottom-left-radius: 17.5px; border: 1px solid rgba(0, 0, 0, 0.1); color: rgb(98, 102, 117); fill: rgb(98, 102, 117); font-size: 14px; font-weight: 400; -webkit-box-align: center; align-items: center; cursor: pointer;\">播报文章</div></div><div class=\"_7w5ONtCawo-feDOnBTED2\" style=\"margin-right: 250px; display: flex;\"><div class=\"XxtoRkG7yvq0XKZfamzhH\" style=\"position: relative; width: 47px; height: 47px; margin-right: 15px; border-top-left-radius: 50%; border-top-right-radius: 50%; border-bottom-right-radius: 50%; border-bottom-left-radius: 50%;\"><a href=\"https://author.baidu.com/home?from=bjh_article&amp;app_id=1572244336932857\" target=\"_blank\" style=\"text-decoration: none;\"><img src=\"https://user-center.cdn.bcebos.com/head/raw/uc.101.13008187.Gv-F9S7Oi9EZRlFUjQ3wEA?x-bce-process=image/resize,m_lfit,w_200,h_200&amp;autime=13200\" style=\"border: 0px; width: 47px; height: 47px; border-top-left-radius: 50%; border-top-right-radius: 50%; border-bottom-right-radius: 50%; border-bottom-left-radius: 50%;\"></a><i class=\"_2aXNFSXQhGEiU1MQ3e8bJR\" style=\"position: absolute; bottom: 0px; right: 0px; width: 15px; heig','心理咨询师阿秋',249,34,0,'2020-04-30','2021-11-20 10:39:43','2021-12-09 11:17:24'),(8,'是不是心结打开了，抑郁症就好了?',1,'https://t11.baidu.com/it/u=596291557,2216472434&fm=173&app=25&f=JPEG?w=400&h=265&s=7AA2DC4FDEE3D0475A297C3903008050','摘要','https://t11.baidu.com/it/u=596291557,2216472434&fm=173&app=25&f=JPEG?w=400&h=265&s=7AA2DC4FDEE3D0475A297C3903008050’,\n‘<div class=\"XvFFhe0M00eU66dhHzH47\" style=\"background-color: rgb(248, 248, 249); border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(242, 242, 242);\"><div class=\"_2j52cNz2l1nejzCrafdFDM\" style=\"padding-bottom: 24px; width: 900px; margin: 0px auto;\"><div class=\"_2akiyyCcJt3GTUofn31jvy \" style=\"font-size: 40px; line-height: 58px; padding-top: 41px; padding-bottom: 21px; text-align: justify; font-weight: 700; font-family: &quot;Microsoft Yahei&quot;, 微软雅黑, 宋体;\">是不是心结打开了，抑郁症就好了？<div class=\"_2p8ac0wGmQwvO7JvBsm6Pn \" style=\"position: relative; display: inline-block; top: -6px; margin-left: 12px; width: 90px; height: 28px; padding-top: 6px; line-height: 1; box-sizing: border-box; padding-left: 24px; border-top-left-radius: 17.5px; border-top-right-radius: 17.5px; border-bottom-right-radius: 17.5px; border-bottom-left-radius: 17.5px; border: 1px solid rgba(0, 0, 0, 0.1); color: rgb(98, 102, 117); fill: rgb(98, 102, 117); font-size: 14px; font-weight: 400; -webkit-box-align: center; align-items: center; cursor: pointer;\">播报文章</div></div><div class=\"_7w5ONtCawo-feDOnBTED2\" style=\"margin-right: 250px; display: flex;\"><div class=\"XxtoRkG7yvq0XKZfamzhH\" style=\"position: relative; width: 47px; height: 47px; margin-right: 15px; border-top-left-radius: 50%; border-top-right-radius: 50%; border-bottom-right-radius: 50%; border-bottom-left-radius: 50%;\"><a href=\"https://author.baidu.com/home?from=bjh_article&amp;app_id=1587544793296125\" target=\"_blank\" style=\"text-decoration: none;\"><img src=\"https://user-center.cdn.bcebos.com/head/raw/uc.101.8abb1442.UcUEUbYpYoAbIFpFqE10nQ?x-bce-process=image/resize,m_lfit,w_200,h_200&amp;autime=47766\" style=\"border: 0px; width: 47px; height: 47px; border-top-left-radius: 50%; border-top-right-radius: 50%; border-bottom-right-radius: 50%; border-bottom-left-radius: 50%;\"></a><i class=\"_2aXNFSXQhGEiU1MQ3e8bJR\" style=\"position: absolute; bottom: 0px; right: 0px; width: 15px; height: 15px; background-image: url(&quot;data:imag','重塑心灵心理训练',688,45,1,'2018-04-17','2021-11-20 10:39:43','2021-11-24 19:08:14'),(9,'抑郁症患者可多吃这五种食物，帮助改善病情',5,'https://t10.baidu.com/it/u=1120442100,2029572236&fm=173&app=25&f=JPEG?w=640&h=427&s=5E06136512324794D60071890300E080','摘要','<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box; text-align: center;\">\n<em><span style=\"color: rgb(192, 57, 43);\">\n一封苦恼者的来信</span></em>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<em><span style=\"font-size: 14px;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n我有一个让人摸不着头脑的男友……<br><br>他常常在我想更接近他的时候选择逃避，比如邀他回家玩，他不想；想带我的朋友见他，他会说：“那是你的朋友，我又不熟。”</span></span></em>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<em><span style=\"font-size: 14px;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n<span style=\"font-weight: bolder;\">\n他认为谈恋爱也要有足够多的个人空间，</span>不要介入彼此的生活和朋友圈里，为此我感到很为难。</span></span></em>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<em><span style=\"font-size: 14px;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n但是，有时候他对我也挺好，尤其是工作上，他什么事都肯帮我，可是<span style=\"font-weight: bolder;\">\n当我很用心地说感谢他，试图跟他亲近时，他马上就会变得很奇怪，然后开始转移话题，</span>搞得不欢而散！</span></span></em>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<em><span style=\"font-size: 14px;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n“既然你那么喜欢独立，又为什么要和我谈恋爱？”我问他。</span></span></em>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<em><span style=\"font-size: 14px;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n“我觉得我们倆还蛮合适的啊，我挺照顾你的，不是吗？”我认为他这是答非所问。</span></span></em>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<em><span style=\"font-size: 14px;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n请问我该怎么办？</span></span></em>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<section class=\"Powered-by-XIUMI V5\" powered-by=\"xiumi.us\" style=\"box-sizing: border-box;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px;\">\n&nbsp;\n</p>\n</section><section class=\"Powered-by-XIUMI V5\" powered-by=\"xiumi.us\" style=\"box-sizing: border-box;\">\n<section style=\"box-sizing: border-box;\">\n<section style=\"letter-spacing: 1px; padding: 0px 5px; box-sizing: border-box;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n看完这位苦恼者的叙述，很多读者脑海里会出现一个熟悉的身影，因为可能你身边就有这样一个人……</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n这个人独立而高冷，若即若离，自我感觉良好，却常常让我们感到苦恼。这其实是典型的高<span style=\"box-sizing: border-box;\">\n<span style=\"font-weight: bolder;\">\n依恋回避</span></span></span><span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n<span style=\"box-sizing: border-box;\">\n<span style=\"font-weight: bolder;\">\n（attachment avoidance）</span></span></span><span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n的体现。</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(193, 88, 85);\">\n<span style=\"font-weight: bolder;\">\n高依恋回避的人通常在</span></span><span style=\"box-sizing: border-box; color: rgb(193, 88, 85);\">\n<span style=\"font-weight: bolder;\">\n面对亲密关系时感到很不舒服</span><span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n（Brennan, Clark, &amp; Shaver, 1998)</span><span style=\"font-weight: bolder;\">\n，他们不愿意依赖他人，也不愿意表露感情。</span></span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n这通常与他们成长过程中受到的情感忽视有关，</span><span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n每个人天生都是需要被爱和关心的，如果在成长过程中经常被忽视，在需要的时候得不到关爱，人们就</span><span style=\"font-weight: bolder;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n会逐渐形成“我不需要别人”的信念，以此当作自我保护，因为他们害怕表现出需要时会再次受到忽视和伤害。</span></span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n慢慢地，这种回避的模式成为了他们人际关系中的常态，并且他们通常认为自己不需要改变，而他们身边的人却会为此苦恼。</span>\n</p>\n</section></section></section><section class=\"Powered-by-XIUMI V5\" powered-by=\"xiumi.us\" style=\"box-sizing: border-box;\">\n<section style=\"box-sizing: border-box;\">\n<section style=\"box-sizing: border-box;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n</section></section></section><section class=\"Powered-by-XIUMI V5\" powered-by=\"xiumi.us\" style=\"box-sizing: border-box;\">\n<section style=\"box-sizing: border-box;\">\n<section style=\"letter-spacing: 1px; padding: 0px 5px; box-sizing: border-box;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n回顾刚刚那封苦恼者的来信，总结起来高依恋回避的人最核心的问题是：</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"color: rgb(193, 88, 85);\">\n<span style=\"font-weight: bolder;\">\n<span style=\"box-sizing: border-box;\">\n不信任、不在乎、“高”自尊。</span></span></span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; text-align: center;\">\n<img data-ratio=\"0.4444444444444444\" data-s=\"300,640\" data-type=\"png\" data-w=\"900\" src=\"https://jdxl-img.jdxlt.com/post/b1c43837eddb42978d075ba56f2ddc05.png\" style=\"border-style: none; vertical-align: middle; max-width: 100%;\">\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"font-weight: bolder;\">\n<span style=\"color: rgb(193, 88, 85);\">\n回避者的不信任</span></span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"font-weight: bolder;\">\n<span style=\"color: rgb(193, 88, 85);\">\n信赖他人代表可能再次受伤</span></span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; text-align: center; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n高依恋回避的人通常不信任他人，因为过去的经验告诉他们：<span style=\"font-weight: bolder;\">\n别人不可能给我情感的满足，我只能靠自己。</span></span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"color: rgb(68, 68, 68);\">\n所以他们在确定关系的时候会犹豫不决，害怕在感情中给予承诺。</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"color: rgb(68, 68, 68);\">\n面对恋爱，他们会说：“再说吧，不想那么快确定关系。”</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"color: rgb(68, 68, 68);\">\n面对婚姻，他们认为：“现在这样不是挺好的吗？我可不想那么快结婚。”</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n他们为自己保留了足够多的个人空间，并会在伴侣提出“想更多了解彼此”的需要时表现出慌乱不安，或者在伴侣表现出“粘人”时选择躲避。</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"font-weight: bolder; color: rgb(68, 68, 68); font-size: 15px;\">\n因为他们害怕与伴侣的亲密感会使他们再次对他人产生依赖，进而再次受伤。</span><span style=\"font-weight: bolder;\">\n<span style=\"color: rgb(68, 68, 68);\">\n他们的回避是出于不信任，而不代表他们真的不需要爱。</span></span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; text-align: center;\">\n<img data-ratio=\"0.014619883040935672\" data-s=\"300,640\" data-type=\"jpeg\" data-w=\"684\" src=\"https://jdxl-img.jdxlt.com/post/98c4c1feed9149bfa7d880a68079e7a9.jpeg\" style=\"border-style: none; vertical-align: middle; max-width: 100%;\">\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"font-weight: bolder;\">\n<span style=\"box-sizing: border-box; color: rgb(193, 88, 85);\">\n回避者的不在乎</span></span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"font-weight: bolder;\">\n<span style=\"box-sizing: border-box; color: rgb(193, 88, 85);\">\n就算很在意，也要装作不屑一顾</span></span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n他们还会表现出一副对什么都不在乎的样子，伤心难过时也不会轻易表露内心，因为他们认为<span style=\"font-weight: bolder;\">\n需要让自己显得强大而冷漠，才可以免受情感的伤害。</span>因此也常被认为是“花花公子”或“浪子”。</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n但是，有研究发现其实<span style=\"font-weight: bolder;\">\n高依恋回避的人内心深处对伴侣的忠诚非常看重，对伴侣的背叛行为会有激烈的情绪反应</span>(Feeney, 2004)<span style=\"font-weight: bolder;\">\n。</span></span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n他们虽然表面不在意，但内心敏感。如果伴侣撒谎、隐瞒，或者有背叛的行为，他们会感觉到自尊心很受打击，觉得自己再次被忽视。</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"font-weight: bolder;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n他们并不是真的不在乎，而是怕自己的在乎得不到情感回报，因此努力使自己显得不在乎。</span></span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; text-align: center;\">\n<img data-ratio=\"0.014619883040935672\" data-s=\"300,640\" data-type=\"jpeg\" data-w=\"684\" src=\"https://jdxl-img.jdxlt.com/post/59f5e6dcfb65487dbe5923311c462a28.jpeg\" style=\"border-style: none; vertical-align: middle; max-width: 100%;\">\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"font-weight: bolder;\">\n<span style=\"box-sizing: border-box; color: rgb(193, 88, 85);\">\n回避者的“高”自尊</span></span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"color: rgb(193, 88, 85);\">\n<span style=\"font-weight: bolder;\">\n我需要（看起来）很独立和强大</span></span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n相比于从社交中获得自我肯定，他们更倾向于把心思放在工作上，让自己在事业上尽可能出色和独立</span><span style=\"color: rgb(68, 68, 68);\">\n，</span><span style=\"color: rgb(68, 68, 68);\">\n并迫切地希望别人肯定自己。<br><br>他们会很乐意在生活上给伴侣提供帮助，比如介绍工作、指点人生方向什么的，一般在谈恋爱的时候，主动帮助对方通常代表着示好和希望进一步发展。</span><br><br><span style=\"color: rgb(68, 68, 68);\">\n但回避者并不是想要创造“亲密接触”的机会，而仅仅是想让对方觉得他们很自信和强大，以此进行自我保护。</span><br><br><span style=\"color: rgb(68, 68, 68);\">\n<span style=\"font-weight: bolder;\">\n<span style=\"box-sizing: border-box;\">\n大量的研究也发现，高依恋回避者的自尊水平并不像他们表现出的那样高</span></span></span><span style=\"color: rgb(68, 68, 68);\">\n<span style=\"box-sizing: border-box;\">\n（</span><span style=\"box-sizing: border-box;\">\n研究总结见 Mikulincer &amp; Shaver, 2016, p151-153）。</span></span><br><br><span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n他们只是</span><span style=\"color: rgb(68, 68, 68);\">\n希望让自己看起来更加强大，从而把命运掌握在自己手里，不再受伤。</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n</section></section></section><section class=\"Powered-by-XIUMI V5\" powered-by=\"xiumi.us\" style=\"box-sizing: border-box;\">\n<section style=\"text-align: center; box-sizing: border-box;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px;\">\n<img data-ratio=\"0.5\" data-type=\"gif\" data-w=\"700\" src=\"https://jdxl-img.jdxlt.com/post/e28b3bc6b19f423389dfd78b1389a40e.gif\" style=\"border-style: none; vertical-align: middle; max-width: 100%;\">\n<br>&nbsp;\n</p>\n</section></section><section class=\"Powered-by-XIUMI V5\" powered-by=\"xiumi.us\" style=\"box-sizing: border-box;\">\n<section style=\"box-sizing: border-box;\">\n<section style=\"letter-spacing: 1px; padding: 0px 5px; box-sizing: border-box;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; text-align: center; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(193, 88, 85);\">\n<span style=\"font-weight: bolder; box-sizing: border-box;\">\n作为伴侣，你可以做什么？</span></span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n作为回避者的伴侣，通常人们会从自己身上找原因，“是不是我盯得太紧了？”、</span><span style=\"color: rgb(68, 68, 68);\">\n“我应该给Ta更多空间的啊”、“Ta不喜欢黏人我就该尽量忍住不找Ta啊”。</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"color: rgb(68, 68, 68);\">\n但真的这样做的时候，有时候会感到特别特别委屈：“我就是想和你亲近点，这要求过分吗？！人家谈恋爱都如胶似漆，再不济也算是若即若离，就我，若离若离若离若离……”</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n而且还存在这样一个悖论<span style=\"font-weight: bolder; box-sizing: border-box;\">\n：</span></span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(193, 88, 85);\">\n给对方空间可以短期缓解依恋回避，</span><span style=\"box-sizing: border-box; color: rgb(193, 88, 85);\">\n但是从长期来看，这反而使他们觉得“回避是有用的”。</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(193, 88, 85);\">\n他们可能会更习惯于用“高冷”来保护自己免受伤害，从而加剧依恋回避</span><span style=\"box-sizing: border-box; color: rgb(193, 88, 85);\">\n（Arriaga et al., 2017)。</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n那么到底该如何帮助伴侣缓解依恋回避？</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; text-align: center;\">\n<img data-ratio=\"0.014619883040935672\" data-s=\"300,640\" data-type=\"jpeg\" data-w=\"684\" src=\"https://jdxl-img.jdxlt.com/post/6a33690827524ab2b2ad618364f08ea8.jpeg\" style=\"border-style: none; vertical-align: middle; max-width: 100%;\">\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n我们总结了三个方法：</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box; text-align: center;\">\n<em><span style=\"color: rgb(68, 68, 68);\">\n<span style=\"color: rgb(193, 88, 85);\">\n1.</span></span></em>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; text-align: center; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(193, 88, 85);\">\n<span style=\"font-weight: bolder; box-sizing: border-box;\">\n主动出击，但并非出于焦虑</span></span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n在依恋回避者高冷的人生路途中，能够让Ta改变的通常是“被动”经历一段令人惊喜的亲密关系。</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n以《致青春》中的男主陈孝正举例，</span><span style=\"color: rgb(68, 68, 68);\">\n他是典型的高依恋回避者，但是也在女主的死缠烂打下慢慢改变。</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"color: rgb(68, 68, 68);\">\n这场“被迫”建立的亲密关系却让他获得了许多意外的爱和支持，他的回避也在逐渐减少。</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n但有一点需要指出，</span><span style=\"box-sizing: border-box; color: rgb(193, 88, 85);\">\n如果你的出击带着一种自我怀疑（依恋焦虑），也就是因为害怕被抛弃而想时刻粘着对方，那会带来更大的矛盾。</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n因为</span><span style=\"color: rgb(68, 68, 68);\">\n你常常会因为对方的退缩而陷入自我否定和焦虑中，而你的纠缠会让</span><span style=\"color: rgb(68, 68, 68);\">\nTa</span><span style=\"color: rgb(68, 68, 68);\">\n感到更恐慌，更想逃离。</span>\n</p>\n</section></section></section><section class=\"Powered-by-XIUMI V5\" powered-by=\"xiumi.us\" style=\"box-sizing: border-box;\">\n<section style=\"box-sizing: border-box;\">\n<section style=\"letter-spacing: 1px; padding: 0px 5px; line-height: 1.6; box-sizing: border-box;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n<span style=\"font-weight: bolder;\">\n因此，若你本身有很高的依恋焦虑，那么你需要先建立自己在关系中的自信，才能够在这场恋爱中主动出击，帮助对方建立健康的亲密关系。</span></span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box; text-align: center;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n<span style=\"font-weight: bolder;\">\n<img data-ratio=\"0.43010752688172044\" data-w=\"465\" src=\"https://jdxl-img.jdxlt.com/post/fb7a86b48fcd4039875cfdd37560933b.gif\" style=\"border-style: none; vertical-align: middle; max-width: 100%; box-sizing: border-box; width: 556px;\">\n</span></span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box; text-align: center;\">\n<em><span style=\"box-sizing: border-box; color: rgb(193, 88, 85);\">\n2.</span></em>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; text-align: center; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(193, 88, 85);\">\n<span style=\"font-weight: bolder; box-sizing: border-box;\">\n表达情感，并减少情绪性指责</span></span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"color: rgb(68, 68, 68);\">\n作为伴侣，你可能很多次试图向对方敞开心扉，渴望有心灵的沟通，然而都被对方的高冷打败了，导致你心灰意冷甚至大发脾气，想着就算Ta以后需要你的时候，你也不要再管了。</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n其实，对方的高冷是因为曾经遭受过冷落，害怕再次经历沟通受挫的伤痛。</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n表达自己的情感对于他们来说是很难的，如果可以得到伴侣的支持和回应的话，</span><span style=\"box-sizing: border-box; color: rgb(193, 88, 85);\">\n这将会慢慢改变他们觉得“自己就算表达也一定会遭到忽视”的想法，从而与你<span style=\"box-sizing: border-box;\">\n建立起信任。</span></span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n<span style=\"box-sizing: border-box;\">\n<span style=\"font-weight: bolder;\">\n<span style=\"box-sizing: border-box;\">\n<span style=\"box-sizing: border-box;\">\n信任将是缓解依恋回避的有利解药</span></span></span><span style=\"box-sizing: border-box;\">\n<span style=\"box-sizing: border-box;\">\n(Arriaga et al., 2014)。</span></span></span></span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(193, 88, 85);\">\n<span style=\"font-weight: bolder; box-sizing: border-box;\">\n所以，请在Ta</span></span><span style=\"box-sizing: border-box; color: rgb(193, 88, 85);\">\n<span style=\"font-weight: bolder; box-sizing: border-box;\">\n需要的时候给予支持，而不要轻易指责Ta为何不愿意跟你说心里话，</span></span><span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n指责</span><span style=\"color: rgb(68, 68, 68);\">\n会使他们感到自尊受威胁，从而进行更加严重的自我防御。</span>\n</p>\n</section></section></section><section class=\"Powered-by-XIUMI V5\" powered-by=\"xiumi.us\" style=\"box-sizing: border-box;\">\n<section style=\"box-sizing: border-box;\">\n<section style=\"box-sizing: border-box;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n</section></section></section><section class=\"Powered-by-XIUMI V5\" powered-by=\"xiumi.us\" style=\"box-sizing: border-box;\">\n<section style=\"box-sizing: border-box;\">\n<section style=\"box-sizing: border-box;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box; text-align: center;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box; text-align: center;\">\n<span style=\"color: rgb(193, 88, 85);\">\n<em>3.</em></span>\n</p>\n</section></section></section><section class=\"Powered-by-XIUMI V5\" powered-by=\"xiumi.us\" style=\"box-sizing: border-box;\">\n<section style=\"box-sizing: border-box;\">\n<section style=\"letter-spacing: 1px; padding: 0px 5px; box-sizing: border-box;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; text-align: center; box-sizing: border-box;\">\n<span style=\"font-weight: bolder;\">\n<span style=\"box-sizing: border-box; color: rgb(193, 88, 85);\">\n多肯定Ta在社交上的进步</span></span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n一般来说，高依恋回避的人会渴望在个人目标上有所成就并得到肯定，包括工作、兴趣爱好等。但是你不仅可以肯定他的个人成就，更<span style=\"font-weight: bolder;\">\n应该多在社交能力上肯定他的进步。</span></span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n这并不是指虚无的夸奖，而是可以对那些很小的进步做出肯定，例如当Ta愿意：</span>\n</p>\n<ul class=\"list-paddingleft-2\" style=\"color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif;\">\n<li><p style=\"margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"color: rgb(68, 68, 68);\">\n跟你一起见好友；</span>\n</p>\n</li>\n    <li><p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"color: rgb(68, 68, 68);\">\n跟你有身体上的接触；</span>\n</p>\n</li>\n    <li><p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"color: rgb(68, 68, 68);\">\n跟你聊聊最近的烦恼；</span>\n</p>\n</li>\n    <li><p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"color: rgb(68, 68, 68);\">\n……</span>\n</p>\n</li></ul><p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n这些小事上你都可以肯定Ta，让Ta知道这些微妙的瞬间给你带来了感动和快乐。</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; box-sizing: border-box;\">\n<span style=\"color: rgb(68, 68, 68);\">\n<span style=\"font-weight: bolder;\">\n<span style=\"box-sizing: border-box;\">\n但是，在对方不愿意时，请不要强迫其做出改变和妥协。</span></span></span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; box-sizing: border-box;\">\n<span style=\"color: rgb(68, 68, 68);\">\n<span style=\"box-sizing: border-box;\">\n特别要说明的是，本文尽管是从伴侣的角度展开写的，但是<span style=\"box-sizing: border-box; color: rgb(193, 88, 85);\">\n并不代表你需要变成Ta改变的负责人</span>，伴侣是陪伴者的角色，最终决定是否改变的还是Ta自己。</span></span>\n</p>\n<div><span style=\"color: rgb(68, 68, 68);\">\n<span style=\"box-sizing: border-box;\">\n<br></span></span></div></section></section></section>','家庭医生名医在线',32,45,1,'2018-04-27','2021-11-20 10:39:43','2021-12-08 23:27:22');
INSERT INTO `kno_info` VALUES (10,'抑郁症、焦虑应注意：这些影响你情绪的食物',5,'https://t12.baidu.com/it/u=2755681325,160192771&fm=173&s=3F61F5170D126CC0004901E40300702A&w=640&h=456&img.JPEG','摘要','<div class=\"XvFFhe0M00eU66dhHzH47\" style=\"background-color: rgb(248, 248, 249); border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(242, 242, 242); font-family: arial; font-size: 12px;\">\n<div class=\"_2j52cNz2l1nejzCrafdFDM\" style=\"padding-bottom: 24px; width: 900px; margin: 0px auto;\">\n<div class=\"_2akiyyCcJt3GTUofn31jvy \" style=\"font-size: 40px; line-height: 58px; padding-top: 41px; padding-bottom: 21px; text-align: justify; font-weight: 700; font-family: &quot;Microsoft Yahei&quot;, 微软雅黑, 宋体;\">\n抑郁症、焦虑应注意：这些影响你情绪的食物<div class=\"_2p8ac0wGmQwvO7JvBsm6Pn \" style=\"position: relative; display: inline-block; top: -6px; margin-left: 12px; width: 90px; height: 28px; padding-top: 6px; line-height: 1; box-sizing: border-box; padding-left: 24px; border-top-left-radius: 17.5px; border-top-right-radius: 17.5px; border-bottom-right-radius: 17.5px; border-bottom-left-radius: 17.5px; border: 1px solid rgba(0, 0, 0, 0.1); color: rgb(98, 102, 117); fill: rgb(98, 102, 117); font-size: 14px; font-weight: 400; -webkit-box-align: center; align-items: center; cursor: pointer;\">\n<svg width=\"20px\" height=\"20px\" class=\"_3OdBW4WGFU1qSM-2IxXvzA\">\n<use xlink:href=\"#voice\">\n</use></svg>播报文章</div></div><div class=\"_7w5ONtCawo-feDOnBTED2\" style=\"margin-right: 250px; display: flex;\">\n<div class=\"XxtoRkG7yvq0XKZfamzhH\" style=\"position: relative; width: 47px; height: 47px; margin-right: 15px; border-top-left-radius: 50%; border-top-right-radius: 50%; border-bottom-right-radius: 50%; border-bottom-left-radius: 50%;\">\n<a href=\"https://author.baidu.com/home?from=bjh_article&amp;app_id=1576842586107937\" target=\"_blank\" style=\"text-decoration: none;\">\n<img src=\"https://user-center.cdn.bcebos.com/head/raw/uc.101.28d7d8b6.LbQ283d2rKfxs4Kp_kMv3g?x-bce-process=image/resize,m_lfit,w_200,h_200&amp;autime=51817\" style=\"border: 0px; width: 47px; height: 47px; border-top-left-radius: 50%; border-top-right-radius: 50%; border-bottom-right-radius: 50%; border-bottom-left-radius: 50%;\">\n</a></div><div class=\"_2hS5V7S24qH6ZgwPlwS24R\" style=\"max-width: 492px; overflow: hidden;\">\n<a href=\"https://author.baidu.com/home?from=bjh_article&amp;app_id=1576842586107937\" target=\"_blank\" style=\"text-decoration: none; color: rgb(0, 0, 0);\">\n<p class=\"_2K64ifOOmB97zMt4Zbh5tO\" style=\"margin-top: 0px; margin-bottom: 5px; width: 157.734375px; font-size: 16px;\">\n生活风趣事\n</p>\n</a><div class=\"_3FCL5rjl99gXUIj3vArRhW\" style=\"color: rgb(153, 153, 153); display: flex; -webkit-box-align: center; align-items: center;\">\n<span style=\"white-space: nowrap;\">\n发布时间: 18-03-04</span><span class=\"_2sNnKvMtJ8vRc88MvYIpT8\" style=\"margin: 0px 8px; white-space: nowrap;\">\n16:42</span><span class=\"_3MxZzPNuE_jM1w8fxcPDBh\" style=\"display: inline-block; height: 12px; width: 1px; background-color: rgb(224, 224, 224); margin-right: 8px; white-space: nowrap;\">\n</span><span class=\"ySuECg1udlHQ8BdVVdBSD\" style=\"white-space: nowrap; overflow: hidden; text-overflow: ellipsis;\">\n</span></div></div></div></div></div><div class=\"_2xiZo1dd2Za0bMpJ-pdB7B\" style=\"display: flex; -webkit-box-pack: justify; justify-content: space-between; width: 900px; margin: 0px auto; background-color: rgb(242, 242, 242); font-family: arial; font-size: 12px;\">\n<div class=\"_1vFr3RWSK0wz54V5vbzAW2\" style=\"width: 600px; position: relative; background-color: rgb(255, 255, 255); padding-right: 50px;\">\n<div class=\"_2OVtLCRVVVa5RwDyfhipoy \" style=\"padding-top: 40px; padding-bottom: 30px;\">\n<div class=\"_1T4GB1ESohOqTGNmwCAcuf \" style=\"max-width: 100%; overflow-x: visible; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51);\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; overflow-y: auto; max-width: 100%; line-height: 24px;\">\n<span class=\"bjh-p\" style=\"max-width: 100%;\">\n处理抑郁和焦虑可能很困难。当你处理这些情况时，你需要尽量避免坏情绪和压力，以及可能治疗有负面影响的事。</span>\n</p>\n</div><div class=\"_1T4GB1ESohOqTGNmwCAcuf \" style=\"max-width: 100%; overflow-x: visible; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51); margin-top: 22px;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; overflow-y: auto; max-width: 100%; line-height: 24px;\">\n<span class=\"bjh-p\" style=\"max-width: 100%;\">\n有些食物可以消极地影响你的抑郁和焦虑，所以需要有意识的避免食用这些食物。通过远离使你的抑郁或焦虑恶化的食物，你可以消除一些不必要的负面影响。</span>\n</p>\n</div><div class=\"_1T4GB1ESohOqTGNmwCAcuf \" style=\"max-width: 100%; overflow-x: visible; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51); margin-top: 22px;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; overflow-y: auto; max-width: 100%; line-height: 24px;\">\n<span class=\"bjh-p\" style=\"max-width: 100%;\">\n<span class=\"bjh-strong\" style=\"max-width: 100%; font-size: 18px; font-weight: 700;\">\n一.改变你的饮食</span></span>\n</p>\n</div><div class=\"_4TUZRg-Yz-JYH2P3V5fsh \" style=\"max-width: 100%; overflow-x: visible; display: flex; margin-top: 36px;\">\n<div class=\"_1Dg_4ksYttUK-gyQE1RGR6\" style=\"display: flex; -webkit-box-orient: vertical; -webkit-box-direction: normal; flex-direction: column; -webkit-box-align: center; align-items: center; width: 599px;\">\n<img src=\"https://t12.baidu.com/it/u=2755681325,160192771&amp;fm=173&amp;s=3F61F5170D126CC0004901E40300702A&amp;w=640&amp;h=456&amp;img.JPEG\" width=\"640\" class=\"_3yZQZ9OxCCD0QVw16rnEsS\" style=\"border: 0px; width: 599px; border-top-left-radius: 13px; border-top-right-radius: 13px; border-bottom-right-radius: 13px; border-bottom-left-radius: 13px;\">\n</div></div><div class=\"_1T4GB1ESohOqTGNmwCAcuf \" style=\"max-width: 100%; overflow-x: visible; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51); margin-top: 36px;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; overflow-y: auto; max-width: 100%; line-height: 24px;\">\n<span class=\"bjh-p\" style=\"max-width: 100%;\">\n<span class=\"bjh-strong\" style=\"max-width: 100%; font-size: 18px; font-weight: 700;\">\n避开糖</span></span>\n</p>\n</div><div class=\"_1T4GB1ESohOqTGNmwCAcuf \" style=\"max-width: 100%; overflow-x: visible; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51); margin-top: 22px;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; overflow-y: auto; max-width: 100%; line-height: 24px;\">\n<span class=\"bjh-p\" style=\"max-width: 100%;\">\n许多抑郁的人在食物中找到了舒适感，比如含糖的垃圾食品。冰淇淋，蛋糕和其他糖果可以提高血清素水平，这可能会让你感觉更好。但这只是一个暂时的情况，随后可能会出现崩溃。太多的精制糖会破坏你的情绪并导致疲劳。当您的血糖水平下降并且您经历崩溃时，糖类还会导致焦虑。</span>\n</p>\n</div><div class=\"_1T4GB1ESohOqTGNmwCAcuf \" style=\"max-width: 100%; overflow-x: visible; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51); margin-top: 22px;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; overflow-y: auto; max-width: 100%; line-height: 24px;\">\n<span class=\"bjh-ul\" style=\"max-width: 100%; font-size: 18px; list-style-type: disc; display: block; line-height: 27px; padding-left: 26px;\">\n<span class=\"bjh-li\" style=\"max-width: 100%; display: list-item; margin-bottom: 0px;\">\n<span class=\"bjh-p\" style=\"max-width: 100%;\">\n除了给你一种虚假的舒适感之外，高水平的糖还会导致健康问题，如体重增加，糖尿病和心脏病。</span></span></span>\n</p>\n</div><div class=\"_1T4GB1ESohOqTGNmwCAcuf \" style=\"max-width: 100%; overflow-x: visible; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51); margin-top: 22px;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; overflow-y: auto; max-width: 100%; line-height: 24px;\">\n<span class=\"bjh-p\" style=\"max-width: 100%;\">\n<span class=\"bjh-br\" style=\"max-width: 100%;\">\n</span></span>\n</p>\n</div><div class=\"_4TUZRg-Yz-JYH2P3V5fsh \" style=\"max-width: 100%; overflow-x: visible; display: flex; margin-top: 36px;\">\n<div class=\"_1Dg_4ksYttUK-gyQE1RGR6\" style=\"display: flex; -webkit-box-orient: vertical; -webkit-box-direction: normal; flex-direction: column; -webkit-box-align: center; align-items: center; width: 599px;\">\n<img src=\"https://t11.baidu.com/it/u=1910791384,938345048&amp;fm=173&amp;s=76C0D81B4F1274CE0EF194C8030060B5&amp;w=640&amp;h=458&amp;img.JPEG\" width=\"640\" class=\"_3yZQZ9OxCCD0QVw16rnEsS\" style=\"border: 0px; width: 599px; border-top-left-radius: 13px; border-top-right-radius: 13px; border-bottom-right-radius: 13px; border-bottom-left-radius: 13px;\">\n</div></div><div class=\"_1T4GB1ESohOqTGNmwCAcuf \" style=\"max-width: 100%; overflow-x: visible; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51); margin-top: 36px;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; overflow-y: auto; max-width: 100%; line-height: 24px;\">\n<span class=\"bjh-p\" style=\"max-width: 100%;\">\n精制碳水化合物，如白面包，零食和许多谷类食品，对你的抑郁和焦虑不利。</span>\n</p>\n</div><div class=\"_1T4GB1ESohOqTGNmwCAcuf \" style=\"max-width: 100%; overflow-x: visible; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51); margin-top: 22px;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; overflow-y: auto; max-width: 100%; line-height: 24px;\">\n<span class=\"bjh-p\" style=\"max-width: 100%;\">\n它们会导致血糖下降，从而导致不良情绪波动，烦躁不安和悲伤。不要使用精制的简单碳水化合物，而要吃复合碳水化合物，如全谷物。</span>\n</p>\n</div><div class=\"_1T4GB1ESohOqTGNmwCAcuf \" style=\"max-width: 100%; overflow-x: visible; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51); margin-top: 22px;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; overflow-y: auto; max-width: 100%; line-height: 24px;\">\n<span class=\"bjh-ul\" style=\"max-width: 100%; font-size: 18px; list-style-type: disc; display: block; line-height: 27px; padding-left: 26px;\">\n<span class=\"bjh-li\" style=\"max-width: 100%; display: list-item; margin-bottom: 11px;\">\n<span class=\"bjh-p\" style=\"max-width: 100%;\">\n避免像白面包，加工饼干和薯条，甜甜圈，蛋糕和松饼等食物。</span></span><span class=\"bjh-li\" style=\"max-width: 100%; display: list-item; margin-bottom: 0px;\">\n<span class=\"bjh-p\" style=\"max-width: 100%;\">\n血糖指数低的食物，如水果，蔬菜和全谷物更能保持心情稳定。</span></span></span>\n</p>\n</div><div class=\"_1T4GB1ESohOqTGNmwCAcuf \" style=\"max-width: 100%; overflow-x: visible; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51); margin-top: 22px;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; overflow-y: auto; max-width: 100%; line-height: 24px;\">\n<span class=\"bjh-p\" style=\"max-width: 100%;\">\n<span class=\"bjh-br\" style=\"max-width: 100%;\">\n</span></span>\n</p>\n</div><div class=\"_4TUZRg-Yz-JYH2P3V5fsh \" style=\"max-width: 100%; overflow-x: visible; display: flex; margin-top: 36px;\">\n<div class=\"_1Dg_4ksYttUK-gyQE1RGR6\" style=\"display: flex; -webkit-box-orient: vertical; -webkit-box-direction: normal; flex-direction: column; -webkit-box-align: center; align-items: center; width: 599px;\">\n<img src=\"https://t11.baidu.com/it/u=2519991841,423286835&amp;fm=173&amp;s=99A3C315891271DEC2B910D7030080A0&amp;w=640&amp;h=428&amp;img.JPEG\" width=\"640\" class=\"_3yZQZ9OxCCD0QVw16rnEsS\" style=\"border: 0px; width: 599px; border-top-left-radius: 13px; border-top-right-radius: 13px; border-bottom-right-radius: 13px; border-bottom-left-radius: 13px;\">\n</div></div><div class=\"_1T4GB1ESohOqTGNmwCAcuf \" style=\"max-width: 100%; overflow-x: visible; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51); margin-top: 36px;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; overflow-y: auto; max-width: 100%; line-height: 24px;\">\n<span class=\"bjh-p\" style=\"max-width: 100%;\">\n<span class=\"bjh-strong\" style=\"max-width: 100%; font-size: 18px; font-weight: 700;\">\n多吃优质蛋白质</span></span>\n</p>\n</div><div class=\"_1T4GB1ESohOqTGNmwCAcuf \" style=\"max-width: 100%; overflow-x: visible; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51); margin-top: 22px;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; overflow-y: auto; max-width: 100%; line-height: 24px;\">\n<span class=\"bjh-p\" style=\"max-width: 100%;\">\n蛋白质含有正常大脑功能所需的氨基酸。确保你的饮食中含有大量高质量的蛋白质，以帮助维持正常的情绪平衡。蛋白质的最佳来源包括肉类，鸡蛋和乳制品。</span>\n</p>\n</div><div class=\"_1T4GB1ESohOqTGNmwCAcuf \" style=\"max-width: 100%; overflow-x: visible; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51); margin-top: 22px;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; overflow-y: auto; max-width: 100%; line-height: 24px;\">\n<span class=\"bjh-ul\" style=\"max-width: 100%; font-size: 18px; list-style-type: disc; display: block; line-height: 27px; padding-left: 26px;\">\n<span class=\"bjh-li\" style=\"max-width: 100%; display: list-item; margin-bottom: 0px;\">\n<span class=\"bjh-p\" style=\"max-width: 100%;\">\n植物蛋白如大豆，豆类和种子也含有氨基酸。</span></span></span>\n</p>\n</div><div class=\"_1T4GB1ESohOqTGNmwCAcuf \" style=\"max-width: 100%; overflow-x: visible; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51); margin-top: 22px;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; overflow-y: auto; max-width: 100%; line-height: 24px;\">\n<span class=\"bjh-p\" style=\"max-width: 100%;\">\n<span class=\"bjh-br\" style=\"max-width: 100%;\">\n</span></span>\n</p>\n</div><div class=\"_4TUZRg-Yz-JYH2P3V5fsh \" style=\"max-width: 100%; overflow-x: visible; display: flex; margin-top: 36px;\">\n<div class=\"_1Dg_4ksYttUK-gyQE1RGR6\" style=\"display: flex; -webkit-box-orient: vertical; -webkit-box-direction: normal; flex-direction: column; -webkit-box-align: center; align-items: center; width: 599px;\">\n<img src=\"https://t10.baidu.com/it/u=1832886906,1291356392&amp;fm=173&amp;s=79C0ED171E5653DE9A107CED0300E020&amp;w=640&amp;h=452&amp;img.JPEG\" width=\"640\" class=\"_3yZQZ9OxCCD0QVw16rnEsS\" style=\"border: 0px; width: 599px; border-top-left-radius: 13px; border-top-right-radius: 13px; border-bottom-right-radius: 13px; border-bottom-left-radius: 13px;\">\n</div></div><div class=\"_1T4GB1ESohOqTGNmwCAcuf \" style=\"max-width: 100%; overflow-x: visible; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51); margin-top: 36px;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; overflow-y: auto; max-width: 100%; line-height: 24px;\">\n<span class=\"bjh-p\" style=\"max-width: 100%;\">\n<span class=\"bjh-strong\" style=\"max-width: 100%; font-size: 18px; font-weight: 700;\">\n避免人造甜味剂</span></span>\n</p>\n</div><div class=\"_1T4GB1ESohOqTGNmwCAcuf \" style=\"max-width: 100%; overflow-x: visible; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51); margin-top: 22px;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; overflow-y: auto; max-width: 100%; line-height: 24px;\">\n<span class=\"bjh-p\" style=\"max-width: 100%;\">\n人造甜味剂，如阿斯巴甜，会对您的抑郁症产生负面影响。研究表明，它会阻止血清素，这可能会导致你情绪低落，并使你的抑郁症加重。</span>\n</p>\n</div><div class=\"_1T4GB1ESohOqTGNmwCAcuf \" style=\"max-width: 100%; overflow-x: visible; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51); margin-top: 22px;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; overflow-y: auto; max-width: 100%; line-height: 24px;\">\n<span class=\"bjh-ul\" style=\"max-width: 100%; font-size: 18px; list-style-type: disc; display: block; line-height: 27px; padding-left: 26px;\">\n<span class=\"bjh-li\" style=\"max-width: 100%; display: list-item; margin-bottom: 0px;\">\n<span class=\"bjh-p\" style=\"max-width: 100%;\">\n虽然精制糖可以对你的抑郁和焦虑产生负面影响，但人造甜味剂更糟糕。如果你一定要吃甜的东西，避免含有阿斯巴甜的食物或饮料。用糖可以代替它，但数量要有限制，或尝试一些甜的水果，如浆果或桃子，以满足糖的需求。</span></span></span>\n</p>\n</div><div class=\"_1T4GB1ESohOqTGNmwCAcuf \" style=\"max-width: 100%; overflow-x: visible; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51); margin-top: 22px;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; overflow-y: auto; max-width: 100%; line-height: 24px;\">\n<span class=\"bjh-p\" style=\"max-width: 100%;\">\n<span class=\"bjh-br\" style=\"max-width: 100%;\">\n</span></span>\n</p>\n</div><div class=\"_4TUZRg-Yz-JYH2P3V5fsh \" style=\"max-width: 100%; overflow-x: visible; display: flex; margin-top: 36px;\">\n<div class=\"_1Dg_4ksYttUK-gyQE1RGR6\" style=\"display: flex; -webkit-box-orient: vertical; -webkit-box-direction: normal; flex-direction: column; -webkit-box-align: center; align-items: center; width: 599px;\">\n<img src=\"https://t11.baidu.com/it/u=23097451,3127581361&amp;fm=173&amp;s=B503F7170FB2F8C4108A15FF0300F038&amp;w=640&amp;h=455&amp;img.JPEG\" width=\"640\" class=\"_3yZQZ9OxCCD0QVw16rnEsS\" style=\"border: 0px; width: 599px; border-top-left-radius: 13px; border-top-right-radius: 13px; border-bottom-right-radius: 13px; border-bottom-left-radius: 13px;\">\n</div></div><div class=\"_1T4GB1ESohOqTGNmwCAcuf \" style=\"max-width: 100%; overflow-x: visible; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51); margin-top: 36px;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; overflow-y: auto; max-width: 100%; line-height: 24px;\">\n<span class=\"bjh-p\" style=\"max-width: 100%;\">\n<span class=\"bjh-strong\" style=\"max-width: 100%; font-size: 18px; font-weight: 700;\">\n减少氢化油和坏脂肪</span></span>\n</p>\n</div><div class=\"_1T4GB1ESohOqTGNmwCAcuf \" style=\"max-width: 100%; overflow-x: visible; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51); margin-top: 22px;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; overflow-y: auto; max-width: 100%; line-height: 24px;\">\n<span class=\"bjh-p\" style=\"max-width: 100%;\">\n煎炸油是油炸食品中不健康的反式脂肪，如炸薯条、奶酪棒、油炸肉和油炸蔬菜。反式脂肪会对你的抑郁产生负面影响。过多的饱和脂肪，如薯片、冰淇淋等垃圾食品会导致焦虑。</span>\n</p>\n</div><div class=\"_1T4GB1ESohOqTGNmwCAcuf \" style=\"max-width: 100%; overflow-x: visible; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51); margin-top: 22px;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; overflow-y: auto; max-width: 100%; line-height: 24px;\">\n<span class=\"bjh-ul\" style=\"max-width: 100%; font-size: 18px; list-style-type: disc; display: block; line-height: 27px; padding-left: 26px;\">\n<span class=\"bjh-li\" style=\"max-width: 100%; display: list-item; margin-bottom: 0px;\">\n<span class=\"bjh-p\" style=\"max-width: 100%;\">\n这些食物还会导致健康问题，如心脏病和肥胖症。</span></span></span>\n</p>\n</div><div class=\"_1T4GB1ESohOqTGNmwCAcuf \" style=\"max-width: 100%; overflow-x: visible; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51); margin-top: 22px;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; overflow-y: auto; max-width: 100%; line-height: 24px;\">\n<span class=\"bjh-p\" style=\"max-width: 100%;\">\n<span class=\"bjh-br\" style=\"max-width: 100%;\">\n</span></span>\n</p>\n</div><div class=\"_4TUZRg-Yz-JYH2P3V5fsh \" style=\"max-width: 100%; overflow-x: visible; display: flex; margin-top: 36px;\">\n<div class=\"_1Dg_4ksYttUK-gyQE1RGR6\" style=\"display: flex; -webkit-box-orient: vertical; -webkit-box-direction: normal; flex-direction: column; -webkit-box-align: center; align-items: center; width: 599px;\">\n<img src=\"https://t11.baidu.com/it/u=2874139693,3840296620&amp;fm=173&amp;s=C77B3CD67A774A98528DADF803000023&amp;w=640&amp;h=459&amp;img.JPEG\" width=\"640\" class=\"_3yZQZ9OxCCD0QVw16rnEsS\" style=\"border: 0px; width: 599px; border-top-left-radius: 13px; border-top-right-radius: 13px; border-bottom-right-radius: 13px; border-bottom-left-radius: 13px;\">\n</div></div><div class=\"_1T4GB1ESohOqTGNmwCAcuf \" style=\"max-width: 100%; overflow-x: visible; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51); margin-top: 36px;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; overflow-y: auto; max-width: 100%; line-height: 24px;\">\n<span class=\"bjh-p\" style=\"max-width: 100%;\">\n<span class=\"bjh-strong\" style=\"max-width: 100%; font-size: 18px; font-weight: 700;\">\n限制酒精</span></span>\n</p>\n</div><div class=\"_1T4GB1ESohOqTGNmwCAcuf \" style=\"max-width: 100%; overflow-x: visible; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51); margin-top: 22px;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; overflow-y: auto; max-width: 100%; line-height: 24px;\">\n<span class=\"bjh-p\" style=\"max-width: 100%;\">\n酒精是一种抑制剂，所以当你感到抑郁和焦虑时，你想避免它。因为酒精搅乱你的中枢神经系统和你的情绪，喝酒会使你的抑郁加重。酒精还会使焦虑症状加重。<span class=\"bjh-br\" style=\"max-width: 100%;\">\n</span></span>\n</p>\n</div><div class=\"_1T4GB1ESohOqTGNmwCAcuf \" style=\"max-width: 100%; overflow-x: visible; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51); margin-top: 22px;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; overflow-y: auto; max-width: 100%; line-height: 24px;\">\n<span class=\"bjh-p\" style=\"max-width: 100%;\">\n咖啡因<span class=\"bjh-br\" style=\"max-width: 100%;\">\n</span></span>\n</p>\n</div><div class=\"_1T4GB1ESohOqTGNmwCAcuf \" style=\"max-width: 100%; overflow-x: visible; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51); margin-top: 22px;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; overflow-y: auto; max-width: 100%; line-height: 24px;\">\n<span class=\"bjh-p\" style=\"max-width: 100%;\">\n咖啡因会引起焦虑，烦躁和紧张，这会对你的焦虑产生负面影响。咖啡因也会导致睡眠问题，这可能会使您的抑郁症恶化。</span>\n</p>\n</div><div class=\"_1T4GB1ESohOqTGNmwCAcuf \" style=\"max-width: 100%; overflow-x: visible; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51); margin-top: 22px;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; overflow-y: auto; max-width: 100%; line-height: 24px;\">\n<span class=\"bjh-ul\" style=\"max-width: 100%; font-size: 18px; list-style-type: disc; display: block; line-height: 27px; padding-left: 26px;\">\n<span class=\"bjh-li\" style=\"max-width: 100%; display: list-item; margin-bottom: 11px;\">\n<span class=\"bjh-p\" style=\"max-width: 100%;\">\n不要喝能量饮料。这些饮料充满咖啡因和糖，这两者都会对焦虑和抑郁产生负面影响。</span></span><span class=\"bjh-li\" style=\"max-width: 100%; display: list-item; margin-bottom: 0px;\">\n<span class=\"bjh-p\" style=\"max-width: 100%;\">\n如果你喝咖啡，限制你的摄入量。</span></span></span>\n</p>\n</div><div class=\"_1T4GB1ESohOqTGNmwCAcuf \" style=\"max-width: 100%; overflow-x: visible; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51); margin-top: 22px;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; overflow-y: auto; max-width: 100%; line-height: 24px;\">\n<span class=\"bjh-p\" style=\"max-width: 100%;\">\n<span class=\"bjh-br\" style=\"max-width: 100%;\">\n</span></span>\n</p>\n</div><div class=\"_4TUZRg-Yz-JYH2P3V5fsh \" style=\"max-width: 100%; overflow-x: visible; display: flex; margin-top: 36px;\">\n<div class=\"_1Dg_4ksYttUK-gyQE1RGR6\" style=\"display: flex; -webkit-box-orient: vertical; -webkit-box-direction: normal; flex-direction: column; -webkit-box-align: center; align-items: center; width: 599px;\">\n<img src=\"https://t11.baidu.com/it/u=228915334,4007731996&amp;fm=173&amp;s=0DAAE5174A7A728C4080C7E50300602B&amp;w=640&amp;h=434&amp;img.JPEG\" width=\"640\" class=\"_3yZQZ9OxCCD0QVw16rnEsS\" style=\"border: 0px; width: 599px; border-top-left-radius: 13px; border-top-right-radius: 13px; border-bottom-right-radius: 13px; border-bottom-left-radius: 13px;\">\n</div></div><div class=\"_1T4GB1ESohOqTGNmwCAcuf \" style=\"max-width: 100%; overflow-x: visible; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51); margin-top: 36px;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; overflow-y: auto; max-width: 100%; line-height: 24px;\">\n<span class=\"bjh-p\" style=\"max-width: 100%;\">\n<span class=\"bjh-strong\" style=\"max-width: 100%; font-size: 18px; font-weight: 700;\">\n增加你的ω-3脂肪酸脂肪</span></span>\n</p>\n</div><div class=\"_1T4GB1ESohOqTGNmwCAcuf \" style=\"max-width: 100%; overflow-x: visible; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51); margin-top: 22px;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; overflow-y: auto; max-width: 100%; line-height: 24px;\">\n<span class=\"bjh-p\" style=\"max-width: 100%;\">\n定期服用ω-3脂肪酸补充剂可以治疗抑郁症。这种脂肪酸可以在鱼类中补充，如鲑鱼，沙丁鱼，鳟鱼，新鲜金枪鱼，鲱鱼，鲭鱼和大比目鱼。其他食物如鸡蛋，牛奶，豆奶，酸奶，面包，麦片，面粉和花生酱也含有这种脂肪酸。坚果和种子也含有ω-3脂肪酸，如核桃，南瓜籽和亚麻籽。</span>\n</p>\n</div><div class=\"_1T4GB1ESohOqTGNmwCAcuf \" style=\"max-width: 100%; overflow-x: visible; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51); margin-top: 22px;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; overflow-y: auto; max-width: 100%; line-height: 24px;\">\n<span class=\"bjh-p\" style=\"max-width: 100%;\">\n<span class=\"bjh-br\" style=\"max-width: 100%;\">\n</span></span>\n</p>\n</div><div class=\"_4TUZRg-Yz-JYH2P3V5fsh \" style=\"max-width: 100%; overflow-x: visible; display: flex; margin-top: 36px;\">\n<div class=\"_1Dg_4ksYttUK-gyQE1RGR6\" style=\"display: flex; -webkit-box-orient: vertical; -webkit-box-direction: normal; flex-direction: column; -webkit-box-align: center; align-items: center; width: 599px;\">\n<img src=\"https://t10.baidu.com/it/u=838818603,2754693184&amp;fm=173&amp;s=63209D40178270FCD8D910DA030070B0&amp;w=640&amp;h=431&amp;img.JPEG\" width=\"640\" class=\"_3yZQZ9OxCCD0QVw16rnEsS\" style=\"border: 0px; width: 599px; border-top-left-radius: 13px; border-top-right-radius: 13px; border-bottom-right-radius: 13px; border-bottom-left-radius: 13px;\">\n</div></div><div class=\"_1T4GB1ESohOqTGNmwCAcuf \" style=\"max-width: 100%; overflow-x: visible; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51); margin-top: 36px;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; overflow-y: auto; max-width: 100%; line-height: 24px;\">\n<span class=\"bjh-p\" style=\"max-width: 100%;\">\n<span class=\"bjh-strong\" style=\"max-width: 100%; font-size: 18px; font-weight: 700;\">\n考虑补充你的饮食，以帮助抑郁症</span></span>\n</p>\n</div><div class=\"_1T4GB1ESohOqTGNmwCAcuf \" style=\"max-width: 100%; overflow-x: visible; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51); margin-top: 22px;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; overflow-y: auto; max-width: 100%; line-height: 24px;\">\n<span class=\"bjh-p\" style=\"max-width: 100%;\">\n一些补充剂已被证明可以改善日常抑郁症的症状。在服用补充剂之前，请确保您与医生核对。另外，请确保您遵循制造商的剂量说明。</span>\n</p>\n</div><div class=\"_1T4GB1ESohOqTGNmwCAcuf \" style=\"max-width: 100%; overflow-x: visible; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51); margin-top: 22px;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; overflow-y: auto; max-width: 100%; line-height: 24px;\">\n<span class=\"bjh-ul\" style=\"max-width: 100%; font-size: 18px; list-style-type: disc; display: block; line-height: 27px; padding-left: 26px;\">\n<span class=\"bjh-li\" style=\"max-width: 100%; display: list-item; margin-bottom: 11px;\">\n<span class=\"bjh-p\" style=\"max-width: 100%;\">\n维生素B12。已经证明每天补充0.4毫克维生素B12可以减轻抑郁症状。</span></span><span class=\"bjh-li\" style=\"max-width: 100%; display: list-item; margin-bottom: 11px;\">\n<span class=\"bjh-p\" style=\"max-width: 100%;\">\n叶酸。每天服用0.8毫克的叶酸也可减少抑郁症的症状。您可以服用含有该量的多种维生素，或单独服用。</span></span><span class=\"bjh-li\" style=\"max-width: 100%; display: list-item; margin-bottom: 0px;\">\n<span class=\"bjh-p\" style=\"max-width: 100%;\">\n镁。服用镁也被证明可以治疗抑郁症</span></span></span>\n</p>\n</div><div class=\"_1T4GB1ESohOqTGNmwCAcuf \" style=\"max-width: 100%; overflow-x: visible; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51); margin-top: 22px;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; overflow-y: auto; max-width: 100%; line-height: 24px;\">\n<span class=\"bjh-p\" style=\"max-width: 100%;\">\n<span class=\"bjh-strong\" style=\"max-width: 100%; font-size: 18px; font-weight: 700;\">\n二.改变你的饮食习惯</span></span>\n</p>\n</div><div class=\"_1T4GB1ESohOqTGNmwCAcuf \" style=\"max-width: 100%; overflow-x: visible; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51); margin-top: 22px;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; overflow-y: auto; max-width: 100%; line-height: 24px;\">\n<span class=\"bjh-p\" style=\"max-width: 100%;\">\n<span class=\"bjh-br\" style=\"max-width: 100%;\">\n</span></span>\n</p>\n</div><div class=\"_4TUZRg-Yz-JYH2P3V5fsh \" style=\"max-width: 100%; overflow-x: visible; display: flex; margin-top: 36px;\">\n<div class=\"_1Dg_4ksYttUK-gyQE1RGR6\" style=\"display: flex; -webkit-box-orient: vertical; -webkit-box-direction: normal; flex-direction: column; -webkit-box-align: center; align-items: center; width: 599px;\">\n<img src=\"https://t11.baidu.com/it/u=2084869483,2918827964&amp;fm=173&amp;s=3DFAE3150542FF4516FD4CCB0300B023&amp;w=640&amp;h=437&amp;img.JPEG\" width=\"640\" class=\"_3yZQZ9OxCCD0QVw16rnEsS\" style=\"border: 0px; width: 599px; border-top-left-radius: 13px; border-top-right-radius: 13px; border-bottom-right-radius: 13px; border-bottom-left-radius: 13px;\">\n</div></div><div class=\"_1T4GB1ESohOqTGNmwCAcuf \" style=\"max-width: 100%; overflow-x: visible; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51); margin-top: 36px;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; overflow-y: auto; max-width: 100%; line-height: 24px;\">\n<span class=\"bjh-p\" style=\"max-width: 100%;\">\n<span class=\"bjh-br\" style=\"max-width: 100%;\">\n</span></span>\n</p>\n</div><div class=\"_1T4GB1ESohOqTGNmwCAcuf \" style=\"max-width: 100%; overflow-x: visible; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51); margin-top: 22px;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; overflow-y: auto; max-width: 100%; line-height: 24px;\">\n<span class=\"bjh-p\" style=\"max-width: 100%;\">\n<span class=\"bjh-strong\" style=\"max-width: 100%; font-size: 18px; font-weight: 700;\">\n避免不吃饭</span><span class=\"bjh-br\" style=\"max-width: 100%;\">\n</span></span>\n</p>\n</div><div class=\"_1T4GB1ESohOqTGNmwCAcuf \" style=\"max-width: 100%; overflow-x: visible; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51); margin-top: 22px;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; overflow-y: auto; max-width: 100%; line-height: 24px;\">\n<span class=\"bjh-p\" style=\"max-width: 100%;\">\n抑郁症可能导致不想吃饭。你可能会失去食欲。不吃适量的食物会导致你烦躁或恶化你的情绪。它可以使你的抑郁症变得更糟。</span>\n</p>\n</div><div class=\"_1T4GB1ESohOqTGNmwCAcuf \" style=\"max-width: 100%; overflow-x: visible; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51); margin-top: 22px;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; overflow-y: auto; max-width: 100%; line-height: 24px;\">\n<span class=\"bjh-ul\" style=\"max-width: 100%; font-size: 18px; list-style-type: disc; display: block; line-height: 27px; padding-left: 26px;\">\n<span class=\"bjh-li\" style=\"max-width: 100%; display: list-item; margin-bottom: 0px;\">\n<span class=\"bjh-p\" style=\"max-width: 100%;\">\n即使你不想吃东西，也要尽量保持正常的饮食时间表。即使你少吃一些，也一定要吃。</span></span></span>\n</p>\n</div><div class=\"_1T4GB1ESohOqTGNmwCAcuf \" style=\"max-width: 100%; overflow-x: visible; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51); margin-top: 22px;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; overflow-y: auto; max-width: 100%; line-height: 24px;\">\n<span class=\"bjh-p\" style=\"max-width: 100%;\">\n<span class=\"bjh-br\" style=\"max-width: 100%;\">\n</span></span>\n</p>\n</div><div class=\"_4TUZRg-Yz-JYH2P3V5fsh \" style=\"max-width: 100%; overflow-x: visible; display: flex; margin-top: 36px;\">\n<div class=\"_1Dg_4ksYttUK-gyQE1RGR6\" style=\"display: flex; -webkit-box-orient: vertical; -webkit-box-direction: normal; flex-direction: column; -webkit-box-align: center; align-items: center; width: 599px;\">\n<img src=\"https://t11.baidu.com/it/u=2183453254,1325242248&amp;fm=173&amp;s=1133C031CF5357DCC6D974D70300D0A0&amp;w=640&amp;h=439&amp;img.JPEG\" width=\"640\" class=\"_3yZQZ9OxCCD0QVw16rnEsS\" style=\"border: 0px; width: 599px; border-top-left-radius: 13px; border-top-right-radius: 13px; border-bottom-right-radius: 13px; border-bottom-left-radius: 13px;\">\n</div></div><div class=\"_1T4GB1ESohOqTGNmwCAcuf \" style=\"max-width: 100%; overflow-x: visible; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51); margin-top: 36px;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; overflow-y: auto; max-width: 100%; line-height: 24px;\">\n<span class=\"bjh-p\" style=\"max-width: 100%;\">\n<span class=\"bjh-strong\" style=\"max-width: 100%; font-size: 18px; font-weight: 700;\">\n吃丰富的食物</span></span>\n</p>\n</div><div class=\"_1T4GB1ESohOqTGNmwCAcuf \" style=\"max-width: 100%; overflow-x: visible; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51); margin-top: 22px;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; overflow-y: auto; max-width: 100%; line-height: 24px;\">\n<span class=\"bjh-p\" style=\"max-width: 100%;\">\n当你情绪低落的时候，选择食物和做饭是很难的。</span>\n</p>\n</div><div class=\"_1T4GB1ESohOqTGNmwCAcuf \" style=\"max-width: 100%; overflow-x: visible; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51); margin-top: 22px;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; overflow-y: auto; max-width: 100%; line-height: 24px;\">\n<span class=\"bjh-p\" style=\"max-width: 100%;\">\n这意味着你可能每天都吃同样的食物，甚至每顿饭都吃同样的食物。这可能意味着你要吃冰箱里或橱柜里的东西，因为它们不能提供你身体所需要的营养。尽量吃均衡的饮食。不要试图精心制作饭菜。烹饪简单的食物，如烤鱼、鸡肉或清蒸蔬菜。</span>\n</p>\n</div><div class=\"_1T4GB1ESohOqTGNmwCAcuf \" style=\"max-width: 100%; overflow-x: visible; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51); margin-top: 22px;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; overflow-y: auto; max-width: 100%; line-height: 24px;\">\n<span class=\"bjh-ul\" style=\"max-width: 100%; font-size: 18px; list-style-type: disc; display: block; line-height: 27px; padding-left: 26px;\">\n<span class=\"bjh-li\" style=\"max-width: 100%; display: list-item; margin-bottom: 0px;\">\n<span class=\"bjh-p\" style=\"max-width: 100%;\">\n如果您需要，请向朋友或家人寻求帮助。他们可能会帮助你准备饭菜。</span></span></span>\n</p>\n</div></div></div></div>\n','生活风趣事',53,74,1,'2018-03-04','2021-11-20 10:39:43','2021-11-20 10:39:43'),(11,'「健康科普知识」抑郁症的症状',6,'https://zhuanjia.cdn.bcebos.com/zhuanjia/0b9f10f80e58f5ead25faa583.png','摘要','<div class=\"XvFFhe0M00eU66dhHzH47\" style=\"background-color: rgb(248, 248, 249); border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(242, 242, 242); font-family: arial; font-size: 12px;\">\n<div class=\"_2j52cNz2l1nejzCrafdFDM\" style=\"padding-bottom: 24px; width: 900px; margin: 0px auto;\">\n<div class=\"_2akiyyCcJt3GTUofn31jvy \" style=\"font-size: 40px; line-height: 58px; padding-top: 41px; padding-bottom: 21px; text-align: justify; font-weight: 700; font-family: &quot;Microsoft Yahei&quot;, 微软雅黑, 宋体;\">\n「健康科普知识」抑郁症的症状<div class=\"_2p8ac0wGmQwvO7JvBsm6Pn \" style=\"position: relative; display: inline-block; top: -6px; margin-left: 12px; width: 90px; height: 28px; padding-top: 6px; line-height: 1; box-sizing: border-box; padding-left: 24px; border-top-left-radius: 17.5px; border-top-right-radius: 17.5px; border-bottom-right-radius: 17.5px; border-bottom-left-radius: 17.5px; border: 1px solid rgba(0, 0, 0, 0.1); color: rgb(98, 102, 117); fill: rgb(98, 102, 117); font-size: 14px; font-weight: 400; -webkit-box-align: center; align-items: center; cursor: pointer;\">\n播报文章</div></div><div class=\"_7w5ONtCawo-feDOnBTED2\" style=\"margin-right: 250px; display: flex;\">\n<div class=\"XxtoRkG7yvq0XKZfamzhH\" style=\"position: relative; width: 47px; height: 47px; margin-right: 15px; border-top-left-radius: 50%; border-top-right-radius: 50%; border-bottom-right-radius: 50%; border-bottom-left-radius: 50%;\">\n<a href=\"https://author.baidu.com/home?from=bjh_article&amp;app_id=1595965604198075\" target=\"_blank\" style=\"text-decoration: none;\">\n<img src=\"https://user-center.cdn.bcebos.com/head/raw/uc.101.525b5192.1-BRmqNnQAaBiOC4ZNK19A?x-bce-process=image/resize,m_lfit,w_200,h_200&amp;autime=59916\" style=\"border: 0px; width: 47px; height: 47px; border-top-left-radius: 50%; border-top-right-radius: 50%; border-bottom-right-radius: 50%; border-bottom-left-radius: 50%;\">\n</a><i class=\"_36dpbkcN-xauJbnOkODju\" style=\"position: absolute; bottom: 0px; right: 0px; width: 15px; height: 15px; background-image: url(&quot;data:image/png;base64,&quot;); background-position: 50% center; background-repeat: no-repeat no-repeat;\">\n</i></div><div class=\"_2hS5V7S24qH6ZgwPlwS24R\" style=\"max-width: 492px; overflow: hidden;\">\n<a href=\"https://author.baidu.com/home?from=bjh_article&amp;app_id=1595965604198075\" target=\"_blank\" style=\"text-decoration: none; color: rgb(0, 0, 0);\">\n<p class=\"_2K64ifOOmB97zMt4Zbh5tO\" style=\"margin-top: 0px; margin-bottom: 5px; width: 313.734375px; font-size: 16px;\">\n陇原健康\n</p>\n</a><div class=\"_3FCL5rjl99gXUIj3vArRhW\" style=\"color: rgb(153, 153, 153); display: flex; -webkit-box-align: center; align-items: center;\">\n<span style=\"white-space: nowrap;\">\n发布时间: 20-10-12</span><span class=\"_2sNnKvMtJ8vRc88MvYIpT8\" style=\"margin: 0px 8px; white-space: nowrap;\">\n16:31</span><span class=\"_3MxZzPNuE_jM1w8fxcPDBh\" style=\"display: inline-block; height: 12px; width: 1px; background-color: rgb(224, 224, 224); margin-right: 8px; white-space: nowrap;\">\n</span><span class=\"ySuECg1udlHQ8BdVVdBSD\" style=\"white-space: nowrap; overflow: hidden; text-overflow: ellipsis;\">\n甘肃省卫生和计划生育委员会</span></div></div></div></div></div><div class=\"_2xiZo1dd2Za0bMpJ-pdB7B\" style=\"display: flex; -webkit-box-pack: justify; justify-content: space-between; width: 900px; margin: 0px auto; background-color: rgb(242, 242, 242); font-family: arial; font-size: 12px;\">\n<div class=\"_1vFr3RWSK0wz54V5vbzAW2\" style=\"width: 600px; position: relative; background-color: rgb(255, 255, 255); padding-right: 50px;\">\n<div class=\"_2OVtLCRVVVa5RwDyfhipoy \" style=\"padding-top: 40px; padding-bottom: 30px;\">\n<div class=\"_4TUZRg-Yz-JYH2P3V5fsh \" style=\"max-width: 100%; overflow-x: visible; display: flex; margin-top: 0px;\">\n<div class=\"_1Dg_4ksYttUK-gyQE1RGR6\" style=\"display: flex; -webkit-box-orient: vertical; -webkit-box-direction: normal; flex-direction: column; -webkit-box-align: center; align-items: center; width: 599px;\">\n<img src=\"https://pics0.baidu.com/feed/77094b36acaf2edd334bf66a7ff36aee3b0193e2.jpeg?token=7036bd1138c4fdb540a2385899cc82b1&amp;s=7EDF6E9E8CD86CC812F099FE0300D039\" width=\"640\" class=\"_3yZQZ9OxCCD0QVw16rnEsS\" style=\"border: 0px; width: 599px; border-top-left-radius: 13px; border-top-right-radius: 13px; border-bottom-right-radius: 13px; border-bottom-left-radius: 13px;\">\n</div></div><div class=\"_4TUZRg-Yz-JYH2P3V5fsh \" style=\"max-width: 100%; overflow-x: visible; display: flex; margin-top: 40px;\">\n<div class=\"_1Dg_4ksYttUK-gyQE1RGR6\" style=\"display: flex; -webkit-box-orient: vertical; -webkit-box-direction: normal; flex-direction: column; -webkit-box-align: center; align-items: center; width: 599px;\">\n<img src=\"http://tukuimg.bdstatic.com/scrop/321c92b73ac72031c18cb1e8092d0bbe.gif\" width=\"215\" class=\"WGim2_x9QY_MDLSyZ5g3O\" style=\"border: 0px; margin: 0px auto; border-top-left-radius: 13px; border-top-right-radius: 13px; border-bottom-right-radius: 13px; border-bottom-left-radius: 13px;\">\n</div></div><div class=\"_1T4GB1ESohOqTGNmwCAcuf \" style=\"max-width: 100%; overflow-x: visible; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51); margin-top: 36px;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; overflow-y: auto; max-width: 100%; line-height: 24px;\">\n抑郁症是以显著而持久的心境低落为主要临床表现，并伴有相应的思维和行为改变的一种精神障碍，是心境障碍的一种表现形式。它具有相应的诊断标准，包括症状学标准、病程标准、严重程度标准。\n</p>\n</div><div class=\"_4TUZRg-Yz-JYH2P3V5fsh \" style=\"max-width: 100%; overflow-x: visible; display: flex; margin-top: 36px;\">\n<div class=\"_1Dg_4ksYttUK-gyQE1RGR6\" style=\"display: flex; -webkit-box-orient: vertical; -webkit-box-direction: normal; flex-direction: column; -webkit-box-align: center; align-items: center; width: 599px;\">\n<img src=\"http://tukuimg.bdstatic.com/scrop/573cb997ac910d611f571d16278ddd94.gif\" width=\"231\" class=\"WGim2_x9QY_MDLSyZ5g3O\" style=\"border: 0px; margin: 0px auto; border-top-left-radius: 13px; border-top-right-radius: 13px; border-bottom-right-radius: 13px; border-bottom-left-radius: 13px;\">\n</div></div><div class=\"_4TUZRg-Yz-JYH2P3V5fsh \" style=\"max-width: 100%; overflow-x: visible; display: flex; margin-top: 40px;\">\n<div class=\"_1Dg_4ksYttUK-gyQE1RGR6\" style=\"display: flex; -webkit-box-orient: vertical; -webkit-box-direction: normal; flex-direction: column; -webkit-box-align: center; align-items: center; width: 599px;\">\n<img src=\"https://pics1.baidu.com/feed/91ef76c6a7efce1b8357f18a57b298d9b58f6558.jpeg?token=4e5c929b90dc150090e39aec9fc8fa97\" width=\"400\" class=\"WGim2_x9QY_MDLSyZ5g3O\" style=\"border: 0px; margin: 0px auto; border-top-left-radius: 13px; border-top-right-radius: 13px; border-bottom-right-radius: 13px; border-bottom-left-radius: 13px;\">\n</div></div><div class=\"_1T4GB1ESohOqTGNmwCAcuf \" style=\"max-width: 100%; overflow-x: visible; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51); margin-top: 36px;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; overflow-y: auto; max-width: 100%; line-height: 24px;\">\n抑郁症都有哪些症状？\n</p>\n</div><div class=\"_4TUZRg-Yz-JYH2P3V5fsh \" style=\"max-width: 100%; overflow-x: visible; display: flex; margin-top: 36px;\">\n<div class=\"_1Dg_4ksYttUK-gyQE1RGR6\" style=\"display: flex; -webkit-box-orient: vertical; -webkit-box-direction: normal; flex-direction: column; -webkit-box-align: center; align-items: center; width: 599px;\">\n<img src=\"https://pics7.baidu.com/feed/bd315c6034a85edfa7140ebd0ae56224df5475c8.png?token=73bf886076bf901b79175c677e9c1bf3\" width=\"49\" class=\"WGim2_x9QY_MDLSyZ5g3O\" style=\"border: 0px; margin: 0px auto; border-top-left-radius: 13px; border-top-right-radius: 13px; border-bottom-right-radius: 13px; border-bottom-left-radius: 13px;\">\n</div></div><div class=\"_1T4GB1ESohOqTGNmwCAcuf \" style=\"max-width: 100%; overflow-x: visible; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51); margin-top: 36px;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; overflow-y: auto; max-width: 100%; line-height: 24px;\">\n症状大体可分为核心症状、心理症状与生物学症状三个方面。\n</p>\n</div><div class=\"_1T4GB1ESohOqTGNmwCAcuf \" style=\"max-width: 100%; overflow-x: visible; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51); margin-top: 22px;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; overflow-y: auto; max-width: 100%; line-height: 24px;\">\n核心症状包括情绪低落、兴趣减退或愉快感丧失、精力下降；\n</p>\n</div><div class=\"_1T4GB1ESohOqTGNmwCAcuf \" style=\"max-width: 100%; overflow-x: visible; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51); margin-top: 22px;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; overflow-y: auto; max-width: 100%; line-height: 24px;\">\n心理症状包括思维迟缓、自我评价下降、自责、自罪、自杀等；\n</p>\n</div><div class=\"_1T4GB1ESohOqTGNmwCAcuf \" style=\"max-width: 100%; overflow-x: visible; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51); margin-top: 22px;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; overflow-y: auto; max-width: 100%; line-height: 24px;\">\n生物学症状包括睡眠障碍、食欲不振等。\n</p>\n</div><div class=\"_1T4GB1ESohOqTGNmwCAcuf \" style=\"max-width: 100%; overflow-x: visible; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51); margin-top: 22px;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; overflow-y: auto; max-width: 100%; line-height: 24px;\">\n如果符合了症状学标准，同时上述症状持续存在2周以上，对工作、学习和生活有肯定的影响，就高度怀疑是否患有抑郁症。当然，需要专科医生经过系统评估后方可做出诊断。\n</p>\n</div><div class=\"_4TUZRg-Yz-JYH2P3V5fsh \" style=\"max-width: 100%; overflow-x: visible; display: flex; margin-top: 36px;\">\n<div class=\"_1Dg_4ksYttUK-gyQE1RGR6\" style=\"display: flex; -webkit-box-orient: vertical; -webkit-box-direction: normal; flex-direction: column; -webkit-box-align: center; align-items: center; width: 599px;\">\n<img src=\"https://pics5.baidu.com/feed/8435e5dde71190ef915ce17a3cf8f611fffa60df.png?token=019dad0550220d6af87e0c5413d81621\" width=\"29\" class=\"WGim2_x9QY_MDLSyZ5g3O\" style=\"border: 0px; margin: 0px auto; border-top-left-radius: 13px; border-top-right-radius: 13px; border-bottom-right-radius: 13px; border-bottom-left-radius: 13px;\">\n</div></div><div class=\"_4TUZRg-Yz-JYH2P3V5fsh \" style=\"max-width: 100%; overflow-x: visible; display: flex; margin-top: 40px;\">\n<div class=\"_1Dg_4ksYttUK-gyQE1RGR6\" style=\"display: flex; -webkit-box-orient: vertical; -webkit-box-direction: normal; flex-direction: column; -webkit-box-align: center; align-items: center; width: 599px;\">\n<img src=\"https://pics2.baidu.com/feed/a08b87d6277f9e2fb00bd5c9e7d38223b999f325.jpeg?token=f292297fec23164abb6379fa52485b4c\" width=\"497\" class=\"WGim2_x9QY_MDLSyZ5g3O\" style=\"border: 0px; margin: 0px auto; border-top-left-radius: 13px; border-top-right-radius: 13px; border-bottom-right-radius: 13px; border-bottom-left-radius: 13px;\">\n</div></div><div class=\"_1T4GB1ESohOqTGNmwCAcuf \" style=\"max-width: 100%; overflow-x: visible; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51); margin-top: 36px;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; overflow-y: auto; max-width: 100%; line-height: 24px;\">\n抑郁症是遗传性疾病吗？\n</p>\n</div><div class=\"_4TUZRg-Yz-JYH2P3V5fsh \" style=\"max-width: 100%; overflow-x: visible; display: flex; margin-top: 36px;\">\n<div class=\"_1Dg_4ksYttUK-gyQE1RGR6\" style=\"display: flex; -webkit-box-orient: vertical; -webkit-box-direction: normal; flex-direction: column; -webkit-box-align: center; align-items: center; width: 599px;\">\n<img src=\"https://pics7.baidu.com/feed/bd315c6034a85edfa7140ebd0ae56224df5475c8.png?token=73bf886076bf901b79175c677e9c1bf3\" width=\"49\" class=\"WGim2_x9QY_MDLSyZ5g3O\" style=\"border: 0px; margin: 0px auto; border-top-left-radius: 13px; border-top-right-radius: 13px; border-bottom-right-radius: 13px; border-bottom-left-radius: 13px;\">\n</div></div><div class=\"_1T4GB1ESohOqTGNmwCAcuf \" style=\"max-width: 100%; overflow-x: visible; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51); margin-top: 36px;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; overflow-y: auto; max-width: 100%; line-height: 24px;\">\n抑郁症会不会通过基因遗传，家系调查发现，抑郁症患者的亲属患本病的概率比一般人群高10～30倍，而且血缘关系越近发病的几率越高。如果父亲或母亲中有1人罹患抑郁症，他们的子女罹患抑郁症的几率约为20%。成长于不同环境中的同卵双胞胎，两人均罹患重度抑郁症的几率近7成。这仅能说明抑郁症有一定的“遗传倾向性”，它与遗传性疾病还是有本质的区别，会不会罹患抑郁症并不是完全由基因决定，抑郁症的致病因素还包括其他<span class=\"bjh-strong\" style=\"max-width: 100%; font-size: 18px; font-weight: 700;\">\n社会、心理因素</span>。对轻度抑郁症患者来说，社会、心理因素对发病的影响远比遗传因素大得多。\n</p>\n</div><div class=\"_1T4GB1ESohOqTGNmwCAcuf \" style=\"max-width: 100%; overflow-x: visible; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51); margin-top: 22px;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; overflow-y: auto; max-width: 100%; line-height: 24px;\">\n<span class=\"bjh-p\" style=\"max-width: 100%;\">\n本文撰稿：郭霞 甘肃中医药大学附属医院精神心理科 精神科副主任医师</span>\n</p>\n</div><div class=\"_1T4GB1ESohOqTGNmwCAcuf \" style=\"max-width: 100%; overflow-x: visible; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51); margin-top: 22px;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; overflow-y: auto; max-width: 100%; line-height: 24px;\">\n<span class=\"bjh-p bjh-text-align-left\" style=\"max-width: 100%;\">\n来源：甘肃中医药大学附属医院</span>\n</p>\n</div><div class=\"_1T4GB1ESohOqTGNmwCAcuf \" style=\"max-width: 100%; overflow-x: visible; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51); margin-top: 22px;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; overflow-y: auto; max-width: 100%; line-height: 24px;\">\n<span class=\"bjh-p bjh-text-align-left\" style=\"max-width: 100%;\">\n制作：甘肃省卫生健康宣传教育中心</span>\n</p>\n</div></div></div></div>','陇原健康',63,32,1,'2020-10-12','2021-11-20 10:39:43','2021-12-09 11:15:12'),(12,'日常要如何预防抑郁症？麻烦记住这4点，积极预防',6,'https://img1.baidu.com/it/u=3271253027,2889237981&fm=26&fmt=auto','摘要','<div class=\"XvFFhe0M00eU66dhHzH47\" style=\"background-color: rgb(248, 248, 249); border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(242, 242, 242); font-family: arial; font-size: 12px;\">\n<div class=\"_2j52cNz2l1nejzCrafdFDM\" style=\"padding-bottom: 24px; width: 900px; margin: 0px auto;\">\n<div class=\"_2akiyyCcJt3GTUofn31jvy \" style=\"font-size: 40px; line-height: 58px; padding-top: 41px; padding-bottom: 21px; text-align: justify; font-weight: 700; font-family: &quot;Microsoft Yahei&quot;, 微软雅黑, 宋体;\">\n日常要如何预防抑郁症？麻烦记住这4点，积极预防<div class=\"_2p8ac0wGmQwvO7JvBsm6Pn \" style=\"position: relative; display: inline-block; top: -6px; margin-left: 12px; width: 90px; height: 28px; padding-top: 6px; line-height: 1; box-sizing: border-box; padding-left: 24px; border-top-left-radius: 17.5px; border-top-right-radius: 17.5px; border-bottom-right-radius: 17.5px; border-bottom-left-radius: 17.5px; border: 1px solid rgba(0, 0, 0, 0.1); color: rgb(98, 102, 117); fill: rgb(98, 102, 117); font-size: 14px; font-weight: 400; -webkit-box-align: center; align-items: center; cursor: pointer;\">\n播报文章</div></div><div class=\"_7w5ONtCawo-feDOnBTED2\" style=\"margin-right: 250px; display: flex;\">\n<div class=\"XxtoRkG7yvq0XKZfamzhH\" style=\"position: relative; width: 47px; height: 47px; margin-right: 15px; border-top-left-radius: 50%; border-top-right-radius: 50%; border-bottom-right-radius: 50%; border-bottom-left-radius: 50%;\">\n<a href=\"https://author.baidu.com/home?from=bjh_article&amp;app_id=1598612982071707\" target=\"_blank\" style=\"text-decoration: none;\">\n<img src=\"https://user-center.cdn.bcebos.com/head/raw/uc.101.d673c2b5.CuTpCvPbg9FIxoqiEqBFmw?x-bce-process=image/resize,m_lfit,w_200,h_200&amp;autime=32166\" style=\"border: 0px; width: 47px; height: 47px; border-top-left-radius: 50%; border-top-right-radius: 50%; border-bottom-right-radius: 50%; border-bottom-left-radius: 50%;\">\n</a><i class=\"_36dpbkcN-xauJbnOkODju\" style=\"position: absolute; bottom: 0px; right: 0px; width: 15px; height: 15px; background-image: url(&quot;data:image/png;base64,&quot;); background-position: 50% center; background-repeat: no-repeat no-repeat;\">\n</i></div><div class=\"_2hS5V7S24qH6ZgwPlwS24R\" style=\"max-width: 492px; overflow: hidden;\">\n<a href=\"https://author.baidu.com/home?from=bjh_article&amp;app_id=1598612982071707\" target=\"_blank\" style=\"text-decoration: none; color: rgb(0, 0, 0);\">\n<p class=\"_2K64ifOOmB97zMt4Zbh5tO\" style=\"margin-top: 0px; margin-bottom: 5px; width: 265.734375px; font-size: 16px;\">\n医联媒体\n</p>\n</a><div class=\"_3FCL5rjl99gXUIj3vArRhW\" style=\"color: rgb(153, 153, 153); display: flex; -webkit-box-align: center; align-items: center;\">\n<span style=\"white-space: nowrap;\">\n发布时间: 19-08-29</span><span class=\"_2sNnKvMtJ8vRc88MvYIpT8\" style=\"margin: 0px 8px; white-space: nowrap;\">\n21:40</span><span class=\"_3MxZzPNuE_jM1w8fxcPDBh\" style=\"display: inline-block; height: 12px; width: 1px; background-color: rgb(224, 224, 224); margin-right: 8px; white-space: nowrap;\">\n</span><span class=\"ySuECg1udlHQ8BdVVdBSD\" style=\"white-space: nowrap; overflow: hidden; text-overflow: ellipsis;\">\n医联媒体官方百家号</span></div></div></div></div></div><div class=\"_2xiZo1dd2Za0bMpJ-pdB7B\" style=\"display: flex; -webkit-box-pack: justify; justify-content: space-between; width: 900px; margin: 0px auto; background-color: rgb(242, 242, 242); font-family: arial; font-size: 12px;\">\n<div class=\"_1vFr3RWSK0wz54V5vbzAW2\" style=\"width: 600px; position: relative; background-color: rgb(255, 255, 255); padding-right: 50px;\">\n<div class=\"_2OVtLCRVVVa5RwDyfhipoy \" style=\"padding-top: 40px; padding-bottom: 30px;\">\n<div class=\"_1T4GB1ESohOqTGNmwCAcuf \" style=\"max-width: 100%; overflow-x: visible; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51);\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; overflow-y: auto; max-width: 100%; line-height: 24px;\">\n<span class=\"bjh-p\" style=\"max-width: 100%;\">\n抑郁症是临床上十分常见的疾病，很多人因为工作压力过大，情感受挫等出现了抑郁症。抑郁症在发展过程中，各种不良症状也会频繁出现，像常见的闷闷不乐，悲观厌世等和抑郁症有关。因此，发现抑郁症之后，需要马上采取治疗措施，防止抑郁症状加重对健康造成威胁。另外，平时需要通过合理的方式来预防抑郁症，这样才能防止抑郁情绪长期存在。那么，生活中如何预防抑郁症呢？</span>\n</p>\n</div><div class=\"_4TUZRg-Yz-JYH2P3V5fsh \" style=\"max-width: 100%; overflow-x: visible; display: flex; margin-top: 36px;\">\n<div class=\"_1Dg_4ksYttUK-gyQE1RGR6\" style=\"display: flex; -webkit-box-orient: vertical; -webkit-box-direction: normal; flex-direction: column; -webkit-box-align: center; align-items: center; width: 599px;\">\n<img src=\"https://pics5.baidu.com/feed/8326cffc1e178a82862eec79374d2f88a877e8a9.jpeg?token=b9902bca815f94adac3ad706d6096632&amp;s=AD9A04DB4E9228594C554912030060C3\" width=\"640\" class=\"_3yZQZ9OxCCD0QVw16rnEsS\" style=\"border: 0px; width: 599px; border-top-left-radius: 13px; border-top-right-radius: 13px; border-bottom-right-radius: 13px; border-bottom-left-radius: 13px;\">\n</div></div><div class=\"_1T4GB1ESohOqTGNmwCAcuf \" style=\"max-width: 100%; overflow-x: visible; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51); margin-top: 36px;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; overflow-y: auto; max-width: 100%; line-height: 24px;\">\n<span class=\"bjh-p\" style=\"max-width: 100%;\">\n<span class=\"bjh-strong\" style=\"max-width: 100%; font-size: 18px; font-weight: 700;\">\n1、及时释放压力</span></span>\n</p>\n</div><div class=\"_1T4GB1ESohOqTGNmwCAcuf \" style=\"max-width: 100%; overflow-x: visible; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51); margin-top: 22px;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; overflow-y: auto; max-width: 100%; line-height: 24px;\">\n<span class=\"bjh-p\" style=\"max-width: 100%;\">\n平时生活中想要有效预防抑郁症，需要及时释放压力，部分人出现抑郁症和压力过大有关，平时存在压力之后没有及时释放，可能就会导致抑郁症的出现。因此，发现抑郁症之后，应该及时释放压力，缓解不良情绪。另外，平时工作或者家庭的一些压力需要及时释放，才能防止压力过大而导致不良情绪，逐渐演变成严重的抑郁症。</span>\n</p>\n</div><div class=\"_1T4GB1ESohOqTGNmwCAcuf \" style=\"max-width: 100%; overflow-x: visible; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51); margin-top: 22px;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; overflow-y: auto; max-width: 100%; line-height: 24px;\">\n<span class=\"bjh-p\" style=\"max-width: 100%;\">\n<span class=\"bjh-strong\" style=\"max-width: 100%; font-size: 18px; font-weight: 700;\">\n2、多运动锻炼</span></span>\n</p>\n</div><div class=\"_1T4GB1ESohOqTGNmwCAcuf \" style=\"max-width: 100%; overflow-x: visible; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51); margin-top: 22px;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; overflow-y: auto; max-width: 100%; line-height: 24px;\">\n<span class=\"bjh-p\" style=\"max-width: 100%;\">\n想要有效预防抑郁症，平时就需要多运动。运动是促进健康的一种方式，对很多疾病的预防均有帮助。很多人平时总是缺乏运动，长时间下去很有可能会导致抵抗力下降，甚至出现内分泌平衡紊乱的情况。如果可以适当进行运动，通过运动的方式调节身体，促进体内多巴胺的分泌，通常可以愉悦身心，让身体保持健康状态，这样也对抑郁症的预防有帮助。</span>\n</p>\n</div><div class=\"_4TUZRg-Yz-JYH2P3V5fsh \" style=\"max-width: 100%; overflow-x: visible; display: flex; margin-top: 36px;\">\n<div class=\"_1Dg_4ksYttUK-gyQE1RGR6\" style=\"display: flex; -webkit-box-orient: vertical; -webkit-box-direction: normal; flex-direction: column; -webkit-box-align: center; align-items: center; width: 599px;\">\n<img src=\"https://pics5.baidu.com/feed/d0c8a786c9177f3ee661d785b18167c29e3d56b0.jpeg?token=5fa9ffe35ab6ff9ac7ebd3ee6b6c15b3&amp;s=BD031B7480FA67969E8C38C70300A0E1\" width=\"640\" class=\"_3yZQZ9OxCCD0QVw16rnEsS\" style=\"border: 0px; width: 599px; border-top-left-radius: 13px; border-top-right-radius: 13px; border-bottom-right-radius: 13px; border-bottom-left-radius: 13px;\">\n</div></div><div class=\"_1T4GB1ESohOqTGNmwCAcuf \" style=\"max-width: 100%; overflow-x: visible; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51); margin-top: 36px;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; overflow-y: auto; max-width: 100%; line-height: 24px;\">\n<span class=\"bjh-p\" style=\"max-width: 100%;\">\n<span class=\"bjh-strong\" style=\"max-width: 100%; font-size: 18px; font-weight: 700;\">\n3、充足睡眠</span></span>\n</p>\n</div><div class=\"_1T4GB1ESohOqTGNmwCAcuf \" style=\"max-width: 100%; overflow-x: visible; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51); margin-top: 22px;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; overflow-y: auto; max-width: 100%; line-height: 24px;\">\n<span class=\"bjh-p\" style=\"max-width: 100%;\">\n想要有效预防抑郁症，平时应该保证充足的睡眠。充足的睡眠时间对抑郁症的预防有利，很多人工作压力过大，长时间睡眠不足可能会导致精神崩溃，而在精神脆弱的时候，很容易产生不良情绪，这样可能就会引发抑郁症。</span>\n</p>\n</div><div class=\"_1T4GB1ESohOqTGNmwCAcuf \" style=\"max-width: 100%; overflow-x: visible; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51); margin-top: 22px;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; overflow-y: auto; max-width: 100%; line-height: 24px;\">\n<span class=\"bjh-p\" style=\"max-width: 100%;\">\n因此，平时应该保证有充足的睡眠时间，通过合理的睡眠来调养身体，促进身体健康，这样才能防止抑郁情绪的出现对健康造成威胁。如果可以每天做到早睡早起，有充足的睡眠调养身体，通常可以维持内部环境稳定状态，防止抑郁症出现。</span>\n</p>\n</div><div class=\"_4TUZRg-Yz-JYH2P3V5fsh \" style=\"max-width: 100%; overflow-x: visible; display: flex; margin-top: 36px;\">\n<div class=\"_1Dg_4ksYttUK-gyQE1RGR6\" style=\"display: flex; -webkit-box-orient: vertical; -webkit-box-direction: normal; flex-direction: column; -webkit-box-align: center; align-items: center; width: 599px;\">\n<img src=\"https://pics4.baidu.com/feed/faedab64034f78f085c3c888b87f5650b2191c50.jpeg?token=0f4fc7e7f83c6d94478a01ae912fcbe0&amp;s=4DDDA8445983AF7C08D1D8800300E088\" width=\"640\" class=\"_3yZQZ9OxCCD0QVw16rnEsS\" style=\"border: 0px; width: 599px; border-top-left-radius: 13px; border-top-right-radius: 13px; border-bottom-right-radius: 13px; border-bottom-left-radius: 13px;\">\n</div></div><div class=\"_1T4GB1ESohOqTGNmwCAcuf \" style=\"max-width: 100%; overflow-x: visible; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51); margin-top: 36px;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; overflow-y: auto; max-width: 100%; line-height: 24px;\">\n<span class=\"bjh-p\" style=\"max-width: 100%;\">\n<span class=\"bjh-strong\" style=\"max-width: 100%; font-size: 18px; font-weight: 700;\">\n4、做自己感兴趣的事情</span></span>\n</p>\n</div><div class=\"_1T4GB1ESohOqTGNmwCAcuf \" style=\"max-width: 100%; overflow-x: visible; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51); margin-top: 22px;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; overflow-y: auto; max-width: 100%; line-height: 24px;\">\n<span class=\"bjh-p\" style=\"max-width: 100%;\">\n想要预防抑郁症，平时就要多做自己感兴趣的事情。因为在做自己感兴趣的事情时，通常会提起兴趣，而且比较专注，在做这些事情的时候，心情会保持舒畅，而心情舒畅是预防疾病的有效措施，可以防止不良情绪的出现引发抑郁症。因此，想要避免抑郁症出现的人，平时就需要多做自己感兴趣的事情。</span>\n</p>\n</div></div></div></div>','医联媒体',64,86,1,'2019-08-29','2021-11-20 10:39:43','2021-12-09 11:21:08'),(13,'亲密关系中的高依恋回避',1,'https://jdxl-img.jdxlt.com/post/4fcde6b0feb34d9cb9677df95f528c6c.jpeg','摘要','<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box; text-align: center;\">\n<em><span style=\"color: rgb(192, 57, 43);\">\n一封苦恼者的来信</span></em>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<em><span style=\"font-size: 14px;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n我有一个让人摸不着头脑的男友……<br><br>他常常在我想更接近他的时候选择逃避，比如邀他回家玩，他不想；想带我的朋友见他，他会说：“那是你的朋友，我又不熟。”</span></span></em>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<em><span style=\"font-size: 14px;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n<span style=\"font-weight: bolder;\">\n他认为谈恋爱也要有足够多的个人空间，</span>不要介入彼此的生活和朋友圈里，为此我感到很为难。</span></span></em>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<em><span style=\"font-size: 14px;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n但是，有时候他对我也挺好，尤其是工作上，他什么事都肯帮我，可是<span style=\"font-weight: bolder;\">\n当我很用心地说感谢他，试图跟他亲近时，他马上就会变得很奇怪，然后开始转移话题，</span>搞得不欢而散！</span></span></em>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<em><span style=\"font-size: 14px;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n“既然你那么喜欢独立，又为什么要和我谈恋爱？”我问他。</span></span></em>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<em><span style=\"font-size: 14px;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n“我觉得我们倆还蛮合适的啊，我挺照顾你的，不是吗？”我认为他这是答非所问。</span></span></em>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<em><span style=\"font-size: 14px;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n请问我该怎么办？</span></span></em>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<section class=\"Powered-by-XIUMI V5\" powered-by=\"xiumi.us\" style=\"box-sizing: border-box;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px;\">\n&nbsp;\n</p>\n</section><section class=\"Powered-by-XIUMI V5\" powered-by=\"xiumi.us\" style=\"box-sizing: border-box;\">\n<section style=\"box-sizing: border-box;\">\n<section style=\"letter-spacing: 1px; padding: 0px 5px; box-sizing: border-box;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n看完这位苦恼者的叙述，很多读者脑海里会出现一个熟悉的身影，因为可能你身边就有这样一个人……</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n这个人独立而高冷，若即若离，自我感觉良好，却常常让我们感到苦恼。这其实是典型的高<span style=\"box-sizing: border-box;\">\n<span style=\"font-weight: bolder;\">\n依恋回避</span></span></span><span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n<span style=\"box-sizing: border-box;\">\n<span style=\"font-weight: bolder;\">\n（attachment avoidance）</span></span></span><span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n的体现。</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(193, 88, 85);\">\n<span style=\"font-weight: bolder;\">\n高依恋回避的人通常在</span></span><span style=\"box-sizing: border-box; color: rgb(193, 88, 85);\">\n<span style=\"font-weight: bolder;\">\n面对亲密关系时感到很不舒服</span><span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n（Brennan, Clark, &amp; Shaver, 1998)</span><span style=\"font-weight: bolder;\">\n，他们不愿意依赖他人，也不愿意表露感情。</span></span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n这通常与他们成长过程中受到的情感忽视有关，</span><span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n每个人天生都是需要被爱和关心的，如果在成长过程中经常被忽视，在需要的时候得不到关爱，人们就</span><span style=\"font-weight: bolder;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n会逐渐形成“我不需要别人”的信念，以此当作自我保护，因为他们害怕表现出需要时会再次受到忽视和伤害。</span></span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n慢慢地，这种回避的模式成为了他们人际关系中的常态，并且他们通常认为自己不需要改变，而他们身边的人却会为此苦恼。</span>\n</p>\n</section></section></section><section class=\"Powered-by-XIUMI V5\" powered-by=\"xiumi.us\" style=\"box-sizing: border-box;\">\n<section style=\"box-sizing: border-box;\">\n<section style=\"box-sizing: border-box;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n</section></section></section><section class=\"Powered-by-XIUMI V5\" powered-by=\"xiumi.us\" style=\"box-sizing: border-box;\">\n<section style=\"box-sizing: border-box;\">\n<section style=\"letter-spacing: 1px; padding: 0px 5px; box-sizing: border-box;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n回顾刚刚那封苦恼者的来信，总结起来高依恋回避的人最核心的问题是：</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"color: rgb(193, 88, 85);\">\n<span style=\"font-weight: bolder;\">\n<span style=\"box-sizing: border-box;\">\n不信任、不在乎、“高”自尊。</span></span></span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; text-align: center;\">\n<img data-ratio=\"0.4444444444444444\" data-s=\"300,640\" data-type=\"png\" data-w=\"900\" src=\"https://jdxl-img.jdxlt.com/post/b1c43837eddb42978d075ba56f2ddc05.png\" style=\"border-style: none; vertical-align: middle; max-width: 100%;\">\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"font-weight: bolder;\">\n<span style=\"color: rgb(193, 88, 85);\">\n回避者的不信任</span></span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"font-weight: bolder;\">\n<span style=\"color: rgb(193, 88, 85);\">\n信赖他人代表可能再次受伤</span></span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; text-align: center; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n高依恋回避的人通常不信任他人，因为过去的经验告诉他们：<span style=\"font-weight: bolder;\">\n别人不可能给我情感的满足，我只能靠自己。</span></span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"color: rgb(68, 68, 68);\">\n所以他们在确定关系的时候会犹豫不决，害怕在感情中给予承诺。</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"color: rgb(68, 68, 68);\">\n面对恋爱，他们会说：“再说吧，不想那么快确定关系。”</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"color: rgb(68, 68, 68);\">\n面对婚姻，他们认为：“现在这样不是挺好的吗？我可不想那么快结婚。”</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n他们为自己保留了足够多的个人空间，并会在伴侣提出“想更多了解彼此”的需要时表现出慌乱不安，或者在伴侣表现出“粘人”时选择躲避。</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"font-weight: bolder; color: rgb(68, 68, 68); font-size: 15px;\">\n因为他们害怕与伴侣的亲密感会使他们再次对他人产生依赖，进而再次受伤。</span><span style=\"font-weight: bolder;\">\n<span style=\"color: rgb(68, 68, 68);\">\n他们的回避是出于不信任，而不代表他们真的不需要爱。</span></span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; text-align: center;\">\n<img data-ratio=\"0.014619883040935672\" data-s=\"300,640\" data-type=\"jpeg\" data-w=\"684\" src=\"https://jdxl-img.jdxlt.com/post/98c4c1feed9149bfa7d880a68079e7a9.jpeg\" style=\"border-style: none; vertical-align: middle; max-width: 100%;\">\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"font-weight: bolder;\">\n<span style=\"box-sizing: border-box; color: rgb(193, 88, 85);\">\n回避者的不在乎</span></span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"font-weight: bolder;\">\n<span style=\"box-sizing: border-box; color: rgb(193, 88, 85);\">\n就算很在意，也要装作不屑一顾</span></span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n他们还会表现出一副对什么都不在乎的样子，伤心难过时也不会轻易表露内心，因为他们认为<span style=\"font-weight: bolder;\">\n需要让自己显得强大而冷漠，才可以免受情感的伤害。</span>因此也常被认为是“花花公子”或“浪子”。</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n但是，有研究发现其实<span style=\"font-weight: bolder;\">\n高依恋回避的人内心深处对伴侣的忠诚非常看重，对伴侣的背叛行为会有激烈的情绪反应</span>(Feeney, 2004)<span style=\"font-weight: bolder;\">\n。</span></span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n他们虽然表面不在意，但内心敏感。如果伴侣撒谎、隐瞒，或者有背叛的行为，他们会感觉到自尊心很受打击，觉得自己再次被忽视。</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"font-weight: bolder;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n他们并不是真的不在乎，而是怕自己的在乎得不到情感回报，因此努力使自己显得不在乎。</span></span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; text-align: center;\">\n<img data-ratio=\"0.014619883040935672\" data-s=\"300,640\" data-type=\"jpeg\" data-w=\"684\" src=\"https://jdxl-img.jdxlt.com/post/59f5e6dcfb65487dbe5923311c462a28.jpeg\" style=\"border-style: none; vertical-align: middle; max-width: 100%;\">\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"font-weight: bolder;\">\n<span style=\"box-sizing: border-box; color: rgb(193, 88, 85);\">\n回避者的“高”自尊</span></span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"color: rgb(193, 88, 85);\">\n<span style=\"font-weight: bolder;\">\n我需要（看起来）很独立和强大</span></span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n相比于从社交中获得自我肯定，他们更倾向于把心思放在工作上，让自己在事业上尽可能出色和独立</span><span style=\"color: rgb(68, 68, 68);\">\n，</span><span style=\"color: rgb(68, 68, 68);\">\n并迫切地希望别人肯定自己。<br><br>他们会很乐意在生活上给伴侣提供帮助，比如介绍工作、指点人生方向什么的，一般在谈恋爱的时候，主动帮助对方通常代表着示好和希望进一步发展。</span><br><br><span style=\"color: rgb(68, 68, 68);\">\n但回避者并不是想要创造“亲密接触”的机会，而仅仅是想让对方觉得他们很自信和强大，以此进行自我保护。</span><br><br><span style=\"color: rgb(68, 68, 68);\">\n<span style=\"font-weight: bolder;\">\n<span style=\"box-sizing: border-box;\">\n大量的研究也发现，高依恋回避者的自尊水平并不像他们表现出的那样高</span></span></span><span style=\"color: rgb(68, 68, 68);\">\n<span style=\"box-sizing: border-box;\">\n（</span><span style=\"box-sizing: border-box;\">\n研究总结见 Mikulincer &amp; Shaver, 2016, p151-153）。</span></span><br><br><span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n他们只是</span><span style=\"color: rgb(68, 68, 68);\">\n希望让自己看起来更加强大，从而把命运掌握在自己手里，不再受伤。</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n</section></section></section><section class=\"Powered-by-XIUMI V5\" powered-by=\"xiumi.us\" style=\"box-sizing: border-box;\">\n<section style=\"text-align: center; box-sizing: border-box;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px;\">\n<img data-ratio=\"0.5\" data-type=\"gif\" data-w=\"700\" src=\"https://jdxl-img.jdxlt.com/post/e28b3bc6b19f423389dfd78b1389a40e.gif\" style=\"border-style: none; vertical-align: middle; max-width: 100%;\">\n<br>&nbsp;\n</p>\n</section></section><section class=\"Powered-by-XIUMI V5\" powered-by=\"xiumi.us\" style=\"box-sizing: border-box;\">\n<section style=\"box-sizing: border-box;\">\n<section style=\"letter-spacing: 1px; padding: 0px 5px; box-sizing: border-box;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; text-align: center; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(193, 88, 85);\">\n<span style=\"font-weight: bolder; box-sizing: border-box;\">\n作为伴侣，你可以做什么？</span></span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n作为回避者的伴侣，通常人们会从自己身上找原因，“是不是我盯得太紧了？”、</span><span style=\"color: rgb(68, 68, 68);\">\n“我应该给Ta更多空间的啊”、“Ta不喜欢黏人我就该尽量忍住不找Ta啊”。</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"color: rgb(68, 68, 68);\">\n但真的这样做的时候，有时候会感到特别特别委屈：“我就是想和你亲近点，这要求过分吗？！人家谈恋爱都如胶似漆，再不济也算是若即若离，就我，若离若离若离若离……”</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n而且还存在这样一个悖论<span style=\"font-weight: bolder; box-sizing: border-box;\">\n：</span></span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(193, 88, 85);\">\n给对方空间可以短期缓解依恋回避，</span><span style=\"box-sizing: border-box; color: rgb(193, 88, 85);\">\n但是从长期来看，这反而使他们觉得“回避是有用的”。</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(193, 88, 85);\">\n他们可能会更习惯于用“高冷”来保护自己免受伤害，从而加剧依恋回避</span><span style=\"box-sizing: border-box; color: rgb(193, 88, 85);\">\n（Arriaga et al., 2017)。</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n那么到底该如何帮助伴侣缓解依恋回避？</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; text-align: center;\">\n<img data-ratio=\"0.014619883040935672\" data-s=\"300,640\" data-type=\"jpeg\" data-w=\"684\" src=\"https://jdxl-img.jdxlt.com/post/6a33690827524ab2b2ad618364f08ea8.jpeg\" style=\"border-style: none; vertical-align: middle; max-width: 100%;\">\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n我们总结了三个方法：</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box; text-align: center;\">\n<em><span style=\"color: rgb(68, 68, 68);\">\n<span style=\"color: rgb(193, 88, 85);\">\n1.</span></span></em>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; text-align: center; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(193, 88, 85);\">\n<span style=\"font-weight: bolder; box-sizing: border-box;\">\n主动出击，但并非出于焦虑</span></span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n在依恋回避者高冷的人生路途中，能够让Ta改变的通常是“被动”经历一段令人惊喜的亲密关系。</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n以《致青春》中的男主陈孝正举例，</span><span style=\"color: rgb(68, 68, 68);\">\n他是典型的高依恋回避者，但是也在女主的死缠烂打下慢慢改变。</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"color: rgb(68, 68, 68);\">\n这场“被迫”建立的亲密关系却让他获得了许多意外的爱和支持，他的回避也在逐渐减少。</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n但有一点需要指出，</span><span style=\"box-sizing: border-box; color: rgb(193, 88, 85);\">\n如果你的出击带着一种自我怀疑（依恋焦虑），也就是因为害怕被抛弃而想时刻粘着对方，那会带来更大的矛盾。</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n因为</span><span style=\"color: rgb(68, 68, 68);\">\n你常常会因为对方的退缩而陷入自我否定和焦虑中，而你的纠缠会让</span><span style=\"color: rgb(68, 68, 68);\">\nTa</span><span style=\"color: rgb(68, 68, 68);\">\n感到更恐慌，更想逃离。</span>\n</p>\n</section></section></section><section class=\"Powered-by-XIUMI V5\" powered-by=\"xiumi.us\" style=\"box-sizing: border-box;\">\n<section style=\"box-sizing: border-box;\">\n<section style=\"letter-spacing: 1px; padding: 0px 5px; line-height: 1.6; box-sizing: border-box;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n<span style=\"font-weight: bolder;\">\n因此，若你本身有很高的依恋焦虑，那么你需要先建立自己在关系中的自信，才能够在这场恋爱中主动出击，帮助对方建立健康的亲密关系。</span></span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box; text-align: center;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n<span style=\"font-weight: bolder;\">\n<img data-ratio=\"0.43010752688172044\" data-w=\"465\" src=\"https://jdxl-img.jdxlt.com/post/fb7a86b48fcd4039875cfdd37560933b.gif\" style=\"border-style: none; vertical-align: middle; max-width: 100%; box-sizing: border-box; width: 556px;\">\n</span></span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box; text-align: center;\">\n<em><span style=\"box-sizing: border-box; color: rgb(193, 88, 85);\">\n2.</span></em>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; text-align: center; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(193, 88, 85);\">\n<span style=\"font-weight: bolder; box-sizing: border-box;\">\n表达情感，并减少情绪性指责</span></span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"color: rgb(68, 68, 68);\">\n作为伴侣，你可能很多次试图向对方敞开心扉，渴望有心灵的沟通，然而都被对方的高冷打败了，导致你心灰意冷甚至大发脾气，想着就算Ta以后需要你的时候，你也不要再管了。</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n其实，对方的高冷是因为曾经遭受过冷落，害怕再次经历沟通受挫的伤痛。</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n表达自己的情感对于他们来说是很难的，如果可以得到伴侣的支持和回应的话，</span><span style=\"box-sizing: border-box; color: rgb(193, 88, 85);\">\n这将会慢慢改变他们觉得“自己就算表达也一定会遭到忽视”的想法，从而与你<span style=\"box-sizing: border-box;\">\n建立起信任。</span></span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n<span style=\"box-sizing: border-box;\">\n<span style=\"font-weight: bolder;\">\n<span style=\"box-sizing: border-box;\">\n<span style=\"box-sizing: border-box;\">\n信任将是缓解依恋回避的有利解药</span></span></span><span style=\"box-sizing: border-box;\">\n<span style=\"box-sizing: border-box;\">\n(Arriaga et al., 2014)。</span></span></span></span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(193, 88, 85);\">\n<span style=\"font-weight: bolder; box-sizing: border-box;\">\n所以，请在Ta</span></span><span style=\"box-sizing: border-box; color: rgb(193, 88, 85);\">\n<span style=\"font-weight: bolder; box-sizing: border-box;\">\n需要的时候给予支持，而不要轻易指责Ta为何不愿意跟你说心里话，</span></span><span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n指责</span><span style=\"color: rgb(68, 68, 68);\">\n会使他们感到自尊受威胁，从而进行更加严重的自我防御。</span>\n</p>\n</section></section></section><section class=\"Powered-by-XIUMI V5\" powered-by=\"xiumi.us\" style=\"box-sizing: border-box;\">\n<section style=\"box-sizing: border-box;\">\n<section style=\"box-sizing: border-box;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n&nbsp;\n</p>\n</section></section></section><section class=\"Powered-by-XIUMI V5\" powered-by=\"xiumi.us\" style=\"box-sizing: border-box;\">\n<section style=\"box-sizing: border-box;\">\n<section style=\"box-sizing: border-box;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box; text-align: center;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box; text-align: center;\">\n<span style=\"color: rgb(193, 88, 85);\">\n<em>3.</em></span>\n</p>\n</section></section></section><section class=\"Powered-by-XIUMI V5\" powered-by=\"xiumi.us\" style=\"box-sizing: border-box;\">\n<section style=\"box-sizing: border-box;\">\n<section style=\"letter-spacing: 1px; padding: 0px 5px; box-sizing: border-box;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; text-align: center; box-sizing: border-box;\">\n<span style=\"font-weight: bolder;\">\n<span style=\"box-sizing: border-box; color: rgb(193, 88, 85);\">\n多肯定Ta在社交上的进步</span></span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n一般来说，高依恋回避的人会渴望在个人目标上有所成就并得到肯定，包括工作、兴趣爱好等。但是你不仅可以肯定他的个人成就，更<span style=\"font-weight: bolder;\">\n应该多在社交能力上肯定他的进步。</span></span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n这并不是指虚无的夸奖，而是可以对那些很小的进步做出肯定，例如当Ta愿意：</span>\n</p>\n<ul class=\"list-paddingleft-2\" style=\"color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif;\">\n<li><p style=\"margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"color: rgb(68, 68, 68);\">\n跟你一起见好友；</span>\n</p>\n</li>\n    <li><p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"color: rgb(68, 68, 68);\">\n跟你有身体上的接触；</span>\n</p>\n</li>\n    <li><p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"color: rgb(68, 68, 68);\">\n跟你聊聊最近的烦恼；</span>\n</p>\n</li>\n    <li><p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"color: rgb(68, 68, 68);\">\n……</span>\n</p>\n</li></ul><p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n这些小事上你都可以肯定Ta，让Ta知道这些微妙的瞬间给你带来了感动和快乐。</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; box-sizing: border-box;\">\n<span style=\"color: rgb(68, 68, 68);\">\n<span style=\"font-weight: bolder;\">\n<span style=\"box-sizing: border-box;\">\n但是，在对方不愿意时，请不要强迫其做出改变和妥协。</span></span></span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; box-sizing: border-box;\">\n<span style=\"color: rgb(68, 68, 68);\">\n<span style=\"box-sizing: border-box;\">\n特别要说明的是，本文尽管是从伴侣的角度展开写的，但是<span style=\"box-sizing: border-box; color: rgb(193, 88, 85);\">\n并不代表你需要变成Ta改变的负责人</span>，伴侣是陪伴者的角色，最终决定是否改变的还是Ta自己。</span></span>\n</p>\n<div><span style=\"color: rgb(68, 68, 68);\">\n<span style=\"box-sizing: border-box;\">\n<br></span></span></div></section></section></section>','简单心理',22,3,1,'2021-10-05','2021-11-20 10:39:43','2021-12-08 23:27:31');
INSERT INTO `kno_info` VALUES (14,'活着就是要有“生气”！',1,'https://jdxl-img.jdxlt.com/post/98fd8a682ada46918d4fda9daf970b12.jpg','摘要','<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; line-height: 1.75em; text-align: center;\">\n<inherit><span style=\"font-size: 15px;\">\n<img data-ratio=\"0.5629496402877698\" data-s=\"300,640\" data-type=\"jpeg\" data-w=\"\" src=\"https://jdxl-img.jdxlt.com/post/4e3f50e382944c8689c9f952f52457d9.jpeg\" style=\"border-style: none; vertical-align: middle; max-width: 100%;\">\n</span></inherit>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; line-height: 1.75em;\">\n<br><span style=\"color: rgb(193, 88, 85);\">\n你是一个常常愤怒并表现出来的人吗？我不是。我甚至觉得自己是一个“愤怒无能者”，因为我总认为愤怒是“不得体”，“不沉稳”的表现。有一句话说：人的一切痛苦，都是源于对自己无能的愤怒。我曾在很长一段时间里把它当作金科玉律——因为我不想当一个无能的人呀。<br><br>直到后来我走进咨询室里慢慢探索自己时，我才明白，我应该愤怒，为什么不呢？愤怒，从来就不是一件值得羞耻的事情～</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; line-height: 1.75em;\">\n<inherit></inherit>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; line-height: 1.75em;\">\n<inherit></inherit>\n</p>\n<blockquote style=\"color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; line-height: 1.75em;\">\n<inherit></inherit>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; text-align: right; line-height: 1.75em;\">\n<inherit><span style=\"color: rgb(193, 88, 85);\">\n——简单心理J室长</span></inherit><inherit></inherit><br>&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; line-height: 1.75em; text-align: center;\">\n<inherit><img data-ratio=\"0.11330935251798561\" data-type=\"gif\" data-w=\"\" src=\"https://jdxl-img.jdxlt.com/post/c05307b45fbd499cae1716fe9619a718.gif\" style=\"border-style: none; vertical-align: middle; max-width: 100%;\">\n</inherit>\n</p>\n</blockquote><p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; line-height: 1.75em;\">\n<inherit></inherit>文｜<span style=\"color: rgb(193, 88, 85);\">\n<span style=\"font-weight: bolder;\">\n犀犀张</span></span>&nbsp;简单心理内容实验室<br><br><inherit>Terry 今年四十好几岁了。用她女儿的话说，她「曾是这世界上最和善、温柔、甜美的女人」——是的，「曾是」。</inherit><br><br>在丈夫 Gary 忽而一夜消失不见之后，她确信自己和四个女儿被抛弃了。于是，这个曾经最温柔的女人内心的<span style=\"font-weight: bolder;\">\n愤怒</span>像火山一样爆发。她开始酗酒、乱发脾气、戾气十足。<br><br>这部电影的英文名叫做 The Upside of Anger，或许可以翻译成「愤怒的好处」。电影中，愤怒就是一把利刃，戳破 Terry 看似四平八稳、实则被空虚和不安全感充满的生活。想来，Terry 可能从来就不是那么和善、没脾气的一个人，而是一直在掩盖自己的「阴暗」情绪，直到愤怒的导火索将她陈旧的心理模式和生活方式捅破。<br>&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; line-height: 1.75em;\">\n<inherit></inherit>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; line-height: 1.75em;\">\n<inherit></inherit>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; line-height: 1.75em;\">\n<inherit></inherit>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; line-height: 1.75em;\">\n<inherit></inherit>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; text-align: center; line-height: 1.75em;\">\n<inherit><img data-ratio=\"0.5575539568345323\" data-type=\"jpeg\" data-w=\"\" src=\"https://jdxl-img.jdxlt.com/post/8d22a42bac204a1c8911d6cbe05f3b83.jpeg\" style=\"border: 0px; vertical-align: middle; max-width: 100%; cursor: default; width: 1000px;\">\n</inherit>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; line-height: 1.75em;\">\n<inherit></inherit>尽管影片中的 Terry 并没发生太多成长，但「愤怒的好处」绝对是确实存在的。<br><br><span style=\"font-weight: bolder;\">\n在大多数人眼里，愤怒是种从根本上就很消极的情绪，甚至还带着让人羞愧、耻辱的余味。</span>悲伤、焦虑尚且有人凑过来，理解、陪伴，给予安慰和同情；还因为它天生具有攻击性，给予安慰和同情；但愤怒之下，必然只剩你一人站在怒火之中。<br><br>但是，真的一定要如此么？<br>&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; line-height: 1.75em; text-align: center;\">\n<inherit><span style=\"color: rgb(193, 88, 85);\">\n<span style=\"font-weight: bolder;\">\n压抑愤怒，是一种受伤害的开始</span></span></inherit><br>&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; line-height: 1.75em;\">\n<inherit></inherit>如果我告诉你，<span style=\"font-weight: bolder;\">\n婴孩时期，我们的大哭便是一种愤怒的方式，来表达需求未被满足或被剥夺的痛苦。</span>那么，你就会相信，愤怒是一种<span style=\"font-weight: bolder;\">\n原始</span>的情绪。只是，随着成长，人们渐渐意识到，在社会文化中，我们需要压制愤怒，因为它太不讨喜——不仅因为消极，还因为它天生具有攻击性，往往会吓跑身边的所有人，无法招致好意。<br><br>初二的时候，我们班来了一个刚毕业的语文老师。也不记得最初是谁惹了谁，总之，我们班迅速成了她口中全年级纪律最差、最不像话的班级。上课时，她总是将手里的鼠标当成角尺，噼里啪啦摔打在桌上。而同学们也不负所望，纷纷则从课桌里掏出 CD 机、小说、漫画，甚至三五成群坐在一起小声聊天。课堂瞬时变成两军交火的战场——不管你信不信，这是所重点中学。<br><br>我，也和所有那个年纪的孩子一样<span style=\"color: rgb(136, 136, 136);\">\n（呃什么？你们都不是这样？）</span>，甚至可能再稍稍过分那么一点点：私下给老师起外号，对她的吼叫置若罔闻，上语文课有可能在做任何事，除了听课……<br><br>终于，在一次公然与老师作对之后，我被请到了办公室。当时我怒不可遏，但又隐隐有些担忧。在语文教研组的办公室里，另一位老师走过来，拍拍我，笑着满是善意地说：听说过你，挺有个性的？<br><br>瞬时间，我所有的未被尊重<span style=\"color: rgb(136, 136, 136);\">\n（当然，反过来年少的我也报以对方伤害和不尊重）</span>、被否认、被质疑，都在她这动作、话语和笑容间消散了。后来，初三的时候，她成了我们的语文老师。我和她关系还挺不错。<br><br>但是，话说回来，未必人人愿意以我这种「叛逆」表达自己的不满，或许也惧怕面对类似的冲突。所以，我们渐渐学会压抑自己的愤怒：在学校得不到尊重，梦想被嘲笑；转身，面对社会上的不公，对房价、对上司……绝大多数时候，我们只能选择控制。还有些时候，原本和平的社交关系被对方忽然越界，边界被侵犯混淆；有时，陌生人或亲人会侵犯、剥夺我们的权利或空间。凡此种种。<inherit></inherit><br>&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; line-height: 1.75em; text-align: center;\">\n<inherit><img data-ratio=\"0.76\" data-type=\"gif\" data-w=\"450\" src=\"https://jdxl-img.jdxlt.com/post/970eebcdaaf947cbb526dbefb5933604.gif\" style=\"border: 0px; vertical-align: middle; max-width: 100%; cursor: default; width: 1000px;\">\n</inherit><br>&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; line-height: 1.75em;\">\n<inherit></inherit><span style=\"font-weight: bolder;\">\n愤怒的「电光火石」，随处皆是。</span>小火苗一旦燃起，被它伤害的「初始」模式，就是压抑愤怒，将其埋在心里。尤其，对于向影片女主角 Terry 这般，有着诸多「附属身份」的女性而言，发怒似乎意味着粗鲁、自私，更不被接受，因而也更倾向被抑制。<br><br><span style=\"font-weight: bolder;\">\n未被熄灭的愤怒会转化为两种情绪：</span><span style=\"font-weight: bolder;\">\n自责、自我怀疑，与焦虑</span>。心理学家凯伦·霍妮曾有一个著名的理论：孩子对于父母的敌意如果受到了压抑，将会逐渐导致这种敌意被转化为焦虑，并且蔓延到孩子对整个世界的观感中去。即，如果长期压抑自己的敌意，那么这种敌意就会从有明确对象的「愤怒」，转化为失去明确对象的，泛化的焦虑。<br><br>长此以往控制情绪，可能会愈发丧失觉察与满足自我需要的能力，甚至无法体验、表达愤怒，而这本身，已会让人们产生无力感。<br>&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; line-height: 1.75em; text-align: center;\">\n<span style=\"font-weight: bolder; color: rgb(193, 88, 85);\">\n「合理」面对自己的愤怒</span><br>&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; line-height: 1.75em;\">\n不同的情绪会激活大脑不同的部分。例如，焦虑和恐惧会激活大脑中与「逃避」有关的区域，而愤怒则与趋向行为有关。在生气的时候，我们相信自己可以改变结果，使之有所不同。所以，我们会趋向行动、改变，而非逃避。因此，从本质上讲，愤怒的情绪带有「命题验证」的色彩，驱使人们去寻求能够证明自己想法的证据。<br><br>那，愤怒的人一定会更热衷寻找与先前想法结论一致的证据啦？——你的直觉这样告诉你，毕竟，像十五岁的我一样，他们看起来那么自以为是。<br><br>然而，研究者得出的结论恰恰相反。<span style=\"font-weight: bolder;\">\n愤怒状态下的人反而会更多地采集驳斥自己的信息，想法更开放，结果也更有可能改变先前的认识，而非陷在其中。</span><inherit></inherit><br>&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; line-height: 1.75em; text-align: center;\">\n<inherit><img data-ratio=\"0.51\" data-type=\"gif\" data-w=\"500\" src=\"https://jdxl-img.jdxlt.com/post/852afb8f7759444c9f4cab93b05b0486.gif\" style=\"border: 0px; vertical-align: middle; max-width: 100%; cursor: default; width: 1000px;\">\n</inherit>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; line-height: 1.75em;\">\n<inherit></inherit><br>相比仅仅只是感到「悲伤」，愤怒下的行动倾向有助于减弱「证实偏差」，即一种搜索、解释新信息，将其统统解读为对原有认识的强化和支持的偏差。<br><br>这种状态下，如果运用得好，别说是和语文老师的战争了，即使是社会中根深蒂固的冲突，甚至也能以愤怒化解。<br><br>在巴以冲突的背景下，曾有研究者请以色列的被试者们阅读一份煽动性很强的内容，以此激发他们愤怒的情绪，然后衡量这种情绪会让他们更激进，还是更缓和。结果是：说不准，更高涨的愤怒使一些人的仇恨加深，但同时会令另一些人的仇恨得到妥协和缓解。<br><br>这听起来很奇怪：<span style=\"font-weight: bolder;\">\n为什么同样的刺激会使人们向两个不同方向驱使？</span><br><br>原因要归结于一个社会心理学概念：「基本归因谬误」。对方做出惹恼你的行为，究竟因为他这个人就是坏，还是当时情况所导致的呢？如果我们相信令我们愤怒的、不受欢迎的行为来自对方的内在特点，例如人格，或是一个组织的道德风气时，我们便会以破坏性的、激进的方式行动；而如果认为不满意的结果只是由当时的情境因素造成，那么人们则会以更缓和、更和平的方式趋近。<br><br>所以，如果当时语文课战场上的双方都能意识到自己在火上浇油，讲台上的人未必天生气急败坏爱攻击，讲台下的人也不全然无药可救、目中无人，那么，可能……可能会少很多有趣的回忆吧。<br>&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; text-align: center; line-height: 1.75em;\">\n<inherit></inherit>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; line-height: 1.75em;\">\n<inherit></inherit>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; line-height: 1.75em;\">\n<inherit></inherit>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; line-height: 1.75em;\">\n<inherit></inherit>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; text-align: center; line-height: 1.75em;\">\n<inherit></inherit>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; line-height: 1.75em;\">\n<inherit></inherit>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; line-height: 1.75em;\">\n<inherit></inherit>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; line-height: 1.75em;\">\n<inherit></inherit>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; line-height: 1.75em;\">\n<inherit></inherit>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; text-align: center; line-height: 1.75em;\">\n<inherit><span style=\"font-weight: bolder;\">\n<span style=\"color: rgb(193, 88, 85);\">\n高情商之间的对决</span></span></inherit><br>&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; line-height: 1.75em;\">\n<inherit></inherit>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; line-height: 1.75em;\">\n乍看之下，「情商高」的人似乎会选择把怒气及一切消极情绪都抛到一边，但心理学家 Dan Moshavi 指出，越来越多的证据说明，<span style=\"font-weight: bolder;\">\n真正情商高的人会选择拥抱愤怒</span>——只要这种情绪在当时情况下，可以帮助他们实现某个目标。<br><br>毫无疑问，「会哭的孩子」能够得到更多的注意力，愤怒的表达甚至可以让对方感受到某种真实可信感。<span style=\"font-weight: bolder;\">\n相比那些从不发怒的伙伴，懂得愤怒的人们居然能得到更大的尊重，也更受欢迎。</span><br><br>如此说来，高情商者们一定意识到，如何解读愤怒也是门艺术。这种能力被称作「认知动机」：认知动机高的人，会试图寻找愤怒表达背后的意义，而认知动机低的人则会关注与愤怒本身。<br><br>可能，这也正是为什么有些人会宁愿选择脾气不好的老板的原因：当你以高认知动机对待上司的愤怒时，看到的是引发愤怒的根本原因，例如自己工作上的不足，或是沟通上的失误，进而可以得到自我的进步和提升。<br>&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; line-height: 1.75em; text-align: center;\">\n<img data-ratio=\"0.6007194244604317\" data-s=\"300,640\" data-type=\"jpeg\" data-w=\"\" src=\"https://jdxl-img.jdxlt.com/post/5d52fb5e71e24a85b1dc03d2efdf1bc6.jpeg\" style=\"border-style: none; vertical-align: middle; max-width: 100%;\">\n<br>&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; line-height: 1.75em;\">\n正如亚里士多德所说：<span style=\"line-height: 1.75em;\">\n「每个人都会发怒，这很简单。但向恰当的人，在恰当的时间，以恰当的动机、恰当的方法，表达恰当程度的愤怒，并不是每个人都能做到的易事。」</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; line-height: 1.75em; text-align: center;\">\n<br><img data-ratio=\"0.11330935251798561\" data-type=\"gif\" data-w=\"\" src=\"https://jdxl-img.jdxlt.com/post/82722f6ee6884c5fb6d7d00f2f6609ef.gif\" style=\"border-style: none; vertical-align: middle; max-width: 100%;\">\n<br>&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; line-height: 1.75em;\">\n<span style=\"font-weight: bolder;\">\n其实，愤怒的情绪并不值得我们羞耻。相反，其中对于个人空间、边界、正义的知觉，恰恰是人性中自尊与道德的生动证明。</span><br><br>很多时候，愤怒只是我们的一种逃避方法，逃避其他更深、更脆弱的感情，例如悲伤，或羞耻。当被有意识地处理、审视的时候，愤怒可以将我们指向那些真正的问题，告诉我们，自己究竟在意什么，甚至，究竟什么是自己真实的模样。<br><br>所以，事情很简单：<span style=\"font-weight: bolder;\">\n要么，你观察、了解你的愤怒；要么，你受制于它。</span><br><br>电影中，Terry 的女儿这样讲道：<br>「<span style=\"font-weight: bolder;\">\n我现在知道，愤怒和憎恶让你在原有轨迹上停下来。它并不需要什么燃料，仅是把空气和生活吞噬、浸没……但它是真实的。它会改变你，塑造你，把你打造成不同于原本的样子。</span>」<br><br>是的，愤怒有这种力量。<span style=\"font-weight: bolder;\">\n但将它驶向哪里的方向盘，在你手上。</span><br>&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; line-height: 1.75em; text-align: center;\">\n<img data-ratio=\"0.7960893854748603\" data-s=\"300,640\" data-type=\"png\" data-w=\"358\" src=\"https://jdxl-img.jdxlt.com/post/1ba4749eca12433387e50e25e84ecbb3.png\" style=\"border-style: none; vertical-align: middle; max-width: 100%;\">\n<br><span style=\"font-weight: bolder;\">\n<span style=\"color: rgb(193, 88, 85);\">\n“<span style=\"color: rgb(41, 42, 106);\">\n当你生气时，数到四。</span></span></span><br><span style=\"color: rgb(41, 42, 106);\">\n<span style=\"font-weight: bolder;\">\n当你非常生气时，咒骂吧～</span></span><span style=\"font-weight: bolder;\">\n<span style=\"color: rgb(193, 88, 85);\">\n”&nbsp;</span></span><br><span style=\"color: rgb(41, 42, 106);\">\n― Mark Twain</span>\n</p>','简单心理',49,4,1,'2019-10-04','2021-11-20 10:39:43','2021-11-20 10:39:43'),(15,'如何应对职场情绪暴力？',2,'https://jdxl-img.jdxlt.com/post/e36cac2aae4a4a6da0e20e88bb45cb1a.png','摘要','<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; box-sizing: border-box;\">\n<span style=\"color: rgb(68, 68, 68);\">\n小编的一位好朋友，最近辞职了。不是因为“节后恐班”综合症，而是因为一个非常严肃、却常常被职场新人忽视的原因。<br><br>本来大家都以为，她在一家理想的公司，也很喜欢自己的工作，所以听到这个消息都很惊讶。</span><br><br><span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n一开始和她聊起这件事的时候，她只是轻描淡写地说：“我老板脾气不太好。”</span><br><br><span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n后来，我们才发现，她一直有些抑郁。而诱因也与她先前这份工作有关：</span>\n</p>\n<blockquote style=\"color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n“老板会突然发脾气，而且当着全公司的人骂我，骂得很难听。时间长了，我也怀疑是不是真的自己工作能力不行，频繁出现失误，不值得公司给的资源。</span><br><br><span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n我也努力过，只要是交给我的任务，加班加点也会去做，但是Ta看都不看，可能是还没有达到Ta的要求吧……但这样上班真的太压抑了，所以就走了。”</span>\n</p>\n</blockquote><p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n&nbsp;如果你也和小编的朋友一样，因为一些“脾气不好”的老板或同事而倍感压抑、怀疑自己能力，甚至做不下去。</span><br><br><span style=\"box-sizing: border-box; color: rgb(193, 88, 85);\">\n也许该反思的并不是“我是不是能力不行”，而是“我是不是在遭受职场情绪暴力？”</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; box-sizing: border-box; text-align: center;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n<img data-ratio=\"0.44375\" data-s=\"300,640\" data-type=\"jpeg\" data-w=\"1280\" src=\"https://jdxl-img.jdxlt.com/post/e4c159f2426249b18fd8642f30b8a2f4.jpeg\" style=\"border-style: none; vertical-align: middle; max-width: 100%; letter-spacing: 1px;\">\n</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; box-sizing: border-box;\">\n<span style=\"font-size: 18px;\">\n<span style=\"font-weight: bolder; color: rgb(193, 88, 85);\">\n<span style=\"box-sizing: border-box;\">\n脾气不好不能成为暴力的理由</span></span></span><br>&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; box-sizing: border-box;\">\n<span style=\"color: rgb(193, 88, 85);\">\n<span style=\"font-weight: bolder;\">\n<span style=\"box-sizing: border-box;\">\n职场情绪暴力</span></span><span style=\"font-weight: bolder;\">\n<span style=\"box-sizing: border-box;\">\n（Emotional abuse in the workplace）</span></span></span><span style=\"font-weight: bolder;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n，指的某个人在工作场合反复地采取非身体形式的、非与性相关的暴力行为来攻击他人。</span></span><br><br><span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n它可能发生在任何工作关系中，上下级之间、同事之间、乙方和甲方(爸爸)之间等等。</span><br><br><span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n区别于偶发的暴力行为，或一般的工作压力，职场情绪暴力通常是<span style=\"font-weight: bolder;\">\n反复发生</span>、<span style=\"font-weight: bolder;\">\n长期进行</span><span style=\"font-weight: bolder;\">\n的，且是施虐者有意为之</span>。</span><span style=\"color: rgb(68, 68, 68);\">\n</span><br><br><span style=\"color: rgb(68, 68, 68);\">\n<span style=\"letter-spacing: 1px;\">\n情绪暴力</span>所涵盖的范围非常广泛，可能是<span style=\"color: rgb(193, 88, 85);\">\n以语言的形式</span>，例如谩骂、持续的批评、取外号，也可能是一些<span style=\"color: rgb(193, 88, 85);\">\n更为微妙的举动</span>，例如隐瞒工作上的重要信息、冷处理、怒视，或是翻白眼。</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; text-align: center;\">\n<img data-ratio=\"0.4444444444444444\" data-s=\"300,640\" data-type=\"png\" data-w=\"900\" src=\"https://jdxl-img.jdxlt.com/post/ac6f14664edd4ad2b55345c7b85d591f.png\" style=\"border-style: none; vertical-align: middle; max-width: 100%;\">\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n通常，施暴者会从以下6个方面来操控受害者的情绪：</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; box-sizing: border-box;\">\n<br><span style=\"font-weight: bolder;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n1. 威胁专业地位</span></span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; box-sizing: border-box;\">\n<span style=\"color: rgb(68, 68, 68);\">\n无论怎么努力，都会被“鸡蛋里挑骨头”；任何不尽如人意的结果，都归因于<span style=\"color: rgb(193, 88, 85);\">\n“你个人能力不行”、“你还是不够努力”。</span></span><span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; box-sizing: border-box;\">\n<br><span style=\"font-weight: bolder;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n2. 威胁个人地位</span></span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n取羞辱性质的外号、<span style=\"color: rgb(193, 88, 85); letter-spacing: 1px; line-height: 24px;\">\n冷嘲热讽</span>；“你一个女的能当经理，肯定用了见不得人的手段吧”，“你上班怎么不带脑子啊？”</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; box-sizing: border-box;\">\n<br><span style=\"font-weight: bolder;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n3. 孤立</span></span>\n</p>\n<p style=\"margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n对你视而不见，组织同事聚餐但就是不通知你，当你不存在，甚至<span style=\"color: rgb(193, 88, 85); letter-spacing: 1px; line-height: 24px;\">\n离间</span>你与其他同事的关系等等。</span><br>&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; box-sizing: border-box; text-align: center;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n<img data-ratio=\"0.5182186234817814\" data-s=\"300,640\" data-type=\"jpeg\" data-w=\"494\" src=\"https://jdxl-img.jdxlt.com/post/dfd8e9bcdea24f9dba423559e268b302.jpeg\" style=\"border-style: none; vertical-align: middle; max-width: 100%; letter-spacing: 1px; width: 1000px; height: auto;\">\n</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; box-sizing: border-box;\">\n<br><span style=\"font-weight: bolder;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n4. 致使过度工作</span></span>\n</p>\n<p style=\"margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n<span style=\"color: rgb(193, 88, 85); letter-spacing: 1px;\">\n故意打乱你的工作节奏，</span>分派过多的任务给你，要求你加班加点的工作，甚至还教导你说加班是正常的、10点下班是常态，不这么做就是不上进等等。</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; box-sizing: border-box;\">\n<br><span style=\"font-weight: bolder;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n5. 致使陷入混乱</span></span>\n</p>\n<p style=\"margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n不断提到你之前犯过的错，翻旧帐，当你想要推进新工作时，对方可能会说：“得了吧，你上次想做xx的时候就搞砸了，可省省吧。”</span><br><span style=\"color: rgb(68, 68, 68);\">\n或是突然调换你的岗位，让他人替代你，把你踢出项目，还不告诉你。</span><br>&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; box-sizing: border-box;\">\n<span style=\"font-weight: bolder;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n6. 蓄意阻挠</span></span>\n</p>\n<p style=\"margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n拒绝提供任何资源或帮助；<span style=\"color: rgb(193, 88, 85); letter-spacing: 1px; line-height: 24px;\">\n不回复工作邮件、微信</span>，导致工作进程延误，且最终你需要担责任。</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; text-align: center;\">\n<img data-ratio=\"0.4444444444444444\" data-s=\"300,640\" data-type=\"png\" data-w=\"900\" src=\"https://jdxl-img.jdxlt.com/post/aceb333f51874f41b87c62e60f073c4b.png\" style=\"border-style: none; vertical-align: middle; max-width: 100%;\">\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; box-sizing: border-box; text-align: center;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; box-sizing: border-box;\">\n<span style=\"font-size: 18px;\">\n<span style=\"color: rgb(193, 88, 85);\">\n<span style=\"font-weight: bolder;\">\n<span style=\"box-sizing: border-box; letter-spacing: 1px;\">\n职场情绪暴力</span></span></span><span style=\"font-weight: bolder; color: rgb(193, 88, 85);\">\n<span style=\"box-sizing: border-box;\">\n有哪些影响</span></span></span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; box-sizing: border-box;\">\n<br><span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n一开始讲到的朋友，因为来自老板的<span style=\"letter-spacing: 1px;\">\n情绪暴力</span>，不仅被迫辞职，在辞职并换了新工作之后，也时常陷入抑郁、焦虑的情绪，不断怀疑自己的工作能力。<br><br>研究显示，职场<span style=\"letter-spacing: 1px;\">\n情绪暴力</span>会带来<span style=\"font-weight: bolder;\">\n持续的负面情绪，并损伤认知功能</span>，具体包括但不限于：</span>\n</p>\n<ul class=\"list-paddingleft-2\" style=\"color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<li><p style=\"margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n愤怒与怨恨</span>\n</p>\n</li>\n    <li><p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"color: rgb(193, 88, 85); line-height: 1.6;\">\n恐惧</span>\n</p>\n</li>\n    <li><p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"color: rgb(68, 68, 68); line-height: 1.6;\">\n抑郁与焦虑情绪</span>\n</p>\n</li>\n    <li><p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"color: rgb(193, 88, 85); line-height: 1.6;\">\n注意力下降</span>\n</p>\n</li></ul><p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n试着想象自己就是<span style=\"letter-spacing: 1px;\">\n情绪暴力</span>的受害者时，感觉自己可能也会<span style=\"font-weight: bolder;\">\n内化施暴者的评价</span>，认为自己真的能力不足、不够努力，甚至怪罪自己。<br><br>与此同时，施暴者还在不断强化着对我们的负面评价，带来新的困扰。这让我们距离<span style=\"font-weight: bolder;\">\n抑郁、焦虑</span>又近了一步。</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; text-align: center;\">\n<img data-ratio=\"0.2777777777777778\" data-type=\"gif\" data-w=\"900\" src=\"https://jdxl-img.jdxlt.com/post/e8ae6cf6f7e8484b9cc921320fc1171f.gif\" style=\"border-style: none; vertical-align: middle; max-width: 100%;\">\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; box-sizing: border-box;\">\n<br><span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n每天有8个小时甚至更长的时间处于这种高压环境中，我们的<span style=\"font-weight: bolder;\">\n身体也会有一些不适的反应</span>，例如胃痛、头痛、胸闷、呼吸不畅等等，这是很正常的躯体化反应。<br><br>而施暴者或是其他同事却常常不能理解，以为我们这些胃痛头痛的反应，是因为我们“承受不住压力”。</span><br><br><span style=\"color: rgb(68, 68, 68);\">\n即使在我们离开原先的工作环境后，依然可能长期被这些身体症状所困扰。</span><br><br><span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n在实际工作方面，</span><span style=\"box-sizing: border-box; color: rgb(193, 88, 85);\">\n<span style=\"font-weight: bolder;\">\n职场<span style=\"box-sizing: border-box;\">\n情绪暴力</span>会真的将受害者渐渐变成“什么都做不好”的样子。</span></span><span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n我们可能觉得做什么工作都很累、力不从心；</span><br><br><span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n会开始<span style=\"font-weight: bolder;\">\n消极怠工</span>，因为无论如何努力都不会有好结果；会觉得自己<span style=\"font-weight: bolder;\">\n永远无法融入这个集体</span>，觉得自己是个“局外人”；我们可能会选择<span style=\"font-weight: bolder;\">\n离职</span>，但是对于接下来的职业道路茫然无措……</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; text-align: center;\">\n<img data-ratio=\"0.4444444444444444\" data-s=\"300,640\" data-type=\"png\" data-w=\"900\" src=\"https://jdxl-img.jdxlt.com/post/49301686363447efa7d5c1fd87c0abc2.png\" style=\"border-style: none; vertical-align: middle; max-width: 100%;\">\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n但需要注意的是，这并不是受害者自身的原因造成的，只是连我们自己都会忘记，在遭遇职场<span style=\"letter-spacing: 1px;\">\n情绪暴力</span>之前，我们对工作是如何地充满热情。<br><br>在一些严重的情况下，职场<span style=\"letter-spacing: 1px;\">\n情绪暴力</span>会成为受害者<span style=\"font-weight: bolder;\">\n<span style=\"color: rgb(193, 88, 85); letter-spacing: 1px; line-height: 24px;\">\n长期的心理阴影</span></span>，跟随着我们进入下一份工作。</span><br><br><span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n例如，小编朋友的新老板是个外向开朗、情绪稳定的人，但朋友总是担心自己做错事，只要新老板态度稍微有些严肃，她就以为对方要向自己发脾气，所以仍然时时感到恐慌不安。</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; box-sizing: border-box; text-align: center;\">\n<img data-ratio=\"0.4444444444444444\" data-s=\"300,640\" data-type=\"png\" data-w=\"900\" src=\"https://jdxl-img.jdxlt.com/post/fd8f976d0a9746f688e32467a0cb010c.png\" style=\"border-style: none; vertical-align: middle; max-width: 100%;\">\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; box-sizing: border-box;\">\n&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; box-sizing: border-box;\">\n<span style=\"font-size: 18px;\">\n<span style=\"color: rgb(193, 88, 85);\">\n<span style=\"font-weight: bolder;\">\n<span style=\"box-sizing: border-box;\">\n是什么让人</span></span></span><span style=\"font-weight: bolder; color: rgb(193, 88, 85); line-height: 1.6;\">\n<span style=\"box-sizing: border-box;\">\n成为施暴者</span></span></span><br>&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n许多心理学研究都发现，施暴倾向与病态的、适应不良的人格特质相关联，比如最常被提起的<span style=\"font-weight: bolder;\">\n“黑暗三角人格”</span>。</span><br><br><span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n黑暗三角人格（the dark triad），即<span style=\"font-weight: bolder;\">\n自恋型人格、冷血症和马基雅维利主义者</span>这三种较为阴暗的人格特质。研究发现，在工作场合，这三类人格特质，都与职场<span style=\"letter-spacing: 1px;\">\n情绪暴力</span>有着密不可分的关系。</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; text-align: center;\">\n<img data-ratio=\"0.2222222222222222\" data-s=\"300,640\" data-type=\"png\" data-w=\"900\" src=\"https://jdxl-img.jdxlt.com/post/7ae0eed0b6a546e09d302b2deb749a01.png\" style=\"border-style: none; vertical-align: middle; max-width: 100%;\">\n</p>\n<ul class=\"list-paddingleft-2\" style=\"color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<li><p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"color: rgb(193, 88, 85);\">\n<span style=\"font-weight: bolder;\">\n<span style=\"box-sizing: border-box;\">\n自恋型人格（Narcissism）</span></span></span>\n</p>\n</li></ul><p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; box-sizing: border-box;\">\n<span style=\"color: rgb(68, 68, 68);\">\n自恋者完全以自我为中心，过分关注自我，而忽视外界及他人的存在。</span><br><br><span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\nMattice和Spitzberg指出，自恋者对于<span style=\"letter-spacing: 1px;\">\n情绪暴力</span>他人有很高的动机，且<span style=\"font-weight: bolder;\">\n会在得手后获得满足感。</span></span><br><br><span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n在工作场合，自恋者很少直接言语攻击他人，而更倾向于使用一些<span style=\"font-weight: bolder;\">\n间接的施暴方式</span>，例如，无视他人、散播谣言、隐瞒工作需要的信息、为他人指派远超过对方能力的任务，等等。</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; text-align: center;\">\n<img data-ratio=\"0.2222222222222222\" data-s=\"300,640\" data-type=\"png\" data-w=\"900\" src=\"https://jdxl-img.jdxlt.com/post/f6a853a216344f958a58348bac4149a3.png\" style=\"border-style: none; vertical-align: middle; max-width: 100%;\">\n</p>\n<ul class=\"list-paddingleft-2\" style=\"color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<li><p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"font-weight: bolder;\">\n<span style=\"box-sizing: border-box; color: rgb(193, 88, 85);\">\n冷血症（Psychopathy）</span></span>\n</p>\n</li></ul><p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; box-sizing: border-box;\">\n<span style=\"color: rgb(68, 68, 68);\">\n冷血症或许是黑暗三人格中最为“病态”的一类，其特点是具有</span><span style=\"font-weight: bolder; color: rgb(68, 68, 68); font-size: 15px;\">\n反社会倾向、冷血无情、缺乏道德感、异常大胆、易有冲动行为</span><span style=\"color: rgb(68, 68, 68);\">\n。</span><br><br><span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n有冷血症倾向的人，在工作场合会“见人说人话，见鬼说鬼话”，通过<span style=\"font-weight: bolder;\">\n谎言和自我包装</span>来博得上级好感，而对下级实施<span style=\"letter-spacing: 1px;\">\n情绪暴力</span>。对于他们而言，<span style=\"letter-spacing: 1px;\">\n情绪暴力</span>只是控制下属、获取权力的手段之一。</span><br><br><span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n冷血症倾向的人们，在<span style=\"letter-spacing: 1px;\">\n情绪暴力</span>中，可能使用直接的方式，例如<span style=\"font-weight: bolder;\">\n公开羞辱他人、威胁或恐吓他人</span>；</span><br><br><span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n也可能使用间接的方式，例如快速转换情绪，上一秒还风和日丽，下一秒就板起脸来发脾气，故意制造这种阴晴不定的紧张感，还有的会<span style=\"font-weight: bolder;\">\n<span style=\"letter-spacing: 1px; line-height: 24px;\">\n故意散播关于他人的假消息</span></span>。</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; text-align: center;\">\n<img data-ratio=\"0.2222222222222222\" data-s=\"300,640\" data-type=\"png\" data-w=\"900\" src=\"https://jdxl-img.jdxlt.com/post/c188ac500a4a476db02fca2fe5d21645.png\" style=\"border-style: none; vertical-align: middle; max-width: 100%;\">\n</p>\n<ul class=\"list-paddingleft-2\" style=\"color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<li><p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box;\">\n<span style=\"color: rgb(193, 88, 85);\">\n<span style=\"font-weight: bolder;\">\n<span style=\"box-sizing: border-box;\">\n马基雅维利主义者（Machiavellianism）</span></span></span>\n</p>\n</li></ul><p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n&nbsp;马基雅维利主义倾向较高的人，善于权术和谋略，甚至<span style=\"font-weight: bolder;\">\n为达目的不择手段</span>。</span><br><br><span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n在马基雅维利主义者的眼里，谦卑毫无必要，傲慢才是对待他人的有效方式；弱者才需要遵从道德准则，手握重权的人可以随意欺骗和利用他人；<span style=\"font-weight: bolder;\">\n比起让人爱戴，他们更愿意让人恐惧。</span></span><br><br><span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n在工作场合，马基雅维利主义者不会与同级或下属分享重要的工作信息，也会<span style=\"font-weight: bolder;\">\n暗中使用手段操控他人</span>，让其他人看起来不如Ta适合成为领导者。</span><br><br><br><span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n除人格特质之外，也有其他一些因素，让人们更容易在精神上操控、甚至虐待他人。</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; box-sizing: border-box;\">\n<br><span style=\"font-weight: bolder;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n1. 消极看待他人</span></span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\nAshforth在早年间的一项研究中发现，工作场合中的暴力倾向，与人们的认知也有关联。如果管理者默认员工是消极的、不鞭策是不会努力工作的，那么他们会更倾向于对员工采取暴力的行为。</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; box-sizing: border-box;\">\n<span style=\"font-weight: bolder;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n2. 施虐史与酗酒</span></span><br>&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n多项研究表明，酗酒与过往的施虐史，都可以被用来预测生活中反复、长期暴力行为，这其中也包括了工作场合的<span style=\"letter-spacing: 1px;\">\n情绪暴力</span>。</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; text-align: center;\">\n<img data-ratio=\"0.27684964200477324\" data-type=\"gif\" data-w=\"419\" src=\"https://jdxl-img.jdxlt.com/post/513b0052fd724ac485f63761d1fa88e4.gif\" style=\"border-style: none; vertical-align: middle; max-width: 100%; width: 1000px; height: auto;\">\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n&nbsp;</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; box-sizing: border-box;\">\n<span style=\"font-size: 18px;\">\n<span style=\"color: rgb(193, 88, 85);\">\n<span style=\"font-weight: bolder;\">\n<span style=\"box-sizing: border-box;\">\n遭遇职场<span style=\"letter-spacing: 1px;\">\n情绪暴力</span></span></span></span></span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; box-sizing: border-box;\">\n<span style=\"font-size: 18px;\">\n<span style=\"font-weight: bolder;\">\n<span style=\"box-sizing: border-box; color: rgb(193, 88, 85);\">\n如何“抢救”自己</span></span></span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; box-sizing: border-box;\">\n<br><span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n通常，意识到自己可能遭受了<span style=\"letter-spacing: 1px;\">\n情绪暴力</span>的时候，我们往往已深陷其中，内心可能充满恐慌、抑郁、焦虑甚至自责的情绪。这个时候，不妨尝试以下这些做法：</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; box-sizing: border-box;\">\n<br><span style=\"font-weight: bolder;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n1. 理解与同情自己</span></span>\n</p>\n<p style=\"margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n你也许会发现，自己变得敏感、脆弱、精神紧绷，原本能做好的工作却无法顺利处理；你也许会怪自己真的不够努力。</span><br><br><span style=\"color: rgb(68, 68, 68);\">\n小编想说的是，长期遭受<span style=\"letter-spacing: 1px;\">\n情绪暴力</span>，有这样的感受和行为改变，都是正常的。</span><span style=\"font-weight: bolder; font-size: 15px; color: rgb(193, 88, 85);\">\n<span style=\"box-sizing: border-box;\">\n那不是你的错。</span></span><br>&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; box-sizing: border-box;\">\n<span style=\"font-weight: bolder;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n2. 公事公办</span></span>\n</p>\n<p style=\"margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(193, 88, 85);\">\n不要再与施暴者谈论任何有关自己私人生活的事情，或个人感受。</span><span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n有时候这并不容易做到，因为他们会故意套取这些信息，干涉你的个人生活，以进一步操纵你。<br><br>但设置边界对于工作是极其重要的，可能你会觉得“工作很难和生活分开”，但当工作和生活的重合度越低的时候，也许你会越少的被施暴者操纵。</span>\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; text-align: center;\">\n<img data-ratio=\"0.4444444444444444\" data-s=\"300,640\" data-type=\"png\" data-w=\"900\" src=\"https://jdxl-img.jdxlt.com/post/62b501b68a464703887f2344d4d3736a.png\" style=\"border-style: none; vertical-align: middle; max-width: 100%;\">\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; box-sizing: border-box;\">\n<span style=\"font-weight: bolder;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n3. 收集证据</span></span>\n</p>\n<p style=\"margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n可以单独找一个日记本，把发生的事情记下来。<span style=\"color: rgb(193, 88, 85); letter-spacing: 1px; line-height: 24px;\">\n存档与施暴者往来的所有电子邮件、纸质资料、微信记录。</span>一旦有一天在台面上与Ta对峙，你会需要这些材料作为证据。</span><br>&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; box-sizing: border-box;\">\n<span style=\"font-weight: bolder;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n4. 求助于信任的人</span></span>\n</p>\n<p style=\"margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; box-sizing: border-box;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n你可以把发生的事情告诉你的<span style=\"color: rgb(193, 88, 85); letter-spacing: 1px; line-height: 24px;\">\n家人，或是与工作无关的朋友，</span>前提是他们愿意理解你，而不会轻易做出评断。不要对与这份工作有交集的人倾诉，因为那可能会让情况恶化，也可能将你自己置于更加危险的境地。</span><br>&nbsp;\n</p>\n<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; box-sizing: border-box;\">\n<span style=\"font-weight: bolder;\">\n<span style=\"box-sizing: border-box; color: rgb(68, 68, 68);\">\n5. 求助于心理咨询</span></span>\n</p>\n<p style=\"margin-top: 15px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; box-sizing: border-box;\">\n<span style=\"color: rgb(68, 68, 68);\">\n之所以建议大家求助于心理咨询，是因为当我们把这些工作中的苦恼和他人倾诉时，很常不被理解，甚至会听到：“还是你不够坚强，要用工作成果证明自己；工作不都是这样吗；我跟你说你的老板不算什么，我的老板才奇葩呢”这样的反馈。</span><br><br><span style=\"color: rgb(193, 88, 85);\">\n而心理咨询师不会轻易作出评判，他们会倾听你的苦恼，帮助你缓解负面情绪，降低职场情绪暴力可能对你造成的负面影响。</span>\n</p> ','简单心理',23,4,1,'2020-07-04','2021-11-20 10:39:43','2021-11-20 10:39:43'),(16,'因疫情而过度焦虑怎么办？6个方法帮你快速调节情绪',3,'https://jdxl-img.jdxlt.com/post/44b18daf19654cbfaaef773bd6c3a383.jpg','摘要','<p line=\"7wMM\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n2020年的打开方式令我们每个人都意想不到。疫情的迅猛发展、春节假期的在家隔离、网络负面信息的不断轰炸，使我们每个人的生活都受到许多突如其来的影响。与此同时，<span style=\"font-weight: bolder;\">\n焦虑</span>的情绪也在不断蔓延，对我们及身边的人都有着或多或少的冲击。\n</p>\n<p line=\"37Fd\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<p line=\"As9v\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n那么，为什么人会陷入焦虑之中呢？今天我们邀请了梁娟和李敏楠两位咨询师为大家介绍<span style=\"font-weight: bolder;\">\n焦虑为什么会产生</span>以及当我们焦虑时，<span style=\"font-weight: bolder;\">\n如何快速自我调节</span>的6个方法。希望可以帮助大家在疫情期间更好地调适自己的焦虑情绪，也可以将这些方法分享给家人哦~<br><br>首先我们请梁娟咨询师为大家介绍一下焦虑产生的原因。<br>&nbsp;\n</p>\n<span style=\"color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; font-size: 20px;\">\n<span style=\"font-weight: bolder;\">\n<span style=\"color: rgb(255, 255, 255);\">\n<span style=\"background-color: rgb(26, 188, 156);\">\n&nbsp;为什么我们会焦虑？&nbsp;</span></span></span></span><br style=\"color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<br style=\"color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<span style=\"font-weight: bolder; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<span style=\"color: rgb(26, 188, 156);\">\n1. 焦虑情绪的本质</span></span><span style=\"color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n</span><p line=\"Vnvz\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<p line=\"S8pr\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<span style=\"font-weight: bolder;\">\n焦虑情绪的本质，是一种对于潜在失控的恐惧。</span>\n</p>\n<p line=\"G4ic\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<p line=\"laBt\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n如果要解剖焦虑，从上到下，可分为三层：\n</p>\n<p line=\"laBt\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; text-align: center;\">\n<img alt=\"\" src=\"https://jdxl-img.jdxlt.com/images/dd1641a6daa34caba9d50a7905727df0.png\" style=\"border-style: none; vertical-align: middle; max-width: 100%;\">\n</p>\n<p line=\"JrTL\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n下面来具体说一说。\n</p>\n<p line=\"7o4C\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<p line=\"OTJg\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n心理学家Freeston等人提出了“无法容忍不确定的程度”的概念，它被认为影响着“不确定”和“焦虑”之间的相互关系。\n</p>\n<p line=\"LLg7\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<p line=\"Eica\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<span style=\"font-weight: bolder;\">\n不确定性越大时，我们的焦虑程度就越高。</span>\n</p>\n<p line=\"8p79\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<p line=\"VWwd\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n当我们面对的情形是未知、不确定的时候，这会带给我们一种不在掌控之中的感觉，让我们觉得不安全。\n</p>\n<p line=\"GYqI\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<p line=\"GcuX\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n而面对一种潜在的失控，或不安全，我们所感到的焦虑，其实是人潜意识中的恐惧，甚至是危及生存的恐惧。\n</p>\n<p line=\"cMCn\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<p line=\"I68o\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n我们常常听说心理学上有个词叫分离焦虑。\n</p>\n<p line=\"I3GN\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<p line=\"3Op3\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n当一个孩子在很小的时候，面临和妈妈的分离，Ta会感到不安全，害怕独自一人在这个世界上生活，Ta感到自己没有能力去应对这个世界的危险。\n</p>\n<p line=\"4Ln4\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<p line=\"GwjB\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n我们经常看到一些小朋友在和妈妈分开时，哭得撕心裂肺，就好像和妈妈分开就会死掉一样。\n</p>\n<p line=\"mJge\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<p line=\"Iuvw\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n分离焦虑的本质是对被抛弃的恐惧。焦虑也是如此，在我们内心深处，有一种不易被察觉的恐惧情绪。<br>&nbsp;\n</p>\n<span style=\"font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; color: rgb(26, 188, 156);\">\n<span style=\"font-weight: bolder;\">\n2. 焦虑都是不好的吗？</span></span><span style=\"color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n</span><p line=\"xgQn\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<p line=\"8WUv\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n但是，焦虑情绪也并不都是不好的。\n</p>\n<p line=\"Dl6J\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<p line=\"cZ9p\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n美国心理学家耶基斯和多德森通过实验研究，发现了<span style=\"font-weight: bolder;\">\n耶基斯-多德森定律</span>。\n</p>\n<p line=\"Rxp9\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<p line=\"ZPKN\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n定律指出，动机强度和工作效率之间并不是线性关系，而是呈倒U形的曲线关系。\n</p>\n<p line=\"FIWt\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<p line=\"EhqZ\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n即动机强度处于中等时，也就是当我们保持在中等水平的焦虑状态时，工作和学习的效率最高。\n</p>\n<p line=\"pFn1\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<p line=\"ljb0\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n保持中等强度的焦虑，会让我们保持适度的兴奋感，会有一定的积极性去调动相应的身体机能去完成某项任务。\n</p>\n<p line=\"s5zu\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<p line=\"Hc8h\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n但是，动机强度过低或过高都不好。强度过低时，人们会缺乏做任务的积极性，工作效率不会高。强度过高时，个体又容易处于过度焦虑和紧张的状态，会干扰记忆、思维等正常的心理活动，也会降低工作效率。\n</p>\n<p line=\"gGUR\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<p line=\"lsCH\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n所以，<span style=\"font-weight: bolder;\">\n保持适度的焦虑，会帮助我们更高效的工作和学习</span>。而过度的焦虑，会造成我们效率变低。<br><br><a href=\"https://www.jiandanxinli.com/experts/124485?utm_source=position&amp;utm_medium=app_experts&amp;utm_campaign=zhuanti&amp;utm_content=zx_r&amp;utm_term=20200212\" style=\"text-decoration-line: none; color: rgb(57, 191, 183); -webkit-tap-highlight-color: transparent; background-color: transparent;\">\n<img alt=\"\" src=\"https://jdxl-img.jdxlt.com/images/8316028a4a9f43cc9ee1fec917573110.jpeg\" style=\"border-style: none; vertical-align: middle; max-width: 100%;\">\n</a><br><br>这么看来，当前疫情引发的焦虑主要是由于疫情给生活造成了太多的不确定性，令我们对潜在的失控内心恐惧。这个是在所难免的，也是大家普遍会感受到的。<br><br>那如何来缓解这种焦虑呢？下面我们请李敏楠咨询师为我们介绍一些可以自己在家试着调节情绪的方法吧~~<br>&nbsp;\n</p>\n<span style=\"font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; color: rgb(255, 255, 255);\">\n<span style=\"font-weight: bolder;\">\n<span style=\"font-size: 20px;\">\n<span style=\"background-color: rgb(26, 188, 156);\">\n&nbsp;6个调节焦虑情绪的方法&nbsp;</span></span></span></span><br style=\"color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<br style=\"color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<span style=\"font-weight: bolder; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<span style=\"color: rgb(26, 188, 156);\">\n1. 深呼吸</span></span><span style=\"color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n</span><p line=\"g1ls\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<br>当我们处于焦虑状态的时候，可以尝试用“深呼吸”技能来缓解焦虑情绪。这里推荐一个简单实用的“深呼吸法”：腹式呼吸法。这个方法能够有效缓解焦虑，因为它能激活身体的放松反应(the relaxation response)。它能帮助身体远离交感神经系统的“战或逃”反应，激活副交感神经系统的放松反应。\n</p>\n<p line=\"MuhC\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<p line=\"s2PI\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n具体方法如下：先尝试着慢慢吸气（同时心里从1默数到4），让新鲜空气先充满小腹，然后再慢慢扩充到胸部，接着暂停屏住呼吸（从1数到4），最后再慢慢地呼气（从1数到4）。整个过程可以重复几次。\n</p>\n<p line=\"jyql\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<span style=\"font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; color: rgb(26, 188, 156);\">\n<span style=\"font-weight: bolder;\">\n2.&nbsp;接受焦虑状态的存在</span></span><span style=\"color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n</span><p line=\"mAEJ\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<br>焦虑只是一种情绪。<span style=\"font-weight: bolder;\">\n情绪本身没有好坏，但人们对情绪的认知会影响情绪带来的结果。提醒自己焦虑只是一种情绪反应，这就是接纳的开始。</span>消除或对抗焦虑感只会使得自己状态变得更糟，它只会延续“焦虑是无法容忍的”的想法。\n</p>\n<p line=\"fshW\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<p line=\"xLod\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n接纳焦虑并不意味着你就喜欢焦虑或者把自己陷入痛苦中。它只是说当你接纳了事实，你会从中获益。即使焦虑的感觉依旧不那么理想，它也不是让人无法容忍的。\n</p>\n<p line=\"dU4o\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<span style=\"font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; color: rgb(26, 188, 156);\">\n<span style=\"font-weight: bolder;\">\n3.&nbsp;关注当下</span></span><span style=\"color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n</span><p line=\"YCQT\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<br>人们感到焦虑时，通常是对未来可能发生的小概率事件忧虑不断。所以，先对自己说“暂停”，然后呼吸并体会此时正在发生的事，将有助于缓解焦虑。即使糟糕的事情真的发生了，聚焦于当下也能有效帮助你提升能力应对危机。\n</p>\n<p line=\"BWIZ\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<span style=\"font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; color: rgb(26, 188, 156);\">\n<span style=\"font-weight: bolder;\">\n4.&nbsp;反思自己的想法</span></span><span style=\"color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n</span><p line=\"9NZ5\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<br>处于焦虑的时候，大脑就会迸发出很多想法，有些还会是<span style=\"font-weight: bolder;\">\n不切实际的想法</span>，而<span style=\"font-weight: bolder;\">\n这些想法会强调或者加深已有的焦虑状态</span>。\n</p>\n<p line=\"vCcm\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<p line=\"dA4l\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n比如，当你准备要去进行一场工作面试。你的脑海中可能会有这样的想法，“天呀，我做不到！”“我真是个失败的人！”。 然而，你要提醒自己，这并不是灾难。事实上，没有人会因一次面试而被定义为失败的人。你可能会感到焦虑，甚至会犯错。但是，就算在面试中犯错，最坏的情况也只是得不到这份工作，而不代表得不到所有工作机会。\n</p>\n<p line=\"64QB\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<p line=\"ZgSX\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n处于焦虑时，可以问自己以下问题：\n</p>\n<p line=\"lFuO\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<p line=\"0bU1\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<em>我这个担忧是真实的吗？</em>\n</p>\n<p line=\"FGNE\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<em>假如最坏的结果发生，那会怎么样？</em>\n</p>\n<p line=\"6Nqb\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<em>我能处理吗？</em>\n</p>\n<p line=\"TCdM\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<em>我能怎么做呢？</em>\n</p>\n<p line=\"psj3\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<em>如果不好的事情发生的话，对我意味着什么？</em>\n</p>\n<p line=\"pOYH\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<em>这是真实的吗？还是这只是看起来像这样的而已？</em>\n</p>\n<p line=\"14ad\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<em>我能准备什么来面对将来的事情呢？</em>\n</p>\n<p line=\"DCUi\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<span style=\"font-weight: bolder; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<span style=\"color: rgb(26, 188, 156);\">\n5.&nbsp;运用正念自我对话</span></span><span style=\"color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n</span><p line=\"LDOS\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<br>焦虑时，头脑中可能会出现很多负面的对话。你要意识到这些负性念头的存在，然后尝试用正面、积极的话语来自我对话来鼓励自己。比如，你可以对自己说：“这个焦虑情绪虽然让我感觉很不好，但是我有方法处理它！”引导我们的认知往正性方向进行。\n</p>\n<p line=\"y4hi\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<span style=\"font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px; color: rgb(26, 188, 156);\">\n<span style=\"font-weight: bolder;\">\n6.&nbsp;关注有意义的活动</span></span><span style=\"color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n</span><p line=\"yTuM\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<br>关注有意义且目标导向的活动也能帮助缓解焦虑。如果此刻你并非处于焦虑状态，那会做些什么呢？比如原本打算去看场电影，那就继续去电影院；如果原本打算去会见朋友，那就继续去赴约。最糟糕的做法就是被动地无所事事，不停地感受焦虑情绪。不要把注意力全部集中在焦虑本身，这对好转没有帮助。把原本计划完成的事情继续实施，这能帮助你明白即使焦虑也仍然能好好生活。\n</p>\n<p line=\"7oMj\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<p line=\"JVAF\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<p line=\"1HrW\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n<a href=\"https://www.jiandanxinli.com/experts/73259?utm_source=position&amp;utm_medium=app_experts&amp;utm_campaign=zhuanti&amp;utm_content=zx_r&amp;utm_term=20200212\" style=\"text-decoration-line: none; color: rgb(57, 191, 183); -webkit-tap-highlight-color: transparent; background-color: transparent;\">\n<img alt=\"\" src=\"https://jdxl-img.jdxlt.com/images/84543882f5c54f5e9346f7940759f1e1.jpeg\" style=\"border-style: none; vertical-align: middle; max-width: 100%;\">\n</a>\n</p>\n<p line=\"JMPH\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<p line=\"gHLt\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n疫情之下，焦虑很难避免。希望以上六种方法可以帮到处于焦虑中的你，即便不能面朝大海，依然能够心暖花开～\n</p>\n<p line=\"FTUJ\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<p line=\"OfPF\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n&nbsp;\n</p>\n<p line=\"kvFe\" style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(107, 112, 127); font-family: &quot;Hiragino Sans GB&quot;, 华文细黑, STHeiti, 微软雅黑, &quot;Microsoft YaHei&quot;, SimHei, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; letter-spacing: 0.3px;\">\n最后，值得注意的是，如果焦虑情绪持续时间很长，且也影响到了日常生活、工作、学业表现或者人际交往等，需要及时寻求专业帮助，心理咨询师或精神科医生都是值得考虑的选择。除了文中提及的三位心理咨询师，小编还挑选了6位擅长处理情绪压力的的心理咨询师，不妨<span style=\"color: rgb(26, 188, 156);\">\n<span style=\"font-weight: bolder;\">\n点击名片</span></span>，查看他们更多信息～<br><br><a href=\"https://www.jiandanxinli.com/experts/103719?utm_source=position&amp;utm_medium=app_experts&amp;utm_campaign=zhuanti&amp;utm_content=zx_r&amp;utm_term=20200212\" style=\"text-decoration-line: none; color: rgb(57, 191, 183); -webkit-tap-highlight-color: transparent; background-color: transparent;\">\n<img alt=\"\" src=\"https://jdxl-img.jdxlt.com/images/29819b16114b47a6a92a9797de6f4375.jpeg\" style=\"border-style: none; vertical-align: middle; max-width: 100%;\">\n</a><br><br><a href=\"https://www.jiandanxinli.com/experts/66065?utm_source=position&amp;utm_medium=app_experts&amp;utm_campaign=zhuanti&amp;utm_content=zx_r&amp;utm_term=20200212\" style=\"text-decoration-line: none; color: rgb(57, 191, 183); -webkit-tap-highlight-color: transparent; background-color: transparent;\">\n<img alt=\"\" src=\"https://jdxl-img.jdxlt.com/images/c3de6b0ea9fb48eeb51e901cb88191bb.jpeg\" style=\"border-style: none; vertical-align: middle; max-width: 100%;\">\n</a><br><br><a href=\"https://www.jiandanxinli.com/experts/62002?utm_source=position&amp;utm_medium=app_experts&amp;utm_campaign=zhuanti&amp;utm_content=zx_r&amp;utm_term=20200212\" style=\"text-decoration-line: none; color: rgb(57, 191, 183); -webkit-tap-highlight-color: transparent; background-color: transparent;\">\n<img alt=\"\" src=\"https://jdxl-img.jdxlt.com/images/c7dc1230c12a46cda1ba7acf1e28b257.jpeg\" style=\"border-style: none; vertical-align: middle; max-width: 100%;\">\n</a><br><br><a href=\"https://www.jiandanxinli.com/experts/61025?utm_source=position&amp;utm_medium=app_experts&amp;utm_campaign=zhuanti&amp;utm_content=zx_r&amp;utm_term=20200212\" style=\"text-decoration-line: none; color: rgb(57, 191, 183); -webkit-tap-highlight-color: transparent; background-color: transparent;\">\n<img alt=\"\" src=\"https://jdxl-img.jdxlt.com/images/d3f61f948ff54b498a59cdc9027520ce.jpeg\" style=\"border-style: none; vertical-align: middle; max-width: 100%;\">\n</a><br><br><a href=\"https://www.jiandanxinli.com/experts/81382?utm_source=position&amp;utm_medium=app_experts&amp;utm_campaign=zhuanti&amp;utm_content=zx_r&amp;utm_term=20200212\" style=\"text-decoration-line: none; color: rgb(57, 191, 183); -webkit-tap-highlight-color: transparent; background-color: transparent;\">\n<img alt=\"\" src=\"https://jdxl-img.jdxlt.com/images/77c13072e2dd4ef99ca11576a9ef5220.jpeg\" style=\"border-style: none; vertical-align: middle; max-width: 100%;\">\n</a><br><br><a href=\"https://www.jiandanxinli.com/experts/141075?utm_source=position&amp;utm_medium=app_experts&amp;utm_campaign=zhuanti&amp;utm_content=zx_r&amp;utm_term=20200212\" style=\"text-decoration-line: none; color: rgb(57, 191, 183); -webkit-tap-highlight-color: transparent; background-color: transparent; outline-width: 0px;\">\n<img alt=\"\" src=\"https://jdxl-img.jdxlt.com/images/985c3290f3f24bb5849250e802a60c3b.jpeg\" style=\"border-style: none; vertical-align: middle; max-width: 100%;\">\n</a>\n</p>','简单心理',44,28,1,'2021-05-14','2021-11-20 10:39:43','2021-11-20 10:39:43');
INSERT INTO `kno_info` VALUES (17,'抑郁症的本质是什么？人为什么会得抑郁症？',4,'http://dingyue.ws.126.net/aNLnHDv3viFSbtRzWp4E3rULdQP5lA7TJ3F3jXPsoPQze1556429417212compressflag.jpg','摘要','<p data-pid=\"iAlFM2d9\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n抑郁症既没有传染性，也不是经典的<span style=\"user-select: text !important;\">\n<a data-za-not-track-link=\"true\" href=\"https://www.zhihu.com/search?q=%E5%99%A8%E8%B4%A8%E6%80%A7%E7%96%BE%E7%97%85&amp;search_source=Entity&amp;hybrid_search_source=Entity&amp;hybrid_search_extra=%7B%22sourceType%22%3A%22answer%22%2C%22sourceId%22%3A30045243%7D\" target=\"_blank\" class=\"css-1occaib\" style=\"color: rgb(0, 102, 255); text-decoration-line: none; cursor: pointer; user-select: text !important;\">\n器质性疾病<svg width=\"8px\" height=\"8px\" viewBox=\"0 0 15 15\" class=\"css-ukqak1\">\n<path d=\"M10.89 9.477l3.06 3.059a1 1 0 0 1-1.414 1.414l-3.06-3.06a6 6 0 1 1 1.414-1.414zM6 10a4 4 0 1 0 0-8 4 4 0 0 0 0 8z\" fill=\"currentColor\">\n</path></svg></a></span>，抑郁症没有独特的的病因，表现多样，既有心理行为，也有生理方面，那抑郁症的本质到底是什么？\n</p>\n<p data-pid=\"ZaXpeXHr\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n先给一句话概述：抑郁症是一类（而不只是单一种）<span style=\"user-select: text !important; font-weight: 600;\">\n复杂疾病，</span>抑郁是有生理易感性基础，与外界环境发生相互作用，反馈到自身从而表现出一系列我们称之为“抑郁症状”的动态过程。抑郁症是一个庞大系统的综合表现，而不是单独一个基因、神经递质、神经细胞、<span style=\"user-select: text !important;\">\n<a data-za-not-track-link=\"true\" href=\"https://www.zhihu.com/search?q=%E5%BF%83%E7%90%86%E6%A8%A1%E5%BC%8F&amp;search_source=Entity&amp;hybrid_search_source=Entity&amp;hybrid_search_extra=%7B%22sourceType%22%3A%22answer%22%2C%22sourceId%22%3A30045243%7D\" target=\"_blank\" class=\"css-1occaib\" style=\"color: rgb(0, 102, 255); text-decoration-line: none; cursor: pointer; user-select: text !important;\">\n心理模式<svg width=\"8px\" height=\"8px\" viewBox=\"0 0 15 15\" class=\"css-ukqak1\">\n<path d=\"M10.89 9.477l3.06 3.059a1 1 0 0 1-1.414 1.414l-3.06-3.06a6 6 0 1 1 1.414-1.414zM6 10a4 4 0 1 0 0-8 4 4 0 0 0 0 8z\" fill=\"currentColor\">\n</path></svg></a></span>或者昨天失恋这样的环境事件单独能解决的。\n</p>\n<p data-pid=\"OH-60eRG\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<span style=\"user-select: text !important;\">\n<a data-za-not-track-link=\"true\" href=\"https://www.zhihu.com/search?q=%E5%9F%BA%E5%9B%A0%E7%BB%84&amp;search_source=Entity&amp;hybrid_search_source=Entity&amp;hybrid_search_extra=%7B%22sourceType%22%3A%22answer%22%2C%22sourceId%22%3A30045243%7D\" target=\"_blank\" class=\"css-1occaib\" style=\"color: rgb(0, 102, 255); text-decoration-line: none; cursor: pointer; user-select: text !important;\">\n基因组<svg width=\"8px\" height=\"8px\" viewBox=\"0 0 15 15\" class=\"css-ukqak1\">\n<path d=\"M10.89 9.477l3.06 3.059a1 1 0 0 1-1.414 1.414l-3.06-3.06a6 6 0 1 1 1.414-1.414zM6 10a4 4 0 1 0 0-8 4 4 0 0 0 0 8z\" fill=\"currentColor\">\n</path></svg></a></span>决定了生物个体，但是个体的大脑的发育和老化很大程度上取决于个体和外界的互动情况（经验），而每天身边会遇到的人或事是和作为个体的生物学本质没什么关系。也就是说，抑郁症通过<span style=\"user-select: text !important;\">\n<a data-za-not-track-link=\"true\" href=\"https://www.zhihu.com/search?q=%E8%BF%98%E5%8E%9F%E8%AE%BA&amp;search_source=Entity&amp;hybrid_search_source=Entity&amp;hybrid_search_extra=%7B%22sourceType%22%3A%22answer%22%2C%22sourceId%22%3A30045243%7D\" target=\"_blank\" class=\"css-1occaib\" style=\"color: rgb(0, 102, 255); text-decoration-line: none; cursor: pointer; user-select: text !important;\">\n还原论<svg width=\"8px\" height=\"8px\" viewBox=\"0 0 15 15\" class=\"css-ukqak1\">\n<path d=\"M10.89 9.477l3.06 3.059a1 1 0 0 1-1.414 1.414l-3.06-3.06a6 6 0 1 1 1.414-1.414zM6 10a4 4 0 1 0 0-8 4 4 0 0 0 0 8z\" fill=\"currentColor\">\n</path></svg></a></span>来理解一定是非常片面的。我会简单的叙述一下整个链条中一些关键因素：\n</p>\n<ul style=\"padding: 0px; margin: 0px 0px 1.4em; display: table; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<li data-pid=\"DH5fIf0S\" style=\"list-style: none; user-select: text !important; display: table-row;\">\n基因：对同卵/异卵双生子的对比研究表明，遗传因素在抑郁症中占到30%。5-HT转运体基因被研究较广泛，该基因有S和L两型，携带S等位基因（SS纯合或S/L杂合）可能意味着增加易感性。影响神经递质、神经发育、突触可塑性的基因也可能有一定作用。比如GWAS发现的rs2715148 和 rs2522833单核苷酸多态性（其作用是突触旁基质合成，可影响神经递质的释放和重吸收）；HOMER1 基因及其rs7713917 多态性影响大脑特定认知功能时候额叶的活动度。</li><br style=\"user-select: text !important;\">\n<li data-pid=\"WITDdLmb\" style=\"list-style: none; user-select: text !important; display: table-row;\">\n神经递质：包括5-HT1A、5-HT1B、去甲肾上腺素、GABA、谷氨酸，在合成、转运、发生生物效应等一系列相关过程都可以影响到抑郁症。也是经典的药物SSRI/SNRI作用靶点。</li><br style=\"user-select: text !important;\">\n<li data-pid=\"Gvs59AjB\" style=\"list-style: none; user-select: text !important; display: table-row;\">\n应激-肾上腺-下丘脑轴：古老的内分泌HPA轴。脑感受到应激后，<span style=\"user-select: text !important;\">\n<a data-za-not-track-link=\"true\" href=\"https://www.zhihu.com/search?q=%E4%B8%8B%E4%B8%98%E8%84%91&amp;search_source=Entity&amp;hybrid_search_source=Entity&amp;hybrid_search_extra=%7B%22sourceType%22%3A%22answer%22%2C%22sourceId%22%3A30045243%7D\" target=\"_blank\" class=\"css-1occaib\" style=\"color: rgb(0, 102, 255); text-decoration-line: none; cursor: pointer; user-select: text !important;\">\n下丘脑<svg width=\"8px\" height=\"8px\" viewBox=\"0 0 15 15\" class=\"css-ukqak1\">\n<path d=\"M10.89 9.477l3.06 3.059a1 1 0 0 1-1.414 1.414l-3.06-3.06a6 6 0 1 1 1.414-1.414zM6 10a4 4 0 1 0 0-8 4 4 0 0 0 0 8z\" fill=\"currentColor\">\n</path></svg></a></span>释放肾上腺皮质释放激素（CRH），传递到肾上腺再分泌<span style=\"user-select: text !important;\">\n<a data-za-not-track-link=\"true\" href=\"https://www.zhihu.com/search?q=%E8%82%BE%E4%B8%8A%E8%85%BA%E7%9A%AE%E8%B4%A8%E6%BF%80%E7%B4%A0&amp;search_source=Entity&amp;hybrid_search_source=Entity&amp;hybrid_search_extra=%7B%22sourceType%22%3A%22answer%22%2C%22sourceId%22%3A30045243%7D\" target=\"_blank\" class=\"css-1occaib\" style=\"color: rgb(0, 102, 255); text-decoration-line: none; cursor: pointer; user-select: text !important;\">\n肾上腺皮质激素<svg width=\"8px\" height=\"8px\" viewBox=\"0 0 15 15\" class=\"css-ukqak1\">\n<path d=\"M10.89 9.477l3.06 3.059a1 1 0 0 1-1.414 1.414l-3.06-3.06a6 6 0 1 1 1.414-1.414zM6 10a4 4 0 1 0 0-8 4 4 0 0 0 0 8z\" fill=\"currentColor\">\n</path></svg></a></span>。皮质激素反过来可以抑制大脑海马的神经元再生，皮质激素增高、海马缩小、海马活动度改变等现象都可以在部分病人上观察到。</li><br style=\"user-select: text !important;\">\n<li data-pid=\"f6THSbNq\" style=\"list-style: none; user-select: text !important; display: table-row;\">\n细胞和组织层面：有一个重要的蛋白叫做脑源性生长因子（BDNF），对于神经细胞的发生、发育、神经可塑性至关重要。人脑中只有海马和纹状体有神经再生，除了刚才说到<span style=\"user-select: text !important;\">\n<a data-za-not-track-link=\"true\" href=\"https://www.zhihu.com/search?q=%E7%9A%AE%E8%B4%A8%E9%86%87&amp;search_source=Entity&amp;hybrid_search_source=Entity&amp;hybrid_search_extra=%7B%22sourceType%22%3A%22answer%22%2C%22sourceId%22%3A30045243%7D\" target=\"_blank\" class=\"css-1occaib\" style=\"color: rgb(0, 102, 255); text-decoration-line: none; cursor: pointer; user-select: text !important;\">\n皮质醇<svg width=\"8px\" height=\"8px\" viewBox=\"0 0 15 15\" class=\"css-ukqak1\">\n<path d=\"M10.89 9.477l3.06 3.059a1 1 0 0 1-1.414 1.414l-3.06-3.06a6 6 0 1 1 1.414-1.414zM6 10a4 4 0 1 0 0-8 4 4 0 0 0 0 8z\" fill=\"currentColor\">\n</path></svg></a></span>可以导致神经再生减少，在应激条件下BDNF基因多态性也可以导致。</li></ul><blockquote data-pid=\"7ptisRFu\" style=\"margin: 0px 0px 1.4em; padding-left: 1em; color: rgb(100, 100, 100); border-left: 3px solid rgb(211, 211, 211); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<figure style=\"user-select: text !important; margin: 1.4em 0px;\">\n<img src=\"https://pica.zhimg.com/80/76d32935b01095af5f8cd853d0a30d93_720w.jpg?source=1940ef5c\" data-rawwidth=\"798\" data-rawheight=\"800\" class=\"origin_image zh-lightbox-thumb lazy\" width=\"798\" data-original=\"https://pic1.zhimg.com/76d32935b01095af5f8cd853d0a30d93_r.jpg?source=1940ef5c\" data-actualsrc=\"https://pica.zhimg.com/50/76d32935b01095af5f8cd853d0a30d93_720w.jpg?source=1940ef5c\" data-lazy-status=\"ok\" style=\"user-select: text !important; display: block; max-width: 100%; margin: 0px auto; cursor: zoom-in; background-color: transparent; animation: 0.5s ease-in 0s 1 normal none running fxRichTextFadeIn;\">\n</figure><i style=\"user-select: text !important;\">\nR.H. Belmaker, (2008). N Engl J Med 358(1): 14</i></blockquote><ul style=\"padding: 0px; margin: 1.4em 0px; display: table; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<li data-pid=\"4qB6q_qd\" style=\"list-style: none; user-select: text !important; display: table-row;\">\n脑：富含5-HT受体的扣带回膝下皮层（sgACC）在抑郁症病人中表现出了多种异常，包括不限于：<span style=\"user-select: text !important;\">\n<a data-za-not-track-link=\"true\" href=\"https://www.zhihu.com/search?q=%E7%A5%9E%E7%BB%8F%E8%83%B6%E8%B4%A8%E7%BB%86%E8%83%9E&amp;search_source=Entity&amp;hybrid_search_source=Entity&amp;hybrid_search_extra=%7B%22sourceType%22%3A%22answer%22%2C%22sourceId%22%3A30045243%7D\" target=\"_blank\" class=\"css-1occaib\" style=\"color: rgb(0, 102, 255); text-decoration-line: none; cursor: pointer; user-select: text !important;\">\n神经胶质细胞<svg width=\"8px\" height=\"8px\" viewBox=\"0 0 15 15\" class=\"css-ukqak1\">\n<path d=\"M10.89 9.477l3.06 3.059a1 1 0 0 1-1.414 1.414l-3.06-3.06a6 6 0 1 1 1.414-1.414zM6 10a4 4 0 1 0 0-8 4 4 0 0 0 0 8z\" fill=\"currentColor\">\n</path></svg></a></span>减少，突触减少，耗能，<span style=\"user-select: text !important;\">\n<a data-za-not-track-link=\"true\" href=\"https://www.zhihu.com/search?q=%E6%B4%BB%E5%8A%A8%E5%BA%A6&amp;search_source=Entity&amp;hybrid_search_source=Entity&amp;hybrid_search_extra=%7B%22sourceType%22%3A%22answer%22%2C%22sourceId%22%3A30045243%7D\" target=\"_blank\" class=\"css-1occaib\" style=\"color: rgb(0, 102, 255); text-decoration-line: none; cursor: pointer; user-select: text !important;\">\n活动度<svg width=\"8px\" height=\"8px\" viewBox=\"0 0 15 15\" class=\"css-ukqak1\">\n<path d=\"M10.89 9.477l3.06 3.059a1 1 0 0 1-1.414 1.414l-3.06-3.06a6 6 0 1 1 1.414-1.414zM6 10a4 4 0 1 0 0-8 4 4 0 0 0 0 8z\" fill=\"currentColor\">\n</path></svg></a></span>改变。sgACC又是包含在对情绪和行为有直接影响的一套脑环路系统中，其他重要的节点还包括：背外侧额叶（DLPFC），背内侧额叶（DMPFC），皮质下灰质核团（纹状体和丘脑），后扣带，杏仁核和海马以及小脑。这些区域是通过多种实验条件下（比如给病人看不同包含情绪内容的图片）得到的。这一些列的发现也直接促成了新一代诊断及治疗的工具，比如直接通过脑扫描来进行诊断，通过实时fMRI学习如何调控情绪，通过rTMS刺激DLPFC治疗对药物无效者。</li><li data-pid=\"fSpDaa4u\" style=\"list-style: none; user-select: text !important; display: table-row;\">\n脑与心理：前面说了很多不同的脑区是通过实验研究发现的。总结而言抑郁症病人的心理活动反映到神经活动上就是：对于负面情绪过度反应，对高兴等情绪却毫无反应，情绪调节能力严重下降；<span style=\"user-select: text !important;\">\n<a data-za-not-track-link=\"true\" href=\"https://www.zhihu.com/search?q=%E8%AE%A4%E7%9F%A5%E5%8A%9F%E8%83%BD&amp;search_source=Entity&amp;hybrid_search_source=Entity&amp;hybrid_search_extra=%7B%22sourceType%22%3A%22answer%22%2C%22sourceId%22%3A30045243%7D\" target=\"_blank\" class=\"css-1occaib\" style=\"color: rgb(0, 102, 255); text-decoration-line: none; cursor: pointer; user-select: text !important;\">\n认知功能<svg width=\"8px\" height=\"8px\" viewBox=\"0 0 15 15\" class=\"css-ukqak1\">\n<path d=\"M10.89 9.477l3.06 3.059a1 1 0 0 1-1.414 1.414l-3.06-3.06a6 6 0 1 1 1.414-1.414zM6 10a4 4 0 1 0 0-8 4 4 0 0 0 0 8z\" fill=\"currentColor\">\n</path></svg></a></span>及相关神经活动亦有减退。对来自外部环境的负面信号反应过激，而自身又无法如正常人一样摆脱其干扰。某些性格特质（高neuroticism）可能也是抑郁症的易感因素。<br style=\"user-select: text !important;\">\n</li></ul><p data-pid=\"JCW5Q207\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n注意力的偏曲（尤其对情绪相关的信息），对情绪信息的不正常再加工产生的<span style=\"user-select: text !important;\">\n<a data-za-not-track-link=\"true\" href=\"https://www.zhihu.com/search?q=%E5%81%8F%E6%9B%B2%E8%AE%B0%E5%BF%86&amp;search_source=Entity&amp;hybrid_search_source=Entity&amp;hybrid_search_extra=%7B%22sourceType%22%3A%22answer%22%2C%22sourceId%22%3A30045243%7D\" target=\"_blank\" class=\"css-1occaib\" style=\"color: rgb(0, 102, 255); text-decoration-line: none; cursor: pointer; user-select: text !important;\">\n偏曲记忆<svg width=\"8px\" height=\"8px\" viewBox=\"0 0 15 15\" class=\"css-ukqak1\">\n<path d=\"M10.89 9.477l3.06 3.059a1 1 0 0 1-1.414 1.414l-3.06-3.06a6 6 0 1 1 1.414-1.414zM6 10a4 4 0 1 0 0-8 4 4 0 0 0 0 8z\" fill=\"currentColor\">\n</path></svg></a></span>和自我沉思，异常的心理模式，这一套是抑郁症的主要心理表现，是抑郁症研究和心理治疗界大佬前辈牛人AT Beck所提出。\n</p>\n<blockquote data-pid=\"RqT2X1o6\" style=\"margin: 1.4em 0px; padding-left: 1em; color: rgb(100, 100, 100); border-left: 3px solid rgb(211, 211, 211); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<figure style=\"user-select: text !important; margin: 1.4em 0px;\">\n<img src=\"https://pic1.zhimg.com/80/a8ac81d258746108b5539630174e07ba_720w.jpg?source=1940ef5c\" data-rawwidth=\"855\" data-rawheight=\"526\" class=\"origin_image zh-lightbox-thumb lazy\" width=\"855\" data-original=\"https://pic2.zhimg.com/a8ac81d258746108b5539630174e07ba_r.jpg?source=1940ef5c\" data-actualsrc=\"https://pic1.zhimg.com/50/a8ac81d258746108b5539630174e07ba_720w.jpg?source=1940ef5c\" data-lazy-status=\"ok\" style=\"user-select: text !important; display: block; max-width: 100%; margin: 0px auto; cursor: zoom-in; background-color: transparent; animation: 0.5s ease-in 0s 1 normal none running fxRichTextFadeIn;\">\n</figure><i style=\"user-select: text !important;\">\nDisner, S. G., et al. (2011). Nat Rev Neurosci 12(8): 467-477.</i></blockquote><ul style=\"padding: 0px; margin: 1.4em 0px; display: table; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<li data-pid=\"MJoatcET\" style=\"list-style: none; user-select: text !important; display: table-row;\">\n社会环境：<br style=\"user-select: text !important;\">\n</li></ul><ol style=\"padding: 0px; margin: 0px 0px 1.4em; display: table; counter-reset: ol 0; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<li data-pid=\"WAS7TFI1\" style=\"list-style: none; user-select: text !important; display: table-row;\">\n家庭：婚姻问题是很多人抑郁的直接触发诱因。对于儿童来说尤其重要，家庭中遭受的应激，乃至暴力事件，为之后青春期/成年后爆发抑郁症埋下了一颗定时炸弹。<br style=\"user-select: text !important;\">\n</li><li data-pid=\"sNJJWFqZ\" style=\"list-style: none; user-select: text !important; display: table-row;\">\n经济水平：和所谓『常识』刚好相反，收入越低越容易得抑郁症。在城市化之前的中国，农村的抑郁症发病率远高于城市，体现在少量的研究报告以及官方都承认的农村高自杀率上；这一局面被近些年大规模的城市化进程给逆转了，多个研究报告认为中国近几年自杀率大幅下降，归功于城市化进程。以后会怎样还未知。<br style=\"user-select: text !important;\">\n</li><li data-pid=\"r4wC7uD2\" style=\"list-style: none; user-select: text !important; display: table-row;\">\n性别：女性患者数量达到了男性的三倍之多。除开生理因素，女生在其他方面要承受压力等，严重的比如性侵犯，程度不同可能非常多见，在某些流派的心理治疗中可能是关注点。女性平均收入低，女性在异性关系之中会面对一些不对等的处境。女性在成长过程中承受来额外自社会家庭的压力。女性和男性在在自尊、自我概念以及应对社交的方式可能有差异，是抑郁的风险因素。<br style=\"user-select: text !important;\">\n</li>\n</ol><br style=\"margin-top: 0px; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<p data-pid=\"m3hoe7Kk\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n总结：\n</p>\n<p data-pid=\"IzBa59w2\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n抑郁症是典型的<span style=\"user-select: text !important; font-weight: 600;\">\n复杂疾病</span>，从基因到环境，环环相扣，相互反馈。抑郁症是一大类疾病，每各个体可能在整个环节中的薄弱点不尽相同却又各种相似。为什么得抑郁症？因为我们是有复杂心理的人类，因为我们创造了复杂的世界，因为我们生活在其中。世界是不完美的所以我们有了抑郁症。抑郁症的复杂性决定了抑郁症需要接受科学的、系统的治疗方法。\n</p>','外星菜鸟',333,24,0,'2014-09-02','2021-11-20 10:39:43','2021-12-09 11:22:05'),(18,'为什么女性更容易得抑郁症？有没有科学依据？',4,'https://pic1.zhimg.com/v2-ad7f152bf0e118bf64707452ff3eb025_1440w.jpg','摘要','<p data-pid=\"gO4dMDb3\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n根据我们对5000名付费来访者的数据进行统计分析发现，男女平均面临的困扰数量没有显著差异，<span style=\"user-select: text !important; font-weight: 600 !important;\">\n男性面临的<span style=\"user-select: text !important;\">\n<a data-za-not-track-link=\"true\" href=\"https://www.zhihu.com/search?q=%E5%BF%83%E7%90%86%E5%9B%B0%E6%89%B0&amp;search_source=Entity&amp;hybrid_search_source=Entity&amp;hybrid_search_extra=%7B%22sourceType%22%3A%22answer%22%2C%22sourceId%22%3A%22145595236%22%7D\" target=\"_blank\" class=\"css-1occaib\" style=\"color: rgb(0, 102, 255); text-decoration-line: none; cursor: pointer; user-select: text !important;\">\n心理困扰<svg width=\"8px\" height=\"8px\" viewBox=\"0 0 15 15\" class=\"css-ukqak1\">\n<path d=\"M10.89 9.477l3.06 3.059a1 1 0 0 1-1.414 1.414l-3.06-3.06a6 6 0 1 1 1.414-1.414zM6 10a4 4 0 1 0 0-8 4 4 0 0 0 0 8z\" fill=\"currentColor\">\n</path></svg></a></span>和女性几乎一样多</span>。\n</p>\n<figure data-size=\"normal\" style=\"margin: 0px 0px 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<img src=\"https://pica.zhimg.com/80/v2-6166cb34e7aef513cd93801e513508e2_720w.jpg\" data-caption=\"\" data-size=\"normal\" data-rawwidth=\"726\" data-rawheight=\"527\" class=\"origin_image zh-lightbox-thumb lazy\" width=\"726\" data-original=\"https://pica.zhimg.com/v2-6166cb34e7aef513cd93801e513508e2_r.jpg\" data-actualsrc=\"https://pica.zhimg.com/v2-6166cb34e7aef513cd93801e513508e2_b.jpg\" data-lazy-status=\"ok\" style=\"user-select: text !important; display: block; max-width: 100%; margin: 0px auto; cursor: zoom-in;\">\n</figure><p data-pid=\"dDIdj3FZ\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n甚至<span style=\"user-select: text !important; font-weight: 600 !important;\">\n男女所面临的心理困扰种类的前三名都是一样的</span>，只是排名顺序不同，男性更重视自身的职业、学习、工作压力等方面的困扰，女性则更注重自身的情绪问题。\n</p>\n<p data-pid=\"WRyxaWGW\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n但我们发现男性和女性在面临心理困扰时，<span style=\"user-select: text !important; font-weight: 600 !important;\">\n最大的不同是他们选择求助的比例有显著差异：</span>\n</p>\n<figure data-size=\"normal\" style=\"margin: 1.4em 0px; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<img src=\"https://pic2.zhimg.com/80/v2-451a293be23f22e377f4c1880c6460f1_720w.jpg\" data-caption=\"\" data-size=\"normal\" data-rawwidth=\"618\" data-rawheight=\"272\" class=\"origin_image zh-lightbox-thumb lazy\" width=\"618\" data-original=\"https://pic2.zhimg.com/v2-451a293be23f22e377f4c1880c6460f1_r.jpg\" data-actualsrc=\"https://pic2.zhimg.com/v2-451a293be23f22e377f4c1880c6460f1_b.jpg\" data-lazy-status=\"ok\" style=\"user-select: text !important; display: block; max-width: 100%; margin: 0px auto; cursor: zoom-in;\">\n</figure><p data-pid=\"kQ0AJRoK\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n在心理困扰数量、种类都相似的情况下，<span style=\"user-select: text !important; font-weight: 600 !important;\">\n男性更不愿意求助</span>，在简单心理的来访者中，<span style=\"user-select: text !important; font-weight: 600 !important;\">\n男性来访者只占26%</span>。这种存在问题却不求助的现状，导致男性可能面临更大的心理健康风险，也造成了题主观察到的这种男性更少表达的现象。\n</p>\n<figure data-size=\"normal\" style=\"margin: 1.4em 0px; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<img src=\"https://pic1.zhimg.com/80/v2-a4ca0f168bf40614e808864add6ae268_720w.jpg\" data-caption=\"\" data-size=\"normal\" data-rawwidth=\"684\" data-rawheight=\"10\" class=\"origin_image zh-lightbox-thumb lazy\" width=\"684\" data-original=\"https://pic1.zhimg.com/v2-a4ca0f168bf40614e808864add6ae268_r.jpg\" data-actualsrc=\"https://pic1.zhimg.com/v2-a4ca0f168bf40614e808864add6ae268_b.jpg\" data-lazy-status=\"ok\" style=\"user-select: text !important; display: block; max-width: 100%; margin: 0px auto; cursor: zoom-in;\">\n</figure><p class=\"ztext-empty-paragraph\" style=\"margin-top: -0.8em; margin-bottom: -0.8em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<br style=\"user-select: text !important;\">\n</p>\n<p data-pid=\"4KBAmpXM\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<span style=\"user-select: text !important; font-weight: 600 !important;\">\n我想对这方面多聊一点，男性不愿求助的部分原因要归于两性的性别刻板印象（<span style=\"user-select: text !important;\">\n<a data-za-not-track-link=\"true\" href=\"https://www.zhihu.com/search?q=gender+stereotypes&amp;search_source=Entity&amp;hybrid_search_source=Entity&amp;hybrid_search_extra=%7B%22sourceType%22%3A%22answer%22%2C%22sourceId%22%3A%22145595236%22%7D\" target=\"_blank\" class=\"css-1occaib\" style=\"color: rgb(0, 102, 255); text-decoration-line: none; cursor: pointer; user-select: text !important;\">\ngender stereotypes<svg width=\"8px\" height=\"8px\" viewBox=\"0 0 15 15\" class=\"css-ukqak1\">\n<path d=\"M10.89 9.477l3.06 3.059a1 1 0 0 1-1.414 1.414l-3.06-3.06a6 6 0 1 1 1.414-1.414zM6 10a4 4 0 1 0 0-8 4 4 0 0 0 0 8z\" fill=\"currentColor\">\n</path></svg></a></span>），社会和文化通常会将一些特质强加于性别之上，比如：</span>\n</p>\n<figure data-size=\"normal\" style=\"margin: 1.4em 0px; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<img src=\"https://pic2.zhimg.com/80/v2-70559826bd80932adc7ab935bd2983c9_720w.jpg\" data-caption=\"\" data-size=\"normal\" data-rawwidth=\"785\" data-rawheight=\"348\" class=\"origin_image zh-lightbox-thumb lazy\" width=\"785\" data-original=\"https://pic2.zhimg.com/v2-70559826bd80932adc7ab935bd2983c9_r.jpg\" data-actualsrc=\"https://pic2.zhimg.com/v2-70559826bd80932adc7ab935bd2983c9_b.jpg\" data-lazy-status=\"ok\" style=\"user-select: text !important; display: block; max-width: 100%; margin: 0px auto; cursor: zoom-in;\">\n</figure><p data-pid=\"4P8wIAd8\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n男性被赋予了“坚强、内敛、不敏感”等特质，而女性则更“脆弱、情绪化、敏感”，<span style=\"user-select: text !important; font-weight: 600 !important;\">\n这是对性别的一种极其简单粗暴的两极化划分。</span>\n</p>\n<p data-pid=\"cG4SHp7c\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<span style=\"user-select: text !important; font-weight: 600 !important;\">\n正是这种刻板印象，才导致了男性更少发出声音的现状。</span>\n</p>\n<figure data-size=\"normal\" style=\"margin: 1.4em 0px; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<img src=\"https://pic1.zhimg.com/80/v2-a4ca0f168bf40614e808864add6ae268_720w.jpg\" data-caption=\"\" data-size=\"normal\" data-rawwidth=\"684\" data-rawheight=\"10\" class=\"origin_image zh-lightbox-thumb lazy\" width=\"684\" data-original=\"https://pic1.zhimg.com/v2-a4ca0f168bf40614e808864add6ae268_r.jpg\" data-actualsrc=\"https://pic1.zhimg.com/v2-a4ca0f168bf40614e808864add6ae268_b.jpg\" data-lazy-status=\"ok\" style=\"user-select: text !important; display: block; max-width: 100%; margin: 0px auto; cursor: zoom-in;\">\n</figure><p data-pid=\"duIXES0B\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<span style=\"user-select: text !important; font-weight: 600 !important;\">\n社会告诉你，你要 “Be a man”</span>\n</p>\n<p data-pid=\"WkW9D3F9\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n一个男孩的成长过程，几乎就是<span style=\"user-select: text !important;\">\n<a data-za-not-track-link=\"true\" href=\"https://www.zhihu.com/search?q=%E6%80%A7%E5%88%AB%E5%88%BB%E6%9D%BF%E5%8D%B0%E8%B1%A1&amp;search_source=Entity&amp;hybrid_search_source=Entity&amp;hybrid_search_extra=%7B%22sourceType%22%3A%22answer%22%2C%22sourceId%22%3A%22145595236%22%7D\" target=\"_blank\" class=\"css-1occaib\" style=\"color: rgb(0, 102, 255); text-decoration-line: none; cursor: pointer; user-select: text !important;\">\n性别刻板印象<svg width=\"8px\" height=\"8px\" viewBox=\"0 0 15 15\" class=\"css-ukqak1\">\n<path d=\"M10.89 9.477l3.06 3.059a1 1 0 0 1-1.414 1.414l-3.06-3.06a6 6 0 1 1 1.414-1.414zM6 10a4 4 0 1 0 0-8 4 4 0 0 0 0 8z\" fill=\"currentColor\">\n</path></svg></a></span>不断习得和加深的过程，当一个小男孩摔倒了，他的父母可能对他说“坚强点，不要哭，你是个男孩”；当他受委屈或者不顺心了，可能被教训说：“男孩就得大度点，你这么小心眼儿怎么行。”\n</p>\n<p data-pid=\"iJgqZlVf\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n多数家庭、教育甚至社会文化氛围赞扬的是坚强、独立解决问题的男性形象，<span style=\"user-select: text !important; font-weight: 600 !important;\">\n当男孩表达自己的情绪时，会受到责备，而当我们表现出沉默和坚强时则会受到赞赏。</span>\n</p>\n<figure data-size=\"normal\" style=\"margin: 1.4em 0px; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<img src=\"https://pic4.zhimg.com/80/v2-51ba7d791e702582825667a9a42e491f_720w.jpg\" data-caption=\"\" data-size=\"normal\" data-rawwidth=\"564\" data-rawheight=\"314\" class=\"origin_image zh-lightbox-thumb lazy\" width=\"564\" data-original=\"https://pic4.zhimg.com/v2-51ba7d791e702582825667a9a42e491f_r.jpg\" data-actualsrc=\"https://pic4.zhimg.com/v2-51ba7d791e702582825667a9a42e491f_b.jpg\" data-lazy-status=\"ok\" style=\"user-select: text !important; display: block; max-width: 100%; margin: 0px auto; cursor: zoom-in;\">\n</figure><p data-pid=\"SyTyXAb7\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n所以男性越来越压抑自己的情绪和感受，当他们遭受心理困扰时，很可能归结于这只是一时的心烦，或是对自己说“我一个大老爷们，有什么过不去的”。\n</p>\n<p data-pid=\"tWxsQk9G\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n他们就像从小被教导的那样，保持着安静和沉默，<span style=\"user-select: text !important; font-weight: 600 !important;\">\n而不诉说，最终导致了不会诉说。</span>\n</p>\n<figure data-size=\"normal\" style=\"margin: 1.4em 0px; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<img src=\"https://pic1.zhimg.com/80/v2-a4ca0f168bf40614e808864add6ae268_720w.jpg\" data-caption=\"\" data-size=\"normal\" data-rawwidth=\"684\" data-rawheight=\"10\" class=\"origin_image zh-lightbox-thumb lazy\" width=\"684\" data-original=\"https://pic1.zhimg.com/v2-a4ca0f168bf40614e808864add6ae268_r.jpg\" data-actualsrc=\"https://pic1.zhimg.com/v2-a4ca0f168bf40614e808864add6ae268_b.jpg\" data-lazy-status=\"ok\" style=\"user-select: text !important; display: block; max-width: 100%; margin: 0px auto; cursor: zoom-in;\">\n</figure><p data-pid=\"KqantjI2\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<span style=\"user-select: text !important; font-weight: 600 !important;\">\n个人会将刻板印象内化为信念，认为求助是承认自己的脆弱</span>\n</p>\n<p data-pid=\"KlxkfBTo\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n简单心理的《报告》中调查了人们对于心理问题的误解，发现<span style=\"user-select: text !important; font-weight: 600 !important;\">\n46.2%的人认为心理脆弱的人才会有心理问题</span>，<span style=\"user-select: text !important; font-weight: 600 !important;\">\n19.8%的来访者认为使用<span style=\"user-select: text !important;\">\n<a data-za-not-track-link=\"true\" href=\"https://www.zhihu.com/search?q=%E5%BF%83%E7%90%86%E5%92%A8%E8%AF%A2&amp;search_source=Entity&amp;hybrid_search_source=Entity&amp;hybrid_search_extra=%7B%22sourceType%22%3A%22answer%22%2C%22sourceId%22%3A%22145595236%22%7D\" target=\"_blank\" class=\"css-1occaib\" style=\"color: rgb(0, 102, 255); text-decoration-line: none; cursor: pointer; user-select: text !important;\">\n心理咨询<svg width=\"8px\" height=\"8px\" viewBox=\"0 0 15 15\" class=\"css-ukqak1\">\n<path d=\"M10.89 9.477l3.06 3.059a1 1 0 0 1-1.414 1.414l-3.06-3.06a6 6 0 1 1 1.414-1.414zM6 10a4 4 0 1 0 0-8 4 4 0 0 0 0 8z\" fill=\"currentColor\">\n</path></svg></a></span>是内心软弱的表现。</span>\n</p>\n<p data-pid=\"Q86QURVU\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n其他研究也发现，当人们遭受抑郁的困扰时，相比于女性，男性对于寻求专业的心理帮助是更<span style=\"user-select: text !important; font-weight: 600 !important;\">\n抗拒</span>也更<span style=\"user-select: text !important; font-weight: 600 !important;\">\n不愿意说出口</span>的，他们倾向于遮掩自己的病情。\n</p>\n<figure data-size=\"normal\" style=\"margin: 1.4em 0px; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<img src=\"https://pic4.zhimg.com/80/v2-8913d2048f92c64261b09ffbe29b3a04_720w.jpg\" data-caption=\"\" data-size=\"normal\" data-rawwidth=\"564\" data-rawheight=\"310\" class=\"origin_image zh-lightbox-thumb lazy\" width=\"564\" data-original=\"https://pic4.zhimg.com/v2-8913d2048f92c64261b09ffbe29b3a04_r.jpg\" data-actualsrc=\"https://pic4.zhimg.com/v2-8913d2048f92c64261b09ffbe29b3a04_b.jpg\" data-lazy-status=\"ok\" style=\"user-select: text !important; display: block; max-width: 100%; margin: 0px auto; cursor: zoom-in;\">\n</figure><p data-pid=\"RXsLdjl4\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n由于坚强的男性刻板印象在他们心中根深蒂固，所以当他们认为心理问题与“脆弱”相连，做心理咨询与“内心软弱”等同时，男性是害怕求助的。\n</p>\n<p data-pid=\"lw-gisDg\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<span style=\"user-select: text !important; font-weight: 600 !important;\">\n因为求助似乎就变相承认了自己的脆弱、无力解决问题，似乎他从小建立起来的<span style=\"user-select: text !important;\">\n<a data-za-not-track-link=\"true\" href=\"https://www.zhihu.com/search?q=%E7%8B%AC%E7%AB%8B%E5%9D%9A%E5%BC%BA&amp;search_source=Entity&amp;hybrid_search_source=Entity&amp;hybrid_search_extra=%7B%22sourceType%22%3A%22answer%22%2C%22sourceId%22%3A%22145595236%22%7D\" target=\"_blank\" class=\"css-1occaib\" style=\"color: rgb(0, 102, 255); text-decoration-line: none; cursor: pointer; user-select: text !important;\">\n独立坚强<svg width=\"8px\" height=\"8px\" viewBox=\"0 0 15 15\" class=\"css-ukqak1\">\n<path d=\"M10.89 9.477l3.06 3.059a1 1 0 0 1-1.414 1.414l-3.06-3.06a6 6 0 1 1 1.414-1.414zM6 10a4 4 0 1 0 0-8 4 4 0 0 0 0 8z\" fill=\"currentColor\">\n</path></svg></a></span>的男性形象会就此崩塌，或是他们自认为求助是一件值得羞耻的事，这些原因让他们不愿去寻求专业的治疗。</span>\n</p>\n<p data-pid=\"KYpzQrci\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<span style=\"user-select: text !important; font-weight: 600 !important;\">\n但我们需要着重强调的是，主动求助才是勇敢的表现。</span>\n</p>\n<figure data-size=\"normal\" style=\"margin: 1.4em 0px; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<img src=\"https://pic1.zhimg.com/80/v2-a4ca0f168bf40614e808864add6ae268_720w.jpg\" data-caption=\"\" data-size=\"normal\" data-rawwidth=\"684\" data-rawheight=\"10\" class=\"origin_image zh-lightbox-thumb lazy\" width=\"684\" data-original=\"https://pic1.zhimg.com/v2-a4ca0f168bf40614e808864add6ae268_r.jpg\" data-actualsrc=\"https://pic1.zhimg.com/v2-a4ca0f168bf40614e808864add6ae268_b.jpg\" data-lazy-status=\"ok\" style=\"user-select: text !important; display: block; max-width: 100%; margin: 0px auto; cursor: zoom-in;\">\n</figure><p data-pid=\"-Ovy-c35\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n我们想跟这些不愿表达，害怕求助的男性说的话：\n</p>\n<p data-pid=\"DIHsqXGj\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n在<span style=\"user-select: text !important;\">\n<a data-za-not-track-link=\"true\" href=\"https://www.zhihu.com/search?q=%E5%BF%83%E7%90%86%E9%A2%86%E5%9F%9F&amp;search_source=Entity&amp;hybrid_search_source=Entity&amp;hybrid_search_extra=%7B%22sourceType%22%3A%22answer%22%2C%22sourceId%22%3A%22145595236%22%7D\" target=\"_blank\" class=\"css-1occaib\" style=\"color: rgb(0, 102, 255); text-decoration-line: none; cursor: pointer; user-select: text !important;\">\n心理领域<svg width=\"8px\" height=\"8px\" viewBox=\"0 0 15 15\" class=\"css-ukqak1\">\n<path d=\"M10.89 9.477l3.06 3.059a1 1 0 0 1-1.414 1.414l-3.06-3.06a6 6 0 1 1 1.414-1.414zM6 10a4 4 0 1 0 0-8 4 4 0 0 0 0 8z\" fill=\"currentColor\">\n</path></svg></a></span>，尤其是<span style=\"user-select: text !important; font-weight: 600 !important;\">\n心理咨询领域，早已走在了去性别化的最前面</span>，心理咨询对不同性别的来访者有一个通用的共识：\n</p>\n<p data-pid=\"B5PR_5vz\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n“男性和女性的相似之处要远多于他们相异之处，他们都对生活有相似的渴求，都在寻求爱、尊严、成就和自我实现，在认定他们是男性或女性之前，先肯定他们是一个个独特的个体”\n</p>\n<p data-pid=\"HWTashaq\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n如果你被<span style=\"user-select: text !important;\">\n<a data-za-not-track-link=\"true\" href=\"https://www.zhihu.com/search?q=%E5%BF%83%E7%90%86%E9%97%AE%E9%A2%98&amp;search_source=Entity&amp;hybrid_search_source=Entity&amp;hybrid_search_extra=%7B%22sourceType%22%3A%22answer%22%2C%22sourceId%22%3A%22145595236%22%7D\" target=\"_blank\" class=\"css-1occaib\" style=\"color: rgb(0, 102, 255); text-decoration-line: none; cursor: pointer; user-select: text !important;\">\n心理问题<svg width=\"8px\" height=\"8px\" viewBox=\"0 0 15 15\" class=\"css-ukqak1\">\n<path d=\"M10.89 9.477l3.06 3.059a1 1 0 0 1-1.414 1.414l-3.06-3.06a6 6 0 1 1 1.414-1.414zM6 10a4 4 0 1 0 0-8 4 4 0 0 0 0 8z\" fill=\"currentColor\">\n</path></svg></a></span>所困扰，这很正常，世界上98.1%的人和你一样面临着困扰。<span style=\"user-select: text !important; font-weight: 600 !important;\">\n不要假装问题不存在，肯去寻求帮助已经是一件能证明你勇气的事情了</span>，它绝不可耻。<span style=\"user-select: text !important;\">\n<a data-za-not-track-link=\"true\" href=\"https://www.zhihu.com/search?q=%E5%92%A8%E8%AF%A2%E5%B8%88&amp;search_source=Entity&amp;hybrid_search_source=Entity&amp;hybrid_search_extra=%7B%22sourceType%22%3A%22answer%22%2C%22sourceId%22%3A%22145595236%22%7D\" target=\"_blank\" class=\"css-1occaib\" style=\"color: rgb(0, 102, 255); text-decoration-line: none; cursor: pointer; user-select: text !important;\">\n咨询师<svg width=\"8px\" height=\"8px\" viewBox=\"0 0 15 15\" class=\"css-ukqak1\">\n<path d=\"M10.89 9.477l3.06 3.059a1 1 0 0 1-1.414 1.414l-3.06-3.06a6 6 0 1 1 1.414-1.414zM6 10a4 4 0 1 0 0-8 4 4 0 0 0 0 8z\" fill=\"currentColor\">\n</path></svg></a></span>不会Judge你，而只是无条件的积极关注你、关注你的困扰与情绪。\n</p>\n<p class=\"ztext-empty-paragraph\" style=\"margin-top: -0.8em; margin-bottom: -0.8em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<br style=\"user-select: text !important;\">\n</p>\n<p data-pid=\"SSozAJrV\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<span style=\"user-select: text !important; font-weight: 600 !important;\">\n因为我们并不在乎你是什么性别的人，</span>\n</p>\n<p data-pid=\"0vKw84KE\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<span style=\"user-select: text !important; font-weight: 600 !important;\">\n更在乎你是谁，你的感受是什么。</span>\n</p>','简单心理',467,55,1,'2019-04-12','2021-11-20 10:39:43','2021-11-20 10:39:43'),(19,'抑郁症患者的饮食禁忌',5,'https://pic3.zhimg.com/v2-dd5dfd5d37c7aefb59c109f843613b3d_1440w.jpg','摘要','<h2 style=\"margin: 0px 0px calc(1.16667em); font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 1.2em; line-height: 1.5; font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; clear: left; color: rgb(18, 18, 18);\">\n抑郁症患者饮食禁忌</h2><p data-pid=\"VKUqpxlu\" style=\"margin-top: 0px; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: medium;\">\n<span style=\"font-weight: 600;\">\n1、炒米：</span>\n</p>\n<p data-pid=\"AQRbn8eY\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: medium;\">\n也就是不能吃<a data-za-not-track-link=\"true\" href=\"https://www.zhihu.com/search?q=%E7%88%86%E7%B1%B3%E8%8A%B1&amp;search_source=Entity&amp;hybrid_search_source=Entity&amp;hybrid_search_extra=%7B%22sourceType%22%3A%22article%22%2C%22sourceId%22%3A288003102%7D\" target=\"_blank\" class=\"css-1occaib\" style=\"color: rgb(0, 102, 255); text-decoration-line: none; cursor: pointer;\">\n爆米花<svg width=\"8px\" height=\"8px\" viewBox=\"0 0 15 15\" class=\"css-ukqak1\">\n<path d=\"M10.89 9.477l3.06 3.059a1 1 0 0 1-1.414 1.414l-3.06-3.06a6 6 0 1 1 1.414-1.414zM6 10a4 4 0 1 0 0-8 4 4 0 0 0 0 8z\" fill=\"currentColor\">\n</path></svg></a>。因为这些食物属于香燥伤阴食物，阴虚火旺的<a data-za-not-track-link=\"true\" href=\"https://www.zhihu.com/search?q=%E6%9B%B4%E5%B9%B4%E6%9C%9F%E7%BB%BC%E5%90%88%E7%97%87&amp;search_source=Entity&amp;hybrid_search_source=Entity&amp;hybrid_search_extra=%7B%22sourceType%22%3A%22article%22%2C%22sourceId%22%3A288003102%7D\" target=\"_blank\" class=\"css-1occaib\" style=\"color: rgb(0, 102, 255); text-decoration-line: none; cursor: pointer;\">\n更年期综合症<svg width=\"8px\" height=\"8px\" viewBox=\"0 0 15 15\" class=\"css-ukqak1\">\n<path d=\"M10.89 9.477l3.06 3.059a1 1 0 0 1-1.414 1.414l-3.06-3.06a6 6 0 1 1 1.414-1.414zM6 10a4 4 0 1 0 0-8 4 4 0 0 0 0 8z\" fill=\"currentColor\">\n</path></svg></a>者忌食之。\n</p>\n<p data-pid=\"gDxf8yCk\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: medium;\">\n<span style=\"font-weight: 600;\">\n2、辣椒：</span>\n</p>\n<p data-pid=\"jZy4i3Wq\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: medium;\">\n因为辣椒是性大热、味辛辣的食物，也就是一种大辛大热的刺激性食品，吃完之后极易伤阴动火。而抑郁症患者多多属<a data-za-not-track-link=\"true\" href=\"https://www.zhihu.com/search?q=%E8%82%9D%E8%82%BE%E9%98%B4%E8%99%9A&amp;search_source=Entity&amp;hybrid_search_source=Entity&amp;hybrid_search_extra=%7B%22sourceType%22%3A%22article%22%2C%22sourceId%22%3A288003102%7D\" target=\"_blank\" class=\"css-1occaib\" style=\"color: rgb(0, 102, 255); text-decoration-line: none; cursor: pointer;\">\n肝肾阴虚<svg width=\"8px\" height=\"8px\" viewBox=\"0 0 15 15\" class=\"css-ukqak1\">\n<path d=\"M10.89 9.477l3.06 3.059a1 1 0 0 1-1.414 1.414l-3.06-3.06a6 6 0 1 1 1.414-1.414zM6 10a4 4 0 1 0 0-8 4 4 0 0 0 0 8z\" fill=\"currentColor\">\n</path></svg></a>，内火偏旺，所以，辣椒尤当忌吃。\n</p>\n<p data-pid=\"Nz2FysQ-\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: medium;\">\n<span style=\"font-weight: 600;\">\n3、酒：</span>\n</p>\n<p data-pid=\"aSr37Eu-\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: medium;\">\n一般抑郁症患者在患病期间要尽可能的少喝酒。饮酒过量会使人更加情绪低落，所谓借酒消愁愁更愁，喝酒会扼制食欲，造成营养不良，会加重抑郁病情有损健康。\n</p>\n<p data-pid=\"OOUzmmmW\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: medium;\">\n<span style=\"font-weight: 600;\">\n4、酪氨酸含量高的食物和饮料：</span>\n</p>\n<p data-pid=\"jHP1dUU5\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: medium;\">\n常见的就是<a data-za-not-track-link=\"true\" href=\"https://www.zhihu.com/search?q=%E4%B9%B3%E9%85%AA&amp;search_source=Entity&amp;hybrid_search_source=Entity&amp;hybrid_search_extra=%7B%22sourceType%22%3A%22article%22%2C%22sourceId%22%3A288003102%7D\" target=\"_blank\" class=\"css-1occaib\" style=\"color: rgb(0, 102, 255); text-decoration-line: none; cursor: pointer;\">\n乳酪<svg width=\"8px\" height=\"8px\" viewBox=\"0 0 15 15\" class=\"css-ukqak1\">\n<path d=\"M10.89 9.477l3.06 3.059a1 1 0 0 1-1.414 1.414l-3.06-3.06a6 6 0 1 1 1.414-1.414zM6 10a4 4 0 1 0 0-8 4 4 0 0 0 0 8z\" fill=\"currentColor\">\n</path></svg></a>，罐头肉，酱油，酵母提取物，<a data-za-not-track-link=\"true\" href=\"https://www.zhihu.com/search?q=%E9%B2%B1%E9%B1%BC&amp;search_source=Entity&amp;hybrid_search_source=Entity&amp;hybrid_search_extra=%7B%22sourceType%22%3A%22article%22%2C%22sourceId%22%3A288003102%7D\" target=\"_blank\" class=\"css-1occaib\" style=\"color: rgb(0, 102, 255); text-decoration-line: none; cursor: pointer;\">\n鲱鱼<svg width=\"8px\" height=\"8px\" viewBox=\"0 0 15 15\" class=\"css-ukqak1\">\n<path d=\"M10.89 9.477l3.06 3.059a1 1 0 0 1-1.414 1.414l-3.06-3.06a6 6 0 1 1 1.414-1.414zM6 10a4 4 0 1 0 0-8 4 4 0 0 0 0 8z\" fill=\"currentColor\">\n</path></svg></a>和鲑鱼等，这些都是抑郁症患者在饮食上要禁忌的。\n</p>\n<p data-pid=\"VZxtPnXA\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: medium;\">\n<span style=\"font-weight: 600;\">\n5、丁香：</span>\n</p>\n<p data-pid=\"GD8-wOQK\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: medium;\">\n丁香指我们生活中常见的调味品，这种调味品是温性的，如果患者长期服用这种物质，会给情绪带来一定的影响，可能导致内火偏旺，因此一定要控制这种物质的摄入量。\n</p>\n<p data-pid=\"n2Av8Tpi\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: medium;\">\n<span style=\"font-weight: 600;\">\n6、<a data-za-not-track-link=\"true\" href=\"https://www.zhihu.com/search?q=%E6%A1%82%E7%9A%AE&amp;search_source=Entity&amp;hybrid_search_source=Entity&amp;hybrid_search_extra=%7B%22sourceType%22%3A%22article%22%2C%22sourceId%22%3A288003102%7D\" target=\"_blank\" class=\"css-1occaib\" style=\"color: rgb(0, 102, 255); text-decoration-line: none; cursor: pointer;\">\n桂皮<svg width=\"8px\" height=\"8px\" viewBox=\"0 0 15 15\" class=\"css-ukqak1\">\n<path d=\"M10.89 9.477l3.06 3.059a1 1 0 0 1-1.414 1.414l-3.06-3.06a6 6 0 1 1 1.414-1.414zM6 10a4 4 0 1 0 0-8 4 4 0 0 0 0 8z\" fill=\"currentColor\">\n</path></svg></a>：</span>\n</p>\n<p data-pid=\"6LmvibSe\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: medium;\">\n这是一种调味的佐料。性大热，味辛甘，主要就是有益火温阳的功效，但又有性热助火、香燥伤阴，辛散动血之弊。所以一般抑郁症患者切勿多食。误食之，势必加剧内热炽盛的病情。\n</p>\n<p data-pid=\"yMLGwvtl\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: medium;\">\n<span style=\"font-weight: 600;\">\n7、茶和咖啡：</span>\n</p>\n<p data-pid=\"NoUyNU5z\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: medium;\">\n一旦抑郁症患者摄取的<a data-za-not-track-link=\"true\" href=\"https://www.zhihu.com/search?q=%E5%92%96%E5%95%A1%E5%9B%A0&amp;search_source=Entity&amp;hybrid_search_source=Entity&amp;hybrid_search_extra=%7B%22sourceType%22%3A%22article%22%2C%22sourceId%22%3A288003102%7D\" target=\"_blank\" class=\"css-1occaib\" style=\"color: rgb(0, 102, 255); text-decoration-line: none; cursor: pointer;\">\n咖啡因<svg width=\"8px\" height=\"8px\" viewBox=\"0 0 15 15\" class=\"css-ukqak1\">\n<path d=\"M10.89 9.477l3.06 3.059a1 1 0 0 1-1.414 1.414l-3.06-3.06a6 6 0 1 1 1.414-1.414zM6 10a4 4 0 1 0 0-8 4 4 0 0 0 0 8z\" fill=\"currentColor\">\n</path></svg></a>太多(每天喝四杯以上咖啡或六杯以上的茶)，那么就有可能会加重病情。茶，<a data-za-not-track-link=\"true\" href=\"https://www.zhihu.com/search?q=%E5%8F%AF%E4%B9%90&amp;search_source=Entity&amp;hybrid_search_source=Entity&amp;hybrid_search_extra=%7B%22sourceType%22%3A%22article%22%2C%22sourceId%22%3A288003102%7D\" target=\"_blank\" class=\"css-1occaib\" style=\"color: rgb(0, 102, 255); text-decoration-line: none; cursor: pointer;\">\n可乐<svg width=\"8px\" height=\"8px\" viewBox=\"0 0 15 15\" class=\"css-ukqak1\">\n<path d=\"M10.89 9.477l3.06 3.059a1 1 0 0 1-1.414 1.414l-3.06-3.06a6 6 0 1 1 1.414-1.414zM6 10a4 4 0 1 0 0-8 4 4 0 0 0 0 8z\" fill=\"currentColor\">\n</path></svg></a>和咖啡都会加重抑郁症患者的失眠症状，因此患者在睡觉前不应喝茶或咖啡。\n</p>\n<p data-pid=\"JxTkmOgG\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: medium;\">\n<span style=\"font-weight: 600;\">\n8、加工食品和甜食：</span>\n</p>\n<p data-pid=\"oWIs4g8N\" style=\"margin-top: 1.4em; margin-bottom: 0px; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: medium;\">\n常见的有<a data-za-not-track-link=\"true\" href=\"https://www.zhihu.com/search?q=%E6%B1%89%E5%A0%A1%E5%8C%85&amp;search_source=Entity&amp;hybrid_search_source=Entity&amp;hybrid_search_extra=%7B%22sourceType%22%3A%22article%22%2C%22sourceId%22%3A288003102%7D\" target=\"_blank\" class=\"css-1occaib\" style=\"color: rgb(0, 102, 255); text-decoration-line: none; cursor: pointer;\">\n汉堡包<svg width=\"8px\" height=\"8px\" viewBox=\"0 0 15 15\" class=\"css-ukqak1\">\n<path d=\"M10.89 9.477l3.06 3.059a1 1 0 0 1-1.414 1.414l-3.06-3.06a6 6 0 1 1 1.414-1.414zM6 10a4 4 0 1 0 0-8 4 4 0 0 0 0 8z\" fill=\"currentColor\">\n</path></svg></a>、薯条、炸鸡等，这些吃多了都是对抑郁症的病情不利的。\n</p>','心理医生黄药师',345,98,0,'2020-11-12','2021-11-20 10:39:43','2021-12-09 11:20:59');
INSERT INTO `kno_info` VALUES (20,'最有助于对抗抑郁症的 7 种食物',5,'https://pic3.zhimg.com/v2-2baf0017ebcc277ddc492a63ee9c1a76_b.jpg','摘要','<p data-pid=\"lg4VYFgS\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n虽然有成百上千的文章、饮食策略、政府机构和食品制造商致力于提供如何对抗疾病和改善健康的信息，但你可能会惊讶地发现，在这一专题研究之前，直到 2007 年，还没有人关注大脑健康或精神疾病。\n</p>\n<p data-pid=\"s6JDupXG\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n2018 年 9 月，研究人员报告了一项研究的结果，该研究的重点是寻找有助于对抗抑郁症的最佳食物。R. LaChance 博士和一支多伦多大学团队，以及哥伦比亚大学内科和外科医学院精神病学系的 Drew Ramsey 共同研究了这项计划。据这项研究称：\n</p>\n<p data-pid=\"usvrbhNA\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<span style=\"user-select: text !important; font-weight: 600 !important;\">\n<i style=\"user-select: text !important;\">\n“我们利用<span style=\"user-select: text !important;\">\n<a data-za-not-track-link=\"true\" href=\"https://www.zhihu.com/search?q=%E8%AF%81%E6%8D%AE%E7%AD%89%E7%BA%A7%E6%A0%87%E5%87%86&amp;search_source=Entity&amp;hybrid_search_source=Entity&amp;hybrid_search_extra=%7B%22sourceType%22%3A%22article%22%2C%22sourceId%22%3A%2259851840%22%7D\" target=\"_blank\" class=\"css-1occaib\" style=\"color: rgb(0, 102, 255); text-decoration-line: none; cursor: pointer; user-select: text !important;\">\n证据等级标准<svg width=\"8px\" height=\"8px\" viewBox=\"0 0 15 15\" class=\"css-ukqak1\">\n<path d=\"M10.89 9.477l3.06 3.059a1 1 0 0 1-1.414 1.414l-3.06-3.06a6 6 0 1 1 1.414-1.414zM6 10a4 4 0 1 0 0-8 4 4 0 0 0 0 8z\" fill=\"currentColor\">\n</path></svg></a></span>进行了系统的文献综述，以便从已知的</i></span>&nbsp;<span style=\"user-select: text !important; font-weight: 600 !important;\">\n<i style=\"user-select: text !important;\">\n34</i></span>&nbsp;<span style=\"user-select: text !important; font-weight: 600 !important;\">\n<i style=\"user-select: text !important;\">\n种人体必需营养素中提取一份抗抑郁营养素列表。</i></span>\n</p>\n<p data-pid=\"oCSwbMoC\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<span style=\"user-select: text !important; font-weight: 600 !important;\">\n<i style=\"user-select: text !important;\">\n使用美国农业部（USDA）的数据库，提取了至少一种抗抑郁营养素含量高的食物子集的营养数据。对这些食物进行抗抑郁营养密度分析，得出抗抑郁食物评分（AFS）。”</i></span>\n</p>\n<p data-pid=\"pHY_sCgQ\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n以 AFS 为基础的抗抑郁食物列表以豆瓣菜为首，豆瓣菜是得分最高的植物性食品，得分为 127%，得分最高的动物性食品是牡蛎，得分为 56%，如下所示。\n</p>\n<p data-pid=\"XhtL-Der\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n抑郁症的流行，以及对这类疾病管理不足的潜在费用和现状，是编制 AFS 列表的驱动力。研究人员强调，每种最佳食物都可以纳入任何一种饮食计划。\n</p>\n<p data-pid=\"24YiNdYe\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n你可能会注意到，并不是所有食物均为世界上每个人所熟悉；举例来说，居住在美国的人，特别是历史上记载的人，发现双壳类食物或海鲜并非唾手可得，就像某些地区的人们可能无法获得绿叶蔬菜或其他蔬菜一样。\n</p>\n<p data-pid=\"r1_EHdZ6\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n然而，可获得性并不总是意味着人们可以很容易地获得健康食物；事实上，居住在美国的大多数成年人无法达到建议的每日蔬菜摄入量。旨在增加蔬菜消费和推广其他健康习惯的“健康国民 2010”计划显示，只有 27.2% 的人每天吃三份或更多蔬菜。\n</p>\n<p data-pid=\"gICLz-MS\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<span style=\"user-select: text !important; font-weight: 600 !important;\">\n哪些营养素最能对抗抑郁症？</span>\n</p>\n<p data-pid=\"HB8rIwDd\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n科学家们的结论是，研究人员在设计未来的干预研究时，以及临床医生在制定有助于预防抑郁症的饮食选择时，应该考虑最主要的抗抑郁营养素。他们认为对这类疾病最有效的 12 种抗抑郁营养素是：\n</p>\n<p data-pid=\"wndUe4S1\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n叶酸\n</p>\n<p data-pid=\"eH0J2PyX\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n铁\n</p>\n<p data-pid=\"prwwEzCM\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n长链 ω-3 脂肪酸（EPA 和 DHA）\n</p>\n<p data-pid=\"lK2wfrmM\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n镁\n</p>\n<p data-pid=\"DTp27F75\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n钾\n</p>\n<p data-pid=\"_b-G9RZ0\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n硒\n</p>\n<p data-pid=\"JQcvH3wj\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<span style=\"user-select: text !important;\">\n<a data-za-not-track-link=\"true\" href=\"https://www.zhihu.com/search?q=%E7%A1%AB%E8%83%BA%E7%B4%A0&amp;search_source=Entity&amp;hybrid_search_source=Entity&amp;hybrid_search_extra=%7B%22sourceType%22%3A%22article%22%2C%22sourceId%22%3A%2259851840%22%7D\" target=\"_blank\" class=\"css-1occaib\" style=\"color: rgb(0, 102, 255); text-decoration-line: none; cursor: pointer; user-select: text !important;\">\n硫胺素<svg width=\"8px\" height=\"8px\" viewBox=\"0 0 15 15\" class=\"css-ukqak1\">\n<path d=\"M10.89 9.477l3.06 3.059a1 1 0 0 1-1.414 1.414l-3.06-3.06a6 6 0 1 1 1.414-1.414zM6 10a4 4 0 1 0 0-8 4 4 0 0 0 0 8z\" fill=\"currentColor\">\n</path></svg></a></span>\n</p>\n<p data-pid=\"D0SXjA5Y\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n维生素 A\n</p>\n<p data-pid=\"lF-YHKjE\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n维生素 B6\n</p>\n<p data-pid=\"NtdozitZ\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n维生素 B12\n</p>\n<p data-pid=\"-TBjJrdd\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n维生素 C\n</p>\n<p data-pid=\"hjZCXFhC\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n锌\n</p>\n<p data-pid=\"69kpuYIE\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<span style=\"user-select: text !important; font-weight: 600 !important;\">\n精神疾病的流行和带来希望的“处方”</span>\n</p>\n<p data-pid=\"yjPvlxYV\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\nLaChance 博士和 Ramsey 的研究指出，在 15 至 44 岁的人群中，包括抑郁症在内的精神疾病是导致残疾的主要原因。增加治疗方案，包括考虑“抗抑郁”食物，应视为“当务之急”，以应对与此类疾病斗争的人数不断增加的问题。此外：\n</p>\n<p data-pid=\"atH877fJ\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<span style=\"user-select: text !important; font-weight: 600 !important;\">\n<i style=\"user-select: text !important;\">\n“越来越多的证据基础，包括第一个随机对照试验，表明饮食模式和食物选择可能在治疗和预防大脑疾病，特别是抑郁症方面发挥作用……他们建议遵循传统的饮食模式，如地中海饮食……避免加工食品，比如那些精制碳水化合物或糖含量较高的食品。</i></span>\n</p>\n<p data-pid=\"ejTyJUSY\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<span style=\"user-select: text !important; font-weight: 600 !important;\">\n<i style=\"user-select: text !important;\">\n此外，一个由精神健康和营养研究人员组成的国际联盟最近建议将”营养精神病学”作为精神健康临床实践的常规组成部分。”</i></span>\n</p>\n<p data-pid=\"Mk9MZDa9\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n他们研究的基础部分是围绕一项<span style=\"user-select: text !important;\">\n<a data-za-not-track-link=\"true\" href=\"https://www.zhihu.com/search?q=%E8%8D%9F%E8%90%83%E5%88%86%E6%9E%90&amp;search_source=Entity&amp;hybrid_search_source=Entity&amp;hybrid_search_extra=%7B%22sourceType%22%3A%22article%22%2C%22sourceId%22%3A%2259851840%22%7D\" target=\"_blank\" class=\"css-1occaib\" style=\"color: rgb(0, 102, 255); text-decoration-line: none; cursor: pointer; user-select: text !important;\">\n荟萃分析<svg width=\"8px\" height=\"8px\" viewBox=\"0 0 15 15\" class=\"css-ukqak1\">\n<path d=\"M10.89 9.477l3.06 3.059a1 1 0 0 1-1.414 1.414l-3.06-3.06a6 6 0 1 1 1.414-1.414zM6 10a4 4 0 1 0 0-8 4 4 0 0 0 0 8z\" fill=\"currentColor\">\n</path></svg></a></span>展开的，这项分析涉及澳大利亚、西班牙、芬兰、英国和法国的科学家。该研究的目的是通过饮食建议，以及从 2017 年开始的一项名为“SMILES（微笑）”的随机对照试验，来解决此类疾病。\n</p>\n<p data-pid=\"3-nnh9P-\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\nSMILES 是 Supporting the Modification of Lifestyle in Lowered Emotional States（支持在情绪低落时改变生活方式）的首字母缩写，这个试验由来自澳大利亚全国各地的神经科学、精神病学、饮食、医学中心和其他研究中心的专家合作完成。得出结论的前提是“改善饮食也许可以为这种高度流行的精神疾病的管理提供一种有效和可行的治疗策略。”\n</p>\n<p data-pid=\"NcWC_jMI\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n它还指出，研究一个人吃什么和不吃什么之间的联系，很可能会影响相关死亡人数。然而，将营养精神病学作为“精神健康临床实践的常规部分”的想法源自 LaChance 和 Ramsey 对《柳叶刀·精神病学》的基本观察的应用：\n</p>\n<p data-pid=\"FY1go5Dm\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<span style=\"user-select: text !important; font-weight: 600 !important;\">\n<i style=\"user-select: text !important;\">\n“有证据表明，营养是精神疾病高患病率和高发病率的关键因素，这表明饮食对精神病学的重要性不亚于对<span style=\"user-select: text !important;\">\n<a data-za-not-track-link=\"true\" href=\"https://www.zhihu.com/search?q=%E5%BF%83%E8%84%8F%E7%97%85%E5%AD%A6&amp;search_source=Entity&amp;hybrid_search_source=Entity&amp;hybrid_search_extra=%7B%22sourceType%22%3A%22article%22%2C%22sourceId%22%3A%2259851840%22%7D\" target=\"_blank\" class=\"css-1occaib\" style=\"color: rgb(0, 102, 255); text-decoration-line: none; cursor: pointer; user-select: text !important;\">\n心脏病学<svg width=\"8px\" height=\"8px\" viewBox=\"0 0 15 15\" class=\"css-ukqak1\">\n<path d=\"M10.89 9.477l3.06 3.059a1 1 0 0 1-1.414 1.414l-3.06-3.06a6 6 0 1 1 1.414-1.414zM6 10a4 4 0 1 0 0-8 4 4 0 0 0 0 8z\" fill=\"currentColor\">\n</path></svg></a></span>、内分泌学和胃肠病学的影响。</i></span>\n</p>\n<p data-pid=\"C3yWTo3q\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<span style=\"user-select: text !important; font-weight: 600 !important;\">\n<i style=\"user-select: text !important;\">\n证明饮食质量（以及潜在的营养不足）和精神健康之间的关系的证据越来越多，以及选择性地使用以营养为基础的补充剂来解决营养不足，或作为单一疗法或强化疗法。”</i></span>\n</p>\n<p data-pid=\"0V8ImXfv\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<span style=\"user-select: text !important; font-weight: 600 !important;\">\n抑郁症吃什么食物好：有益于心理健康的得分最高的植物性食物</span>\n</p>\n<p data-pid=\"3YPfOPqr\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n科学家在这一专题研究过程中进行的一项重要观察是，缺乏长链 ω-3 脂肪酸、B 族维生素、锌、镁和维生素 D，毫无疑问是与抑郁症的“病理生理学”有关的。\n</p>\n<p data-pid=\"_dkTAWkM\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n更具体地说，食物对炎症的影响，以及膳食纤维对肠道菌群的影响，是研究对心理健康最有益的食物的两个重要因素。其中一个来源来自一项研究，该研究关注的是与降低慢性疾病风险联系最紧密的“能量”水果和蔬菜。也就是说，抗抑郁得分最高的植物性食物包括：\n</p>\n<p data-pid=\"F3whg5dt\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n· 绿叶蔬菜— 豆瓣菜、菠菜、芥末、萝卜、菊苣和甜菜叶、唐莴苣、<span style=\"user-select: text !important;\">\n<a data-za-not-track-link=\"true\" href=\"https://www.zhihu.com/search?q=%E8%92%B2%E5%85%AC%E8%8B%B1&amp;search_source=Entity&amp;hybrid_search_source=Entity&amp;hybrid_search_extra=%7B%22sourceType%22%3A%22article%22%2C%22sourceId%22%3A%2259851840%22%7D\" target=\"_blank\" class=\"css-1occaib\" style=\"color: rgb(0, 102, 255); text-decoration-line: none; cursor: pointer; user-select: text !important;\">\n蒲公英<svg width=\"8px\" height=\"8px\" viewBox=\"0 0 15 15\" class=\"css-ukqak1\">\n<path d=\"M10.89 9.477l3.06 3.059a1 1 0 0 1-1.414 1.414l-3.06-3.06a6 6 0 1 1 1.414-1.414zM6 10a4 4 0 1 0 0-8 4 4 0 0 0 0 8z\" fill=\"currentColor\">\n</path></svg></a></span>、羽衣甘蓝叶以及香菜、罗勒、欧芹和<span style=\"user-select: text !important;\">\n<a data-za-not-track-link=\"true\" href=\"https://www.zhihu.com/search?q=%E7%BE%BD%E8%A1%A3%E7%94%98%E8%93%9D&amp;search_source=Entity&amp;hybrid_search_source=Entity&amp;hybrid_search_extra=%7B%22sourceType%22%3A%22article%22%2C%22sourceId%22%3A%2259851840%22%7D\" target=\"_blank\" class=\"css-1occaib\" style=\"color: rgb(0, 102, 255); text-decoration-line: none; cursor: pointer; user-select: text !important;\">\n羽衣甘蓝<svg width=\"8px\" height=\"8px\" viewBox=\"0 0 15 15\" class=\"css-ukqak1\">\n<path d=\"M10.89 9.477l3.06 3.059a1 1 0 0 1-1.414 1.414l-3.06-3.06a6 6 0 1 1 1.414-1.414zM6 10a4 4 0 1 0 0-8 4 4 0 0 0 0 8z\" fill=\"currentColor\">\n</path></svg></a></span>\n</p>\n<p data-pid=\"tizD_1dB\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n· 生菜— 红色、绿色和长叶莴苣\n</p>\n<p data-pid=\"sVh5jkFx\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n· 辣椒 — 灯笼椒、塞拉诺辣椒和<span style=\"user-select: text !important;\">\n<a data-za-not-track-link=\"true\" href=\"https://www.zhihu.com/search?q=%E5%A2%A8%E8%A5%BF%E5%93%A5%E8%BE%A3%E6%A4%92&amp;search_source=Entity&amp;hybrid_search_source=Entity&amp;hybrid_search_extra=%7B%22sourceType%22%3A%22article%22%2C%22sourceId%22%3A%2259851840%22%7D\" target=\"_blank\" class=\"css-1occaib\" style=\"color: rgb(0, 102, 255); text-decoration-line: none; cursor: pointer; user-select: text !important;\">\n墨西哥辣椒<svg width=\"8px\" height=\"8px\" viewBox=\"0 0 15 15\" class=\"css-ukqak1\">\n<path d=\"M10.89 9.477l3.06 3.059a1 1 0 0 1-1.414 1.414l-3.06-3.06a6 6 0 1 1 1.414-1.414zM6 10a4 4 0 1 0 0-8 4 4 0 0 0 0 8z\" fill=\"currentColor\">\n</path></svg></a></span>\n</p>\n<p data-pid=\"s0ph7_Ud\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n· 十字花科蔬菜 —&nbsp;<span style=\"user-select: text !important;\">\n<a data-za-not-track-link=\"true\" href=\"https://www.zhihu.com/search?q=%E8%8A%B1%E6%A4%B0%E8%8F%9C&amp;search_source=Entity&amp;hybrid_search_source=Entity&amp;hybrid_search_extra=%7B%22sourceType%22%3A%22article%22%2C%22sourceId%22%3A%2259851840%22%7D\" target=\"_blank\" class=\"css-1occaib\" style=\"color: rgb(0, 102, 255); text-decoration-line: none; cursor: pointer; user-select: text !important;\">\n花椰菜<svg width=\"8px\" height=\"8px\" viewBox=\"0 0 15 15\" class=\"css-ukqak1\">\n<path d=\"M10.89 9.477l3.06 3.059a1 1 0 0 1-1.414 1.414l-3.06-3.06a6 6 0 1 1 1.414-1.414zM6 10a4 4 0 1 0 0-8 4 4 0 0 0 0 8z\" fill=\"currentColor\">\n</path></svg></a></span>、苤蓝、红叶卷心菜、西兰花、球芽甘蓝\n</p>\n<p data-pid=\"A9R6T4qY\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n蔬菜营养丰富，通常含有大量<span style=\"user-select: text !important;\">\n<a data-za-not-track-link=\"true\" href=\"https://www.zhihu.com/search?q=%E6%A4%8D%E7%89%A9%E8%90%A5%E5%85%BB%E7%B4%A0&amp;search_source=Entity&amp;hybrid_search_source=Entity&amp;hybrid_search_extra=%7B%22sourceType%22%3A%22article%22%2C%22sourceId%22%3A%2259851840%22%7D\" target=\"_blank\" class=\"css-1occaib\" style=\"color: rgb(0, 102, 255); text-decoration-line: none; cursor: pointer; user-select: text !important;\">\n植物营养素<svg width=\"8px\" height=\"8px\" viewBox=\"0 0 15 15\" class=\"css-ukqak1\">\n<path d=\"M10.89 9.477l3.06 3.059a1 1 0 0 1-1.414 1.414l-3.06-3.06a6 6 0 1 1 1.414-1.414zM6 10a4 4 0 1 0 0-8 4 4 0 0 0 0 8z\" fill=\"currentColor\">\n</path></svg></a></span>，这些营养素是无法从任何其他来源中获取的，但现代食品生产也带来了一些负面影响。\n</p>\n<p data-pid=\"9UffgjD3\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n例证：尽管草甘膦被认为可能会致癌，但作为最常见、最有争议的杀虫剂之一，草甘膦仍被用于农作物，污染我们的许多食物。多项研究指出，先天缺陷、不孕不育、神经紊乱、内分泌紊乱和癌症均被列为接触草甘膦的潜在风险。\n</p>\n<p data-pid=\"Z0PK3uGZ\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n转基因（GE）种植法是人类处理食物的最糟糕案例之一，转基因作物通常会喷洒草甘膦。最终结果是，据忧思科学家联盟（Union of Concerned Scientists）估计，目前有 6000 万英亩农田被抗草甘膦的超级杂草所覆盖。\n</p>\n<p data-pid=\"kybluwBT\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n环境工作小组根据美国农业部农药数据项目（PDP）报告，提供“Dirty Dozen”年度黑名单（农药残留浓度最高的蔬菜和水果），该名单逐年变化和“15 大清洁食品（Clean 15）”白名单（农药残留最少的植物性食物）。\n</p>\n<p data-pid=\"Zg4u4eP_\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n在 2018 年的榜单上，菠菜排在第二位，甜椒排名第十二位，但当你不知道如何处理可能被杀虫剂或其他有害化学物质污染的蔬菜时，可以参考以下补救办法：科学家报告称，用小苏打清洗水果和蔬菜可以去除 96% 的有毒农药，污染大多数果蔬的就是这些农药。购买有机食物也很重要。\n</p>\n<p data-pid=\"PQB5CzP_\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<span style=\"user-select: text !important; font-weight: 600 !important;\">\n抑郁症吃什么食物好：有益于心理健康的得分最高的动物性食物</span>\n</p>\n<p data-pid=\"wJT5Tl1L\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n就通过饮食对抗抑郁症而言，研究重点已经从研究个别营养物质转移到评估整体饮食模式。传统食物和天然食物（又称健康饮食）肯定与症状减轻和恶化有关。\n</p>\n<p data-pid=\"XpitKNFl\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n其中一项研究指出，吃所谓的“西方”饮食之人，身体里充斥着不健康的脂肪和糖，患抑郁症、注意力缺失症和其他疾病的风险可能更高。\n</p>\n<p data-pid=\"_bnRNjEl\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n举例来说，一项被称为 SUN 世代研究的分析在四年间对超过 10000 名大学生进行了追踪，发现那些长期饮食习惯最接近地中海饮食模式的学生，患抑郁症的风险比那些与地中海饮食模式反差最大的学生低 30% 以上。\n</p>\n<p data-pid=\"0tUBcWLI\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n由于缺乏可获得性，北美驯鹿和鲸鱼肝脏、黑鱼、野猪、羚羊和<span style=\"user-select: text !important;\">\n<a data-za-not-track-link=\"true\" href=\"https://www.zhihu.com/search?q=%E9%BE%99%E7%9C%BC%E9%B1%BC&amp;search_source=Entity&amp;hybrid_search_source=Entity&amp;hybrid_search_extra=%7B%22sourceType%22%3A%22article%22%2C%22sourceId%22%3A%2259851840%22%7D\" target=\"_blank\" class=\"css-1occaib\" style=\"color: rgb(0, 102, 255); text-decoration-line: none; cursor: pointer; user-select: text !important;\">\n龙眼鱼<svg width=\"8px\" height=\"8px\" viewBox=\"0 0 15 15\" class=\"css-ukqak1\">\n<path d=\"M10.89 9.477l3.06 3.059a1 1 0 0 1-1.414 1.414l-3.06-3.06a6 6 0 1 1 1.414-1.414zM6 10a4 4 0 1 0 0-8 4 4 0 0 0 0 8z\" fill=\"currentColor\">\n</path></svg></a></span>都被排除在专题研究之外，得分最高的动物性食物组包括以下食物，并随附相关健康须知：\n</p>\n<p data-pid=\"rNIaWUFK\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n·&nbsp;<span style=\"user-select: text !important;\">\n<a data-za-not-track-link=\"true\" href=\"https://www.zhihu.com/search?q=%E5%8F%8C%E5%A3%B3%E8%B4%9D&amp;search_source=Entity&amp;hybrid_search_source=Entity&amp;hybrid_search_extra=%7B%22sourceType%22%3A%22article%22%2C%22sourceId%22%3A%2259851840%22%7D\" target=\"_blank\" class=\"css-1occaib\" style=\"color: rgb(0, 102, 255); text-decoration-line: none; cursor: pointer; user-select: text !important;\">\n双壳贝<svg width=\"8px\" height=\"8px\" viewBox=\"0 0 15 15\" class=\"css-ukqak1\">\n<path d=\"M10.89 9.477l3.06 3.059a1 1 0 0 1-1.414 1.414l-3.06-3.06a6 6 0 1 1 1.414-1.414zM6 10a4 4 0 1 0 0-8 4 4 0 0 0 0 8z\" fill=\"currentColor\">\n</path></svg></a></span>（生长在两块铰链壳之中的软体无脊椎动物）— 牡蛎、蛤蚌、贻贝\n</p>\n<p data-pid=\"RqjDXrmh\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n· 各种海鲜 — 章鱼、螃蟹、金枪鱼、<span style=\"user-select: text !important;\">\n<a data-za-not-track-link=\"true\" href=\"https://www.zhihu.com/search?q=%E8%83%A1%E7%93%9C%E9%B1%BC&amp;search_source=Entity&amp;hybrid_search_source=Entity&amp;hybrid_search_extra=%7B%22sourceType%22%3A%22article%22%2C%22sourceId%22%3A%2259851840%22%7D\" target=\"_blank\" class=\"css-1occaib\" style=\"color: rgb(0, 102, 255); text-decoration-line: none; cursor: pointer; user-select: text !important;\">\n胡瓜鱼<svg width=\"8px\" height=\"8px\" viewBox=\"0 0 15 15\" class=\"css-ukqak1\">\n<path d=\"M10.89 9.477l3.06 3.059a1 1 0 0 1-1.414 1.414l-3.06-3.06a6 6 0 1 1 1.414-1.414zM6 10a4 4 0 1 0 0-8 4 4 0 0 0 0 8z\" fill=\"currentColor\">\n</path></svg></a></span>、鱼籽（鱼卵）、蓝鱼、狼鱼、鳕鱼、龙虾、<span style=\"user-select: text !important;\">\n<a data-za-not-track-link=\"true\" href=\"https://www.zhihu.com/search?q=%E8%99%B9%E9%B3%9F%E9%B1%BC&amp;search_source=Entity&amp;hybrid_search_source=Entity&amp;hybrid_search_extra=%7B%22sourceType%22%3A%22article%22%2C%22sourceId%22%3A%2259851840%22%7D\" target=\"_blank\" class=\"css-1occaib\" style=\"color: rgb(0, 102, 255); text-decoration-line: none; cursor: pointer; user-select: text !important;\">\n虹鳟鱼<svg width=\"8px\" height=\"8px\" viewBox=\"0 0 15 15\" class=\"css-ukqak1\">\n<path d=\"M10.89 9.477l3.06 3.059a1 1 0 0 1-1.414 1.414l-3.06-3.06a6 6 0 1 1 1.414-1.414zM6 10a4 4 0 1 0 0-8 4 4 0 0 0 0 8z\" fill=\"currentColor\">\n</path></svg></a></span>、狮子鱼、斑点鱼、鲑鱼、鲱鱼、鲷鱼\n</p>\n<p data-pid=\"K3bRYb9l\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n· 动物内脏 — 脾脏、肾脏、心脏和家禽内脏\n</p>\n<p data-pid=\"wYWUT9f9\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n另应指出的是，木瓜、柠檬和草莓的 AFS 评分均为 31%，高于狮子鱼、鲑鱼、鲱鱼和鲷鱼等数种海鲜。鱼类被视为终极超级食物，但在购买时务必要小心，确保规避最差食用鱼类，因为这些鱼容易受到重金属污染，如铅、汞、砷、镉、多氯联苯（多氯联苯）和/或放射性毒物。\n</p>\n<p data-pid=\"PkxBaAiV\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n此外，集中型动物饲养场（CAFO）也存在着猖獗问题，这些问题往往涉及疾病、粪便污染、食用过多谷物和动物副产品、禁用抗生素和致癌物，更不用说糟糕的环境了。\n</p>\n<p data-pid=\"GXM3pn8s\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n金枪鱼的 AFS 评分在 15% 到 21% 之间，是消费最多的海产品之一，但由于汞的毒性，它会对你的健康造成严重影响。野生阿拉斯加鲑鱼是你能吃到的最好的食物之一，但在购买的时候一定要确保不是养殖鱼，也就是所谓的“海里的 CAFO”，因为用转基因大豆和玉米饲养，很可能导致其产生毒性，更不用说汞和其他重金属了。\n</p>\n<p data-pid=\"NVvl592U\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n鲱鱼是五大最健康的鱼之一。据 NPR 新闻报道，海洋保护组织 Oceanalert 的加州活动主管 Geoff Shester 将鲱鱼描述为“一种本地的、可持续的食物来源”，并且表示“鲱鱼很美味，鱼肉呈片状、口感温和，油脂丰富但不腻……”另外值得注意的是，像沙丁鱼、凤尾鱼和鲱鱼这样的小鱼一般污染物较少，而且富含 ω-3 脂肪。\n</p>\n<p data-pid=\"Pnyx0ecc\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<span style=\"user-select: text !important; font-weight: 600 !important;\">\n治疗抑郁症的食物：这七种天然健康食物是对抗抑郁症的最佳选择</span>\n</p>\n<p class=\"ztext-empty-paragraph\" style=\"margin-top: -0.8em; margin-bottom: -0.8em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<br style=\"user-select: text !important;\">\n</p>\n<figure data-size=\"normal\" style=\"margin: 1.4em 0px; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<img src=\"https://pic4.zhimg.com/v2-68aba953ac4382499adffd13a6a60637_b.jpg\" data-caption=\"\" data-size=\"normal\" data-rawwidth=\"413\" data-rawheight=\"258\" class=\"content_image\" width=\"413\" style=\"user-select: text !important; display: block; max-width: 100%; margin: 0px auto;\">\n</figure><p class=\"ztext-empty-paragraph\" style=\"margin-top: -0.8em; margin-bottom: -0.8em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<br style=\"user-select: text !important;\">\n</p>\n<p data-pid=\"5iz2yEtL\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n一个非常重要且鼓舞人心的消息是，医学界似乎掌握了一个事实，即食物 — 不只是药物和/或心理治疗形式的医疗干预 — 极有可能是我们（无论是个人还是集体）重新获得必要的精神立足点的最大希望，以便在生活中接近和取得成功。综上所述，以下七种食物有助于对抗抑郁症：\n</p>\n<p data-pid=\"cpMJZNjs\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<span style=\"user-select: text !important; font-weight: 600 !important;\">\n1. 牡蛎</span>，但要避免来自受污染水域的牡蛎\n</p>\n<p data-pid=\"UBiIlXhe\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<span style=\"user-select: text !important; font-weight: 600 !important;\">\n2. 贻贝</span>，但要确保它们来自未受污染的水域\n</p>\n<p data-pid=\"iQ8sbtHB\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<span style=\"user-select: text !important; font-weight: 600 !important;\">\n3. 海鲜</span>，特别是野生阿拉斯加鲑鱼、鲱鱼、沙丁鱼和凤尾鱼\n</p>\n<p data-pid=\"0HklR5Pp\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<span style=\"user-select: text !important; font-weight: 600 !important;\">\n4. 动物内脏</span>，但仅限于草饲动物（非 CAFO 动物）\n</p>\n<p data-pid=\"xhVVweA_\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<span style=\"user-select: text !important; font-weight: 600 !important;\">\n5. 绿叶蔬菜</span>\n</p>\n<p data-pid=\"K8hJBWfX\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<span style=\"user-select: text !important; font-weight: 600 !important;\">\n6. 辣椒</span>\n</p>\n<p data-pid=\"lGg4uziG\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<span style=\"user-select: text !important; font-weight: 600 !important;\">\n7. 十字花科蔬菜</span>\n</p>\n<p data-pid=\"Efze8ITA\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n为了让人们更容易吃到有益于健康的食物，而不会被错误的饮食限制所误导，该专题研究的研究人员提到了 2016 年发表在《<span style=\"user-select: text !important;\">\n<a data-za-not-track-link=\"true\" href=\"https://www.zhihu.com/search?q=%E8%8B%B1%E5%9B%BD%E5%8C%BB%E5%AD%A6%E6%9D%82%E5%BF%97&amp;search_source=Entity&amp;hybrid_search_source=Entity&amp;hybrid_search_extra=%7B%22sourceType%22%3A%22article%22%2C%22sourceId%22%3A%2259851840%22%7D\" target=\"_blank\" class=\"css-1occaib\" style=\"color: rgb(0, 102, 255); text-decoration-line: none; cursor: pointer; user-select: text !important;\">\n英国医学杂志<svg width=\"8px\" height=\"8px\" viewBox=\"0 0 15 15\" class=\"css-ukqak1\">\n<path d=\"M10.89 9.477l3.06 3.059a1 1 0 0 1-1.414 1.414l-3.06-3.06a6 6 0 1 1 1.414-1.414zM6 10a4 4 0 1 0 0-8 4 4 0 0 0 0 8z\" fill=\"currentColor\">\n</path></svg></a></span>》上的另一项研究。它解决了许多医学专家直到最近还坚持认为“有害”的食物问题。最近的研究结果正在颠覆关于饱和脂肪、胆固醇和钠的老派说法。\n</p>\n<p data-pid=\"iJVEinXI\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n此外，饱和脂肪、胆固醇和钠等营养物质对身心健康的危害和潜在益处正在根据最新的研究进行重新评估。根据最新的美国膳食指南，胆固醇不再被视为令人担忧的营养物质。\n</p>\n<p data-pid=\"GyROkmwS\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n该研究的作者还观察到，心理健康专业人员可能具备支持行为改变的能力，也可能不具备，饮食改变就是一个简单的例子。但是 AFS 列表可以用作一种工具，医疗人员可用其为患者改善营养建议，或者被消费者聪明地用于改善或重新定义营养选择。\n</p>','H2F0N9',789,345,1,'2019-12-04','2021-11-20 10:39:43','2021-11-20 10:39:43'),(21,'关于抑郁症的科普知识',6,'http://qqpublic.qpic.cn/qq_public/0/0-2596354149-1B4A3AE4E8A7B101DF2B05D0A5796D16/0','摘要','<p data-pid=\"nho2tdPh\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n随着心理学知识的普及，“抑郁症”这个词在大众的心目中也不再陌生，又因其在发病期，自杀死亡的风险比较高而格外引人关注。\n</p>\n<p data-pid=\"wDitpJPz\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<span style=\"user-select: text !important; font-weight: 600 !important;\">\n*症状</span>\n</p>\n<p data-pid=\"xVaf9YEN\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n在《国际疾病与分类第10版》（ICD-10）中，抑郁症的诊断标准包括三条核心症状，1.心境低落 2.兴趣和愉快感丧失 3.导致劳累增加和活动减少的精力降低；\n</p>\n<p data-pid=\"ZNAo4AXb\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n七条附加症状：1.注意力降低 2.自我评价和自信降低 3.自罪观念和无价值感 4.认为前途暗淡悲观 5.自杀或自杀的观念或行为6.睡眠障碍7.食欲下降\n</p>\n<p data-pid=\"741Fmltw\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<span style=\"user-select: text !important; font-weight: 600 !important;\">\n*诊断与分类（可忽略）</span>\n</p>\n<p data-pid=\"43Wj5e1b\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n抑郁症的诊断，一般要病程持续至少2周，并存在具有无临床意义的痛苦或社会功能的受损。然后可根据严重程度分为轻度，中度和重度。\n</p>\n<p data-pid=\"qxrwdoL9\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<i style=\"user-select: text !important;\">\n轻度抑郁</i>至少具有2条核心症状和至少2条附加症状，社会功能（工作或社交）有轻度影响。\n</p>\n<p data-pid=\"k-mc2pYw\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<span style=\"user-select: text !important; font-weight: 600 !important;\">\n<i style=\"user-select: text !important;\">\n中度抑郁</i></span>则至少具备2条核心症状和至少3条，社会功能存在相当困难。\n</p>\n<p data-pid=\"Cac4D7yW\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<span style=\"user-select: text !important; font-weight: 600 !important;\">\n<i style=\"user-select: text !important;\">\n重度抑郁&nbsp;</i></span>3条核心症状全都存在并且具备至少4条附加症状，社会功能严重受损。\n</p>\n<p data-pid=\"TEH57eLn\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n若符合中、重度抑郁发作的诊断标准且还存在妄想、幻觉或抑郁性木僵等症状其又分为伴有<span style=\"user-select: text !important;\">\n<a data-za-not-track-link=\"true\" href=\"https://www.zhihu.com/search?q=%E7%B2%BE%E7%A5%9E%E7%97%85%E6%80%A7%E7%97%87%E7%8A%B6&amp;search_source=Entity&amp;hybrid_search_source=Entity&amp;hybrid_search_extra=%7B%22sourceType%22%3A%22article%22%2C%22sourceId%22%3A%22108315441%22%7D\" target=\"_blank\" class=\"css-1occaib\" style=\"color: rgb(0, 102, 255); text-decoration-line: none; cursor: pointer; user-select: text !important;\">\n精神病性症状<svg width=\"8px\" height=\"8px\" viewBox=\"0 0 15 15\" class=\"css-ukqak1\">\n<path d=\"M10.89 9.477l3.06 3.059a1 1 0 0 1-1.414 1.414l-3.06-3.06a6 6 0 1 1 1.414-1.414zM6 10a4 4 0 1 0 0-8 4 4 0 0 0 0 8z\" fill=\"currentColor\">\n</path></svg></a></span>的抑郁。\n</p>\n<p data-pid=\"npzgWH1I\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n临床上抑郁障碍的诊断标准除了源于<span style=\"user-select: text !important;\">\n<a data-za-not-track-link=\"true\" href=\"https://www.zhihu.com/search?q=ICD-10&amp;search_source=Entity&amp;hybrid_search_source=Entity&amp;hybrid_search_extra=%7B%22sourceType%22%3A%22article%22%2C%22sourceId%22%3A%22108315441%22%7D\" target=\"_blank\" class=\"css-1occaib\" style=\"color: rgb(0, 102, 255); text-decoration-line: none; cursor: pointer; user-select: text !important;\">\nICD-10<svg width=\"8px\" height=\"8px\" viewBox=\"0 0 15 15\" class=\"css-ukqak1\">\n<path d=\"M10.89 9.477l3.06 3.059a1 1 0 0 1-1.414 1.414l-3.06-3.06a6 6 0 1 1 1.414-1.414zM6 10a4 4 0 1 0 0-8 4 4 0 0 0 0 8z\" fill=\"currentColor\">\n</path></svg></a></span>的诊断标准外，还有《美国精神障碍诊断统计手册第五版》（DSM-5），这里只诉其一。\n</p>\n<figure data-size=\"normal\" style=\"margin: 0px 0px 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<img src=\"https://pic2.zhimg.com/v2-c898b60226310713cd66755419ae9c49_b.jpg\" data-caption=\"\" data-size=\"normal\" data-rawwidth=\"476\" data-rawheight=\"334\" class=\"origin_image zh-lightbox-thumb\" width=\"476\" data-original=\"https://pic2.zhimg.com/v2-c898b60226310713cd66755419ae9c49_r.jpg\" style=\"user-select: text !important; display: block; max-width: 100%; margin: 0px auto; cursor: zoom-in;\">\n</figure><p data-pid=\"c3QZqEaO\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<span style=\"user-select: text !important; font-weight: 600 !important;\">\n*抑郁症病因</span>\n</p>\n<p data-pid=\"R9hS3ytp\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n抑郁症的病因和发病机制比较复杂，即使目前也都没有完全阐明，其可能的原因也涉及到生物因素、心理因素及社会环境因素等多个因素共同作用的结果。\n</p>\n<p data-pid=\"SwpP58cr\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<span style=\"user-select: text !important; font-weight: 600 !important;\">\n一、生物因素</span>\n</p>\n<p data-pid=\"1TUFewa3\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\na）遗传\n</p>\n<p data-pid=\"BUJ32ABu\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n遗传因素可能是抑郁症发生的一个因素之一，在抑郁症患者中，其一级亲属患抑郁症的风险大约是一般人群的2～10倍。\n</p>\n<p data-pid=\"4wH6TkHY\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n有些抑郁症患者家族里却实有人也有抑郁症，但是仅仅从<span style=\"user-select: text !important;\">\n<a data-za-not-track-link=\"true\" href=\"https://www.zhihu.com/search?q=%E9%81%97%E4%BC%A0%E5%AD%A6&amp;search_source=Entity&amp;hybrid_search_source=Entity&amp;hybrid_search_extra=%7B%22sourceType%22%3A%22article%22%2C%22sourceId%22%3A%22108315441%22%7D\" target=\"_blank\" class=\"css-1occaib\" style=\"color: rgb(0, 102, 255); text-decoration-line: none; cursor: pointer; user-select: text !important;\">\n遗传学<svg width=\"8px\" height=\"8px\" viewBox=\"0 0 15 15\" class=\"css-ukqak1\">\n<path d=\"M10.89 9.477l3.06 3.059a1 1 0 0 1-1.414 1.414l-3.06-3.06a6 6 0 1 1 1.414-1.414zM6 10a4 4 0 1 0 0-8 4 4 0 0 0 0 8z\" fill=\"currentColor\">\n</path></svg></a></span>的角度来看待抑郁症，目前很难帮助到抑郁症患者。\n</p>\n<p data-pid=\"dnUUTCCZ\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n不仅因科学发展的水平也还无法精准定位抑郁症遗传的DNA片段，而且遗传学还很难解释到更多的抑郁患者的家族史中其实并无人有患抑郁症的情况。\n</p>\n<p data-pid=\"IzJmmeZp\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n但不可否认遗传因素会占影响抑郁症发作因素的一定的比例，只是比例大小很难得知，而对应到相应人的身上也很难得到一个标准的答案。\n</p>\n<p data-pid=\"WPdRdu1x\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\nb）神经递质水平\n</p>\n<p data-pid=\"xzSsvh4t\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n从神经生化失调节假说来看，抑郁症患者大脑内的某些神经递质水平异常或其受体功能的失衡也是发病的一个因素。\n</p>\n<figure data-size=\"normal\" style=\"margin: 1.4em 0px; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<img src=\"https://pic3.zhimg.com/v2-c060f2cdb27f3411fc45f3c531be78de_b.jpg\" data-caption=\"\" data-size=\"normal\" data-rawwidth=\"1080\" data-rawheight=\"575\" class=\"origin_image zh-lightbox-thumb\" width=\"1080\" data-original=\"https://pic3.zhimg.com/v2-c060f2cdb27f3411fc45f3c531be78de_r.jpg\" style=\"user-select: text !important; display: block; max-width: 100%; margin: 0px auto; cursor: zoom-in;\">\n</figure><p data-pid=\"sf_EKSMZ\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n比如<span style=\"user-select: text !important; font-weight: 600 !important;\">\n多巴胺，五-羟色胺/5-HT(<span style=\"user-select: text !important;\">\n<a data-za-not-track-link=\"true\" href=\"https://www.zhihu.com/search?q=%E8%A1%80%E6%B8%85%E7%B4%A0&amp;search_source=Entity&amp;hybrid_search_source=Entity&amp;hybrid_search_extra=%7B%22sourceType%22%3A%22article%22%2C%22sourceId%22%3A%22108315441%22%7D\" target=\"_blank\" class=\"css-1occaib\" style=\"color: rgb(0, 102, 255); text-decoration-line: none; cursor: pointer; user-select: text !important;\">\n血清素<svg width=\"8px\" height=\"8px\" viewBox=\"0 0 15 15\" class=\"css-ukqak1\">\n<path d=\"M10.89 9.477l3.06 3.059a1 1 0 0 1-1.414 1.414l-3.06-3.06a6 6 0 1 1 1.414-1.414zM6 10a4 4 0 1 0 0-8 4 4 0 0 0 0 8z\" fill=\"currentColor\">\n</path></svg></a></span>)</span>是能够让我们感到快乐和愉悦的一种神经递质，如果它们在人体内水平下降，那么人能够感受到的快乐或感受到快乐的信息也会减少。\n</p>\n<p data-pid=\"o73rxVra\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n去<span style=\"user-select: text !important; font-weight: 600 !important;\">\n甲肾上腺素</span>是一种神经递质也是一种激素，它能够让我们在运到危险或在压力状态时调动体能去进入一种“战斗”的状态，如果失去它或在人体内水平下降，人就会容易陷入一种毫无动力、毫无意志力去面对当前压力与困难的状态。\n</p>\n<p data-pid=\"28kspIFz\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n我们常说抑郁症在临床上有“三低”主要表现症状，<span style=\"user-select: text !important; font-weight: 600 !important;\">\n分别为情绪低落，思维缓慢，意志活动减少。从这些症状来看，<span style=\"user-select: text !important;\">\n<a data-za-not-track-link=\"true\" href=\"https://www.zhihu.com/search?q=%E7%A5%9E%E7%BB%8F%E7%A5%9E%E7%BB%8F%E9%80%92%E8%B4%A8&amp;search_source=Entity&amp;hybrid_search_source=Entity&amp;hybrid_search_extra=%7B%22sourceType%22%3A%22article%22%2C%22sourceId%22%3A%22108315441%22%7D\" target=\"_blank\" class=\"css-1occaib\" style=\"color: rgb(0, 102, 255); text-decoration-line: none; cursor: pointer; user-select: text !important;\">\n神经神经递质<svg width=\"8px\" height=\"8px\" viewBox=\"0 0 15 15\" class=\"css-ukqak1\">\n<path d=\"M10.89 9.477l3.06 3.059a1 1 0 0 1-1.414 1.414l-3.06-3.06a6 6 0 1 1 1.414-1.414zM6 10a4 4 0 1 0 0-8 4 4 0 0 0 0 8z\" fill=\"currentColor\">\n</path></svg></a></span>水平异常也与之有一定的对应。</span>\n</p>\n<p data-pid=\"yj74_MCN\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<span style=\"user-select: text !important; font-weight: 600 !important;\">\n目前药物治疗的主要原理是通过阻断多巴胺与去甲肾上腺素的再吸收，</span>从而维持这些神经递质或激素的正常水平，但抗抑郁药物也有一定的副作用，比如让人困倦，口干，视线模糊，便秘，心跳加快、排尿困难等，具体的剂量也应遵从医嘱。\n</p>\n<figure data-size=\"normal\" style=\"margin: 1.4em 0px; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<img src=\"https://pic3.zhimg.com/v2-6a77ef9510ec197c5d969d598edab982_b.jpg\" data-caption=\"\" data-size=\"normal\" data-rawwidth=\"800\" data-rawheight=\"306\" class=\"origin_image zh-lightbox-thumb\" width=\"800\" data-original=\"https://pic3.zhimg.com/v2-6a77ef9510ec197c5d969d598edab982_r.jpg\" style=\"user-select: text !important; display: block; max-width: 100%; margin: 0px auto; cursor: zoom-in;\">\n</figure><p data-pid=\"AIgA9NG4\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\nc）激素水平\n</p>\n<p data-pid=\"A_3fNhPF\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n抑郁症的确诊除了量表以外，还可进行激素检查。激素水平是否异常可通过检测血浆中<span style=\"user-select: text !important;\">\n<a data-za-not-track-link=\"true\" href=\"https://www.zhihu.com/search?q=%E7%9A%AE%E8%B4%A8%E9%86%87&amp;search_source=Entity&amp;hybrid_search_source=Entity&amp;hybrid_search_extra=%7B%22sourceType%22%3A%22article%22%2C%22sourceId%22%3A%22108315441%22%7D\" target=\"_blank\" class=\"css-1occaib\" style=\"color: rgb(0, 102, 255); text-decoration-line: none; cursor: pointer; user-select: text !important;\">\n皮质醇<svg width=\"8px\" height=\"8px\" viewBox=\"0 0 15 15\" class=\"css-ukqak1\">\n<path d=\"M10.89 9.477l3.06 3.059a1 1 0 0 1-1.414 1.414l-3.06-3.06a6 6 0 1 1 1.414-1.414zM6 10a4 4 0 1 0 0-8 4 4 0 0 0 0 8z\" fill=\"currentColor\">\n</path></svg></a></span>的含量以及24小时尿液中的尿17-羟皮质类固醇水平来看。\n</p>\n<p data-pid=\"3TBcUzzf\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n注：临床上关于抑郁症的测评量表有：抑郁症自评量表/简称SDS、汉密尔顿抑郁量表-17/简称HAMD-17、蒙哥马力-艾斯伯格抑郁评分量表/简称MARDS\n</p>\n<p data-pid=\"L_QRbUl4\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n从神经内分泌来看，抑郁症患者的体内某些激素分泌水平异常（主要为下丘脑-垂体-肾上腺轴功能异常）可能也参与了抑郁症的发病因素。\n</p>\n<figure data-size=\"normal\" style=\"margin: 1.4em 0px; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<img src=\"https://pic1.zhimg.com/v2-209cbc1ade2eb8cdafc18a6bc4ab96a0_b.jpg\" data-caption=\"\" data-size=\"normal\" data-rawwidth=\"480\" data-rawheight=\"459\" class=\"origin_image zh-lightbox-thumb\" width=\"480\" data-original=\"https://pic1.zhimg.com/v2-209cbc1ade2eb8cdafc18a6bc4ab96a0_r.jpg\" style=\"user-select: text !important; display: block; max-width: 100%; margin: 0px auto; cursor: zoom-in;\">\n</figure><p data-pid=\"Pq19CTEh\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n其表现为抑郁患者血中皮质醇水平增高，应激相关的激素分泌昼夜节律发生改变以及无晚间自发性皮质醇分泌抑制等。\n</p>\n<p data-pid=\"SBbVxiZf\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n皮质醇是<span style=\"user-select: text !important;\">\n<a data-za-not-track-link=\"true\" href=\"https://www.zhihu.com/search?q=%E8%82%BE%E4%B8%8A%E8%85%BA&amp;search_source=Entity&amp;hybrid_search_source=Entity&amp;hybrid_search_extra=%7B%22sourceType%22%3A%22article%22%2C%22sourceId%22%3A%22108315441%22%7D\" target=\"_blank\" class=\"css-1occaib\" style=\"color: rgb(0, 102, 255); text-decoration-line: none; cursor: pointer; user-select: text !important;\">\n肾上腺<svg width=\"8px\" height=\"8px\" viewBox=\"0 0 15 15\" class=\"css-ukqak1\">\n<path d=\"M10.89 9.477l3.06 3.059a1 1 0 0 1-1.414 1.414l-3.06-3.06a6 6 0 1 1 1.414-1.414zM6 10a4 4 0 1 0 0-8 4 4 0 0 0 0 8z\" fill=\"currentColor\">\n</path></svg></a></span>产生的一种激素，它在应付压力中扮演重要角色，又被称为“<span style=\"user-select: text !important;\">\n<a data-za-not-track-link=\"true\" href=\"https://www.zhihu.com/search?q=%E5%8E%8B%E5%8A%9B%E8%8D%B7%E5%B0%94%E8%92%99&amp;search_source=Entity&amp;hybrid_search_source=Entity&amp;hybrid_search_extra=%7B%22sourceType%22%3A%22article%22%2C%22sourceId%22%3A%22108315441%22%7D\" target=\"_blank\" class=\"css-1occaib\" style=\"color: rgb(0, 102, 255); text-decoration-line: none; cursor: pointer; user-select: text !important;\">\n压力荷尔蒙<svg width=\"8px\" height=\"8px\" viewBox=\"0 0 15 15\" class=\"css-ukqak1\">\n<path d=\"M10.89 9.477l3.06 3.059a1 1 0 0 1-1.414 1.414l-3.06-3.06a6 6 0 1 1 1.414-1.414zM6 10a4 4 0 1 0 0-8 4 4 0 0 0 0 8z\" fill=\"currentColor\">\n</path></svg></a></span>”，若皮质醇一直居高不下，人长时间处于压力的应激状态则也很容易崩溃。\n</p>\n<p data-pid=\"y7j8QuhG\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n正常人的<span style=\"user-select: text !important;\">\n<a data-za-not-track-link=\"true\" href=\"https://www.zhihu.com/search?q=%E8%82%BE%E4%B8%8A%E8%85%BA%E7%9A%AE%E8%B4%A8&amp;search_source=Entity&amp;hybrid_search_source=Entity&amp;hybrid_search_extra=%7B%22sourceType%22%3A%22article%22%2C%22sourceId%22%3A%22108315441%22%7D\" target=\"_blank\" class=\"css-1occaib\" style=\"color: rgb(0, 102, 255); text-decoration-line: none; cursor: pointer; user-select: text !important;\">\n肾上腺皮质<svg width=\"8px\" height=\"8px\" viewBox=\"0 0 15 15\" class=\"css-ukqak1\">\n<path d=\"M10.89 9.477l3.06 3.059a1 1 0 0 1-1.414 1.414l-3.06-3.06a6 6 0 1 1 1.414-1.414zM6 10a4 4 0 1 0 0-8 4 4 0 0 0 0 8z\" fill=\"currentColor\">\n</path></svg></a></span>分泌皮质醇是有典型的昼夜节律,每日凌晨3一5时开始升高,至9一10时达高峰,然后逐渐下降,至午夜12时最低。\n</p>\n<p data-pid=\"QZmL1-36\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n但是抑郁症患者的皮质醇分泌则提前1一2小时,即在凌晨1一3时分升高,整体血液中的皮质醇水平显著增高。因皮质醇的节律分泌异常，郁患症者在白天感觉到的抑郁程度痛苦要比晚上要更严重。另一些抑郁症患者容易在凌晨期间出现自杀想法或行为等。\n</p>\n<p data-pid=\"9-UZdULb\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n此外生长激素，<span style=\"user-select: text !important;\">\n<a data-za-not-track-link=\"true\" href=\"https://www.zhihu.com/search?q=%E5%82%AC%E4%B9%B3%E7%B4%A0&amp;search_source=Entity&amp;hybrid_search_source=Entity&amp;hybrid_search_extra=%7B%22sourceType%22%3A%22article%22%2C%22sourceId%22%3A%22108315441%22%7D\" target=\"_blank\" class=\"css-1occaib\" style=\"color: rgb(0, 102, 255); text-decoration-line: none; cursor: pointer; user-select: text !important;\">\n催乳素<svg width=\"8px\" height=\"8px\" viewBox=\"0 0 15 15\" class=\"css-ukqak1\">\n<path d=\"M10.89 9.477l3.06 3.059a1 1 0 0 1-1.414 1.414l-3.06-3.06a6 6 0 1 1 1.414-1.414zM6 10a4 4 0 1 0 0-8 4 4 0 0 0 0 8z\" fill=\"currentColor\">\n</path></svg></a></span>、褪黑素和性激素等在抑郁患者中也均可见不同程度的分泌改变。\n</p>\n<p data-pid=\"ayWzKg8j\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n除了以上一些生物因素外，还有心理与社会因素也可能是抑郁症发病的因素。<br style=\"user-select: text !important;\">\n<br style=\"user-select: text !important;\">\n<br style=\"user-select: text !important;\">\n<span style=\"user-select: text !important; font-weight: 600 !important;\">\n二、心理因素</span><br style=\"user-select: text !important;\">\n<br style=\"user-select: text !important;\">\n<br style=\"user-select: text !important;\">\n在很多动物实验和临床流行学的研究结果中，精神创伤尤其是早年的创伤会显著地增加成年期抑郁症的发病风险。<br style=\"user-select: text !important;\">\n<br style=\"user-select: text !important;\">\n<br style=\"user-select: text !important;\">\n比如早年的性虐待或躯体虐待会与后来的抑郁和抑郁自杀未遂的发生有显著相关。<br style=\"user-select: text !important;\">\n<br style=\"user-select: text !important;\">\n<br style=\"user-select: text !important;\">\n一个人早期的负性经历也会与重性抑郁障碍的现患率以及终身患病率程显著相关，且一个人早期不良经历种类越多，发生重性抑郁障碍的风险也就越高，并且还会使患者发病年龄提前。<br style=\"user-select: text !important;\">\n<br style=\"user-select: text !important;\">\n<br style=\"user-select: text !important;\">\n在具有童年创伤史的抑郁症患者的治疗上，若仅仅靠药物治疗效果可能还不佳，若在治疗期辅以心理治疗可能会达到更理想的效果。<br style=\"user-select: text !important;\">\n</p>\n<figure data-size=\"normal\" style=\"margin: 1.4em 0px; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<img src=\"https://pic2.zhimg.com/v2-38332ecea1414c1fb76988094a9da55d_b.jpg\" data-caption=\"\" data-size=\"normal\" data-rawwidth=\"1000\" data-rawheight=\"560\" class=\"origin_image zh-lightbox-thumb\" width=\"1000\" data-original=\"https://pic2.zhimg.com/v2-38332ecea1414c1fb76988094a9da55d_r.jpg\" style=\"user-select: text !important; display: block; max-width: 100%; margin: 0px auto; cursor: zoom-in;\">\n</figure><p data-pid=\"uydJf8P8\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<br style=\"user-select: text !important;\">\n<br style=\"user-select: text !important;\">\n<span style=\"user-select: text !important; font-weight: 600 !important;\">\n**从精神分析看抑郁</span><br style=\"user-select: text !important;\">\n<br style=\"user-select: text !important;\">\n<br style=\"user-select: text !important;\">\n从精神分析的角度看来，人活着的四个驱动力分别是：力比多/性力（即身体器官获得快乐的一种本能）、攻击性、关系（即寻求与他人建立关系）与自恋。<br style=\"user-select: text !important;\">\n<br style=\"user-select: text !important;\">\n<br style=\"user-select: text !important;\">\n在精神分析看来，抑郁症患者有着攻击性无法正常对外表达或得到象征化，进而攻转向攻击自身的特点。<br style=\"user-select: text !important;\">\n<br style=\"user-select: text !important;\">\n<br style=\"user-select: text !important;\">\n比如内心很愤怒某人，但这种愤怒无法表达到那个人身上，且又无法通过别的比较高级的非原始攻击手段如吐槽或一些艺术性表达手段等方式排解出来，进而这种愤怒转而攻击自己，如表现为自我自责，愧疚感，感觉自己没有价值，或身体部位疼痛但又无法检查出来等。<br style=\"user-select: text !important;\">\n<br style=\"user-select: text !important;\">\n<br style=\"user-select: text !important;\">\n从更深的角度来看，这种对他人的攻击不管是对外还是对内，其实也还是在渴求与他人建立关系。<br style=\"user-select: text !important;\">\n<br style=\"user-select: text !important;\">\n<br style=\"user-select: text !important;\">\n如果外界一点声音都没有，如果失了与世界的链接，那会不会自己就不存在了呢？所以他们宁愿用比较原始的攻击的手段，也以此要保留仅存的一点链接或回应。<br style=\"user-select: text !important;\">\n<br style=\"user-select: text !important;\">\n</p>\n<figure data-size=\"normal\" style=\"margin: 1.4em 0px; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<img src=\"https://pic2.zhimg.com/v2-9ff4ece883f6e605af48ac18f36717b5_b.jpg\" data-caption=\"\" data-size=\"normal\" data-rawwidth=\"1024\" data-rawheight=\"697\" class=\"origin_image zh-lightbox-thumb\" width=\"1024\" data-original=\"https://pic2.zhimg.com/v2-9ff4ece883f6e605af48ac18f36717b5_r.jpg\" style=\"user-select: text !important; display: block; max-width: 100%; margin: 0px auto; cursor: zoom-in;\">\n</figure><p data-pid=\"0cgnaNWd\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<br style=\"user-select: text !important;\">\n<br style=\"user-select: text !important;\">\n<br style=\"user-select: text !important;\">\n除此，抑郁患者还有一个非常强大的超我，简单来说对自己的要求或标准过分地高。因此他们在感到痛苦和自我惩罚手段上也要比一般人要苛刻得多，其实也算是一种自恋的表现。<br style=\"user-select: text !important;\">\n<br style=\"user-select: text !important;\">\n<br style=\"user-select: text !important;\">\n抑郁症患者因如此地关注自己和如此地“爱”自己，所以不想把任何痛苦与伤害伸手给别人，就如同手上有一把刀子，“吝啬”到宁愿插向自己的心脏也舍不得白白插到别人的心脏上。<br style=\"user-select: text !important;\">\n<br style=\"user-select: text !important;\">\n<br style=\"user-select: text !important;\">\n自杀行为便是抑郁症状中最高的自恋形式，但其带来是影响不仅是其让生命过早地枯零，同时还可能会带给身边亲人朋友或社会带来一定的痛苦等，是一种恶性的自恋。<br style=\"user-select: text !important;\">\n<br style=\"user-select: text !important;\">\n<br style=\"user-select: text !important;\">\n从防御机制的层面来看，抑郁症患者不能用更高级别的防御机来抵御痛苦。防御机制就如同一个国家的边界城墙，若城墙不够结实不够高，外敌就很容易入侵，导致国家战乱。<br style=\"user-select: text !important;\">\n<br style=\"user-select: text !important;\">\n<br style=\"user-select: text !important;\">\n换另一种说法，其抵御外界痛苦的城墙不够结实，也是个人与环境或他人边界不清或比较模糊导致。当无法将自己与他人或外界区分开来，你是你，我是我，我们彼此独立存在的话，那他人的一举一动或情绪状态和外界的点点风吹草地也都可能会影响到自身。<br style=\"user-select: text !important;\">\n<br style=\"user-select: text !important;\">\n<br style=\"user-select: text !important;\">\n当过度地背负了本来就不属于自己的负面情绪或痛苦，自然也会感到很累。<br style=\"user-select: text !important;\">\n<br style=\"user-select: text !important;\">\n</p>\n<figure data-size=\"normal\" style=\"margin: 1.4em 0px; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<img src=\"https://pic1.zhimg.com/v2-c134553dd32d73fe93127a75117be9a8_b.jpg\" data-caption=\"\" data-size=\"normal\" data-rawwidth=\"1028\" data-rawheight=\"653\" class=\"origin_image zh-lightbox-thumb\" width=\"1028\" data-original=\"https://pic1.zhimg.com/v2-c134553dd32d73fe93127a75117be9a8_r.jpg\" style=\"user-select: text !important; display: block; max-width: 100%; margin: 0px auto; cursor: zoom-in;\">\n</figure><p data-pid=\"0cqQow1A\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<br style=\"user-select: text !important;\">\n<br style=\"user-select: text !important;\">\n<span style=\"user-select: text !important; font-weight: 600 !important;\">\n三、社会环境因素</span><br style=\"user-select: text !important;\">\n<br style=\"user-select: text !important;\">\n<br style=\"user-select: text !important;\">\n最后，生活中的一些应激事件，如亲人的死亡、婚姻关系不良、失业、严重的疾病等都可能是导致抑郁症发生的一些危险因素。如果多个严重不良的生活事件同时存在，则可能协同影响抑郁症的发生。<br style=\"user-select: text !important;\">\n<br style=\"user-select: text !important;\">\n<br style=\"user-select: text !important;\">\n在面对不幸的生活事件时，抑郁患者可以寻求社会支持系统，如家人、朋友、心理咨询师等的支持，以此让自己能够走出情绪的阴影。\n</p>\n<p class=\"ztext-empty-paragraph\" style=\"margin-top: -0.8em; margin-bottom: -0.8em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<br style=\"user-select: text !important;\">\n</p>\n<p data-pid=\"RqzGRtD8\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n在抑郁症的治疗手段上，除了有药物治疗，心理治疗，还有物理治疗等。电抽搐治疗简称ECT是物理治疗的其中一种，对于重度抑郁障碍患者，或伴有自杀观念的患者有较好的疗效，其可在短时间内控制自杀意念，从而降低患者的自杀死亡率。\n</p>\n<p class=\"ztext-empty-paragraph\" style=\"margin-top: -0.8em; margin-bottom: -0.8em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<br style=\"user-select: text !important;\">\n</p>\n<p data-pid=\"GLKDtAFT\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\nECT的原理主要给予<span style=\"user-select: text !important;\">\n<a data-za-not-track-link=\"true\" href=\"https://www.zhihu.com/search?q=%E4%B8%AD%E6%9E%A2%E7%A5%9E%E7%BB%8F%E7%B3%BB%E7%BB%9F&amp;search_source=Entity&amp;hybrid_search_source=Entity&amp;hybrid_search_extra=%7B%22sourceType%22%3A%22article%22%2C%22sourceId%22%3A%22108315441%22%7D\" target=\"_blank\" class=\"css-1occaib\" style=\"color: rgb(0, 102, 255); text-decoration-line: none; cursor: pointer; user-select: text !important;\">\n中枢神经系统<svg width=\"8px\" height=\"8px\" viewBox=\"0 0 15 15\" class=\"css-ukqak1\">\n<path d=\"M10.89 9.477l3.06 3.059a1 1 0 0 1-1.414 1.414l-3.06-3.06a6 6 0 1 1 1.414-1.414zM6 10a4 4 0 1 0 0-8 4 4 0 0 0 0 8z\" fill=\"currentColor\">\n</path></svg></a></span>适量的电流刺激，引发<span style=\"user-select: text !important;\">\n<a data-za-not-track-link=\"true\" href=\"https://www.zhihu.com/search?q=%E5%A4%A7%E8%84%91%E7%9A%AE%E8%B4%A8&amp;search_source=Entity&amp;hybrid_search_source=Entity&amp;hybrid_search_extra=%7B%22sourceType%22%3A%22article%22%2C%22sourceId%22%3A%22108315441%22%7D\" target=\"_blank\" class=\"css-1occaib\" style=\"color: rgb(0, 102, 255); text-decoration-line: none; cursor: pointer; user-select: text !important;\">\n大脑皮质<svg width=\"8px\" height=\"8px\" viewBox=\"0 0 15 15\" class=\"css-ukqak1\">\n<path d=\"M10.89 9.477l3.06 3.059a1 1 0 0 1-1.414 1.414l-3.06-3.06a6 6 0 1 1 1.414-1.414zM6 10a4 4 0 1 0 0-8 4 4 0 0 0 0 8z\" fill=\"currentColor\">\n</path></svg></a></span>的电活动同步化，即诱发一次癫痫放电，进而体患者短暂意识丧失和全身抽搐发作，从而达到治疗抑郁症状的目的。目前临床使用的电抽搐治疗是改良的电抽蓄治疗MECT，即在电刺激前通过静脉麻醉注射适量的肌肉松弛剂，可使抽搐发作不明显。\n</p>\n<p class=\"ztext-empty-paragraph\" style=\"margin-top: -0.8em; margin-bottom: -0.8em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<br style=\"user-select: text !important;\">\n</p>\n<p data-pid=\"mvWMa1Q6\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<span style=\"user-select: text !important; font-weight: 600 !important;\">\n*一场来自灵魂的对话</span>\n</p>\n<figure data-size=\"normal\" style=\"margin: 1.4em 0px; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<img src=\"https://pic3.zhimg.com/v2-634104f267611c27d72b6bc6c1a709b2_b.jpg\" data-caption=\"\" data-size=\"normal\" data-rawwidth=\"736\" data-rawheight=\"429\" class=\"origin_image zh-lightbox-thumb\" width=\"736\" data-original=\"https://pic3.zhimg.com/v2-634104f267611c27d72b6bc6c1a709b2_r.jpg\" style=\"user-select: text !important; display: block; max-width: 100%; margin: 0px auto; cursor: zoom-in;\">\n</figure><p data-pid=\"MUfoKLKp\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n月有<span style=\"user-select: text !important;\">\n<a data-za-not-track-link=\"true\" href=\"https://www.zhihu.com/search?q=%E7%9B%88%E4%BA%8F&amp;search_source=Entity&amp;hybrid_search_source=Entity&amp;hybrid_search_extra=%7B%22sourceType%22%3A%22article%22%2C%22sourceId%22%3A%22108315441%22%7D\" target=\"_blank\" class=\"css-1occaib\" style=\"color: rgb(0, 102, 255); text-decoration-line: none; cursor: pointer; user-select: text !important;\">\n盈亏<svg width=\"8px\" height=\"8px\" viewBox=\"0 0 15 15\" class=\"css-ukqak1\">\n<path d=\"M10.89 9.477l3.06 3.059a1 1 0 0 1-1.414 1.414l-3.06-3.06a6 6 0 1 1 1.414-1.414zM6 10a4 4 0 1 0 0-8 4 4 0 0 0 0 8z\" fill=\"currentColor\">\n</path></svg></a></span>，潮水有起落，树木有发芽也有落叶，然后再等待下一个周期开花与结果。抑郁症也像生命四季的更换般，由春转入冬，由万物生长转入万物息零，但并非毁灭。\n</p>\n<p class=\"ztext-empty-paragraph\" style=\"margin-top: -0.8em; margin-bottom: -0.8em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<br style=\"user-select: text !important;\">\n</p>\n<p data-pid=\"71aqNvlN\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n它是心灵的一场对话，提醒着人们不要冲太快，该是时候停下好好休息了，就像种子一般冬天的休眠只为积蓄力量待到来年的春天生根发芽。\n</p>\n<p data-pid=\"nm4vp-71\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n圣经里有个寓言，约拿因拒绝上帝被大鱼吞进鱼腹中三天三夜。在漆黑的鱼腹中，<span style=\"user-select: text !important;\">\n<a data-za-not-track-link=\"true\" href=\"https://www.zhihu.com/search?q=%E7%BA%A6%E6%8B%BF&amp;search_source=Entity&amp;hybrid_search_source=Entity&amp;hybrid_search_extra=%7B%22sourceType%22%3A%22article%22%2C%22sourceId%22%3A%22108315441%22%7D\" target=\"_blank\" class=\"css-1occaib\" style=\"color: rgb(0, 102, 255); text-decoration-line: none; cursor: pointer; user-select: text !important;\">\n约拿<svg width=\"8px\" height=\"8px\" viewBox=\"0 0 15 15\" class=\"css-ukqak1\">\n<path d=\"M10.89 9.477l3.06 3.059a1 1 0 0 1-1.414 1.414l-3.06-3.06a6 6 0 1 1 1.414-1.414zM6 10a4 4 0 1 0 0-8 4 4 0 0 0 0 8z\" fill=\"currentColor\">\n</path></svg></a></span>在迷茫恐惧与绝望中不停地祈祷了三天三夜，他忏悔着自己做过的每一件事，或许是他的祈祷感动了上帝，最后大鱼终于把他吐回岸上，约拿也重新获得了光明与自由。\n</p>\n<p data-pid=\"2d6SIb4t\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n或许跌入抑郁的黑暗中的人，也如约拿在黑漆的鱼腹中在黑暗中那般独自对话。<br style=\"user-select: text !important;\">\n<br style=\"user-select: text !important;\">\n在这样黑暗中，约拿也越来越接近内心的声音，在大鱼与海水的包裹中，他也开始回归到最原始的自己，进入到一种深邃的生命对话。当光明再来时，他也越来越能聆听到生命的脉动了。<br style=\"user-select: text !important;\">\n</p>\n<p data-pid=\"TdWb3Osq\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<span style=\"user-select: text !important; font-weight: 600 !important;\">\n最后，愿那些跌入抑郁黑暗中的人，也能在等待的“三天三夜”的对话中，能重新被大鱼吐回岸上，再迎来属于生命的光明！</span>\n</p>\n<figure data-size=\"normal\" style=\"margin: 1.4em 0px; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<img src=\"https://pic1.zhimg.com/v2-d73da0c2ac6287667720e07c01434c10_b.jpg\" data-caption=\"\" data-size=\"normal\" data-rawwidth=\"536\" data-rawheight=\"396\" class=\"origin_image zh-lightbox-thumb\" width=\"536\" data-original=\"https://pic1.zhimg.com/v2-d73da0c2ac6287667720e07c01434c10_r.jpg\" style=\"user-select: text !important; display: block; max-width: 100%; margin: 0px auto; cursor: zoom-in;\">\n</figure>','心理咨询师杨曼妮',567,76,1,'2020-02-01','2021-11-20 10:39:43','2021-11-20 10:39:43'),(22,'抑郁症知识科普系列',6,'https://5b0988e595225.cdn.sohucs.com/q_70,c_zoom,w_640/images/20180611/13b6f5cd99344374a5bbd31d24dc920a.webp','摘要','<p data-pid=\"9VK-WBWO\" style=\"margin-top: 0px; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n根据有效数据统计得出下方数据：\n</p>\n<p data-pid=\"LFLO_EYc\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n（1）抑郁症有多普遍 —19%的美国人在某些时刻会遭受抑郁症带来的痛苦。 —50%的儿童和青少年,以及20%的成年人有抑郁症的一些症状。\n</p>\n<p data-pid=\"SGdZQLRv\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n（2）抑郁症会持续多久 ——70%的患者在1年内靠自己战胜了抑郁症。20%的患者在抑郁症病发2年后依然抑郁。11%的患者在5年后还是感到抑郁。 ——被诊断患有抑郁症的89%的患者在5年后不再抑郁。抑郁症复发的患者在他们的一生中平均发作7次。\n</p>\n<p data-pid=\"nkTxdMWJ\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n在今年3月，中国科学院心理研究所发布了《<span style=\"user-select: text !important;\">\n<a data-za-not-track-link=\"true\" href=\"https://www.zhihu.com/search?q=2019-2020%E5%9B%BD%E6%B0%91%E5%BF%83%E7%90%86%E5%81%A5%E5%BA%B7%E6%8A%A5%E5%91%8A&amp;search_source=Entity&amp;hybrid_search_source=Entity&amp;hybrid_search_extra=%7B%22sourceType%22%3A%22article%22%2C%22sourceId%22%3A%22389607035%22%7D\" target=\"_blank\" class=\"css-1occaib\" style=\"color: rgb(0, 102, 255); text-decoration-line: none; cursor: pointer; user-select: text !important;\">\n2019-2020国民心理健康报告<svg width=\"8px\" height=\"8px\" viewBox=\"0 0 15 15\" class=\"css-ukqak1\">\n<path d=\"M10.89 9.477l3.06 3.059a1 1 0 0 1-1.414 1.414l-3.06-3.06a6 6 0 1 1 1.414-1.414zM6 10a4 4 0 1 0 0-8 4 4 0 0 0 0 8z\" fill=\"currentColor\">\n</path></svg></a></span>》，里面有一组数据让人触目惊心: 小学阶段:抑郁检出率为10%-13%; 初中阶段:抑郁检出率为25%-30%; 高中阶段:抑郁检出率约38%。\n</p>\n<p data-pid=\"e8aNcsht\" style=\"margin-top: 1.4em; margin-bottom: 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n#<span style=\"user-select: text !important;\">\n<a data-za-not-track-link=\"true\" href=\"https://www.zhihu.com/search?q=%E9%9D%92%E6%98%A5%E6%9C%9F%E6%8A%91%E9%83%81%E7%97%87&amp;search_source=Entity&amp;hybrid_search_source=Entity&amp;hybrid_search_extra=%7B%22sourceType%22%3A%22article%22%2C%22sourceId%22%3A%22389607035%22%7D\" target=\"_blank\" class=\"css-1occaib\" style=\"color: rgb(0, 102, 255); text-decoration-line: none; cursor: pointer; user-select: text !important;\">\n青春期抑郁症<svg width=\"8px\" height=\"8px\" viewBox=\"0 0 15 15\" class=\"css-ukqak1\">\n<path d=\"M10.89 9.477l3.06 3.059a1 1 0 0 1-1.414 1.414l-3.06-3.06a6 6 0 1 1 1.414-1.414zM6 10a4 4 0 1 0 0-8 4 4 0 0 0 0 8z\" fill=\"currentColor\">\n</path></svg></a></span>\n</p>\n<figure data-size=\"normal\" style=\"margin: 0px 0px 1.4em; color: rgb(18, 18, 18); font-family: -apple-system, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, &quot;Source Han Sans SC&quot;, &quot;Noto Sans CJK SC&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 15px; user-select: text !important;\">\n<img src=\"https://pic3.zhimg.com/v2-cccae083d81375c47bebb4214c37c0de_b.jpg\" data-caption=\"\" data-size=\"normal\" data-rawwidth=\"3534\" data-rawheight=\"5315\" class=\"origin_image zh-lightbox-thumb\" width=\"3534\" data-original=\"https://pic3.zhimg.com/v2-cccae083d81375c47bebb4214c37c0de_r.jpg\" style=\"user-select: text !important; display: block; max-width: 100%; margin: 0px auto; cursor: zoom-in;\">\n</figure>','明泊心理咨询',457,90,1,'2021-07-14','2021-11-20 10:39:43','2021-11-20 10:39:43');

