#
# Structure for table "t_userinfo"
#

DROP TABLE IF EXISTS `t_userinfo`;
CREATE TABLE `t_userinfo` (
  `CI_ID` varchar(36) NOT NULL DEFAULT '' COMMENT '用户微信OpenID，在插入数据使用uuid()生成ID',
  `CI_NKNAME` varchar(20) DEFAULT '' COMMENT '用户昵称，微信昵称',
  `CI_PERSONTYPE` int(11) DEFAULT '1' COMMENT '用户类型(面向用户 1.普通用户 2.医生)',
  `CI_REALNAME` varchar(16) DEFAULT '' COMMENT '用户真实姓名',
  `CI_ID_CARD` varchar(20) DEFAULT NULL COMMENT '用户身份证号',
  `CI_PHONENUMBER` varchar(16) DEFAULT NULL COMMENT '用户手机号',
  `CI_SEX` tinyint(4) DEFAULT '0' COMMENT '用户性别 0-男性 1-女性',
  `CI_SIGNUPDATE` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '注册日期',
  `CI_STATUS` int(11) DEFAULT '0' COMMENT '用户状态（0.匿名 1.已登录 2.已认证）',
  `CI_AVATARTURL` varchar(400) DEFAULT '' COMMENT '用户头像（url）',
  `CI_LEVEL` int(11) DEFAULT NULL COMMENT '用户等级（面向系统 1.普通用户/2.普通管理员/3.超级管理员）',
  `CI_TIMESTAMP` int(11) DEFAULT NULL COMMENT '敏感数据获取的时间戳, 开发者可以用于数据时效性校验',
  PRIMARY KEY (`CI_ID`),
  KEY `CI_NKNAME` (`CI_NKNAME`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

