#
# Structure for table "t_doctor"
#

DROP TABLE IF EXISTS `t_doctor`;
CREATE TABLE `t_doctor` (
  `DOC_ID` varchar(36) NOT NULL COMMENT '医生id',
  `DOC_ABSTRACT` longtext COMMENT '医生个人简介',
  `DOC_HOSPITAL` varchar(50) NOT NULL COMMENT '医生所属医院',
  `DOC_POST` varchar(50) NOT NULL COMMENT '医生职称',
  `DOC_CERTIFICATION` varchar(100) DEFAULT '' COMMENT '医生认证',
  `DOC_PHOTO` varchar(500) DEFAULT '' COMMENT '医生照片',
  `DOC_DEPARTMENT` varchar(50) NOT NULL COMMENT '医生科室',
  PRIMARY KEY (`DOC_ID`) USING BTREE,
  CONSTRAINT `t_doctor_ibfk_1` FOREIGN KEY (`DOC_ID`) REFERENCES `t_userinfo` (`CI_ID`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;

#
# Data for table "t_doctor"
#

INSERT INTO `t_doctor` VALUES ('1','<div><b>医学博士</b>，现任<b>四川大学华西医院心理卫生中心副主任、主任医师、教授</b></div>\n<div><div>先后于1997年及2003年在昆明医学院(现昆明医科大学)获医学学士及硕士学位，2008年在四川大学华西临床医学院获医学博士学位。先后在浙江大学第二附属医院心理卫生科、昆明医学院第一附属医院精神科、香港中文大学精神医学系及香港健康情绪中心、四川大学华西医院心理卫生中心从事精神医学临床、研究和教学工作。其中，2008年7月至2010年6月为香港中文大学博士后研究员，后回四川大学华西医院工作至今。</div></div>\n<div><br></div>','四川大学华西医院','主任医师','未认证','https://img2.baidu.com/it/u=3559166618,3351049891&fm=26&fmt=auto','精神科'),('2','<div>毕业于<b>华西医科大学</b>获<b>医学博士学位</b>。先后于1999年9月- 2000年1月以及2001年6月- 2002年7月在英国伦敦大学精神病研究所进行博士、博士后研究，研究方向为儿童青少年精神医学与儿童心理学</div>\n<div>1999年获得中德心理治疗师培训班第一期家庭治疗培训结业证书</div>\n<div><br></div>','四川大学华西医院','主治医师','未认证','https://img2.baidu.com/it/u=3559166618,3351049891&fm=26&fmt=auto','精神科'),('3','<div>张骏，华西医院心理卫生中心<b>主任医师</b>、<b>硕士生导师</b>、PICU病房负责人、医疗组长，四川大学《行为医学》课程负责人。擅长精神疾病的药物+心理治疗，四川省心理卫生协会心理治疗分会会长。</div>\n<div><br></div>','四川大学华西医院','主治医师','未认证','https://img2.baidu.com/it/u=3559166618,3351049891&fm=26&fmt=auto','精神科');

