#
# Structure for table "chat_t_records"
#

DROP TABLE IF EXISTS `chat_t_records`;
CREATE TABLE `chat_t_records` (
  `M_ID` varchar(36) NOT NULL DEFAULT '' COMMENT '消息uuid,插入时使用uuid()生成Id',
  `M_TEXT` text COMMENT '消息内容，前端另写代码限制长度。若消息内容为文件，则为内容为文件Url',
  `M_STATUS` tinyint(4) DEFAULT '0' COMMENT '0-成功 1-失败',
  `M_SENDTIME` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '发送时间',
  `M_TYPE` int(11) DEFAULT '1' COMMENT '外键，消息类型 1.文本 2.图片 3.文件',
  `M_SENDERID` varchar(36) DEFAULT '' COMMENT '发送者ID，外键',
  `M_SESSIONKEY` varchar(36) DEFAULT '' COMMENT '外键，聊天记录所属会话',
  PRIMARY KEY (`M_ID`) USING BTREE,
  KEY `M_SESSIONKEY` (`M_SESSIONKEY`) USING BTREE,
  KEY `M_TYPE` (`M_TYPE`) USING BTREE,
  KEY `M_SENDERID` (`M_SENDERID`) USING BTREE,
  CONSTRAINT `chat_t_records_ibfk_1` FOREIGN KEY (`M_SENDERID`) REFERENCES `t_userinfo` (`CI_ID`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `chat_t_records_ibfk_2` FOREIGN KEY (`M_SESSIONKEY`) REFERENCES `chat_t_contact` (`CHAT_SESSIONKEY`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `chat_t_records_ibfk_3` FOREIGN KEY (`M_TYPE`) REFERENCES `message_t_type` (`MT_ID`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;

