  CONSTRAINT `bbs_t_comments_ibfk_2` FOREIGN KEY (`CO_USERID`) REFERENCES `t_userinfo` (`CI_ID`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

