#
# Structure for table "user_healthy_record"
#

DROP TABLE IF EXISTS `user_healthy_record`;
CREATE TABLE `user_healthy_record` (
  `HEALTHY_RECORD_ID` varchar(36) NOT NULL DEFAULT '',
  `CI_ID` varchar(36) NOT NULL DEFAULT '0' COMMENT '用户微信OpenID，外键',
  `RELATION` varchar(50) DEFAULT '' COMMENT '与您的关系',
  `USER_NAME` varchar(30) NOT NULL COMMENT '用户姓名',
  `SEX` int(5) NOT NULL COMMENT '性别，0-男，1-女',
  `USER_BIRTHDAY` datetime NOT NULL COMMENT '出生日期',
  `USER_HEIGHT` decimal(6,3) DEFAULT NULL COMMENT '身高',
